INSERT INTO tour_terms (tour_id, section_title, content) VALUES (3, 'Dịch vụ bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vận chuyển: Xe du lịch chất lượng, điều hòa phục vụ theo lịch trình.</li><li class="text-secondary-v2 text-base">Vé vào cửa 01 lần vào Khu du lịch Vườn Vua Resort</li><li class="text-secondary-v2 text-base">Vé tắm bể bơi khoáng trong nhà, vé tắm bể bơi ngoài trời, Các trò chơi bao gồm: nhà bóng, thú nhún, sân chơi ngoài trời cho trẻ em, xe điện đụng, tàu hỏa, trượt cỏ, xe đạp và xe điện đi thăm quan trong resort( tùy tình trạng xe trống tại thời điểm)</li><li class="text-secondary-v2 text-base">01 bữa ăn trưa tiêu chuẩn</li><li class="text-secondary-v2 text-base">Hướng dẫn viên thành thạo, kinh nghiệm, nhiệt tình theo suốt hành trình</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch trọn tour mức 20,000,000đ/ 1 trường hợp</li><li class="text-secondary-v2 text-base">Nước uống trên xe tiêu chuẩn 1 chai/ người/ ngày</li><li class="text-secondary-v2 text-base">Phí tổ chức Teambuilding: âm thanh, dụng cụ, sân cỏ, kịch bản, MC</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (3, 'Dịch vụ không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chi tiêu cá nhân, đồ uống trong bữa ăn</li><li class="text-secondary-v2 text-base">Thuế VAT- Phí ngủ phòng hoặc nhà sàn trong ngày</li><li class="text-secondary-v2 text-base">Các dịch vụ vui chơi giải trí mất phí trong khu du lịch: xông hơi, Massage, Spa, Karaoke, tập gym, sân bóng đá mini, các trò chơi điện tử, xem phim 6D, Câu cá, thả diều sáo, đi thuyền tham quan quanh hồ, hái sen…</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (3, 'Giá dành cho trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em từ 1-4 tuổi miễn phí tiền tour( Không chiếm ghế trên xe, Tự túc vé thắng cảnh, ăn cùng bố mẹ)</li><li class="text-secondary-v2 text-base">Từ 5-8 tuổi tính 75% giá tour</li><li class="text-secondary-v2 text-base">Từ 9 tuổi trở lên tính như người lớn</li><li class="text-secondary-v2 text-base">Số lượng trẻ em miễn phí chiếm không quá 10% số lượng người lớn trong đoàn. nếu trẻ em đi kèm nhiều hơn 10% thì phải mua ½ tour trọn gói.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (3, 'Lưu ý:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Giá tour không áp dụng vào dịp lễ tết, cao điểm</li><li class="text-secondary-v2 text-base">Chuẩn bị đầy đủ danh sách tham quan bao gồm: Họ tên, Ngày tháng năm sinh, Số CMND, Địa chỉ để làm thủ tục mua bảo hiểm.</li><li class="text-secondary-v2 text-base">Thời gian khởi hành, một số thứ tự và chi tiết chương trình tham quan có thể được sắp xếp lại cho phù hợp với tình hình khách quan (thời tiết, giao thông, sức khỏe du khách) nhưng vẫn đảm bảo đầy đủ chương trình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (4, 'Giá bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô du lịch đời mới đưa đón suốt hành trình.</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">Ăn trưa theo tiêu chuẩn 150.000 VNĐ/người</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 30.000.000đ/ người/vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li><li class="text-secondary-v2 text-base">Vé tham quan tắm suối khoáng nóng, sử dụng các dịch vụ:</li><li class="text-secondary-v2 text-base">+ Ohayo Sauna: xông khô truyền thống, xông khô đá muối Himalaya, Xông đá quý, Xông thảo dược, Tuyết lạnh Igloo, Oxy tươi Hinoki,...</li><li class="text-secondary-v2 text-base">+ Natural Onsen: Bể khoáng ngoài trời, bể nhiệt độ thấp / nhiệt độ cao, bể sục Jacuzzi, bể Kid Onsen</li><li class="text-secondary-v2 text-base">+ Matsuri Graden: Kidzone, Foot Onsen, Trà đạo, Khu Tatami, Vườn Nhật.</li><li class="text-secondary-v2 text-base">Tắm thảo dược - Japanese Onsen: bể khoáng nóng, bể lạnh, bể tắm tia thuỷ lực, bể bọt Nano, bể bọt Micro, bể Soda, xông ướt.</li><li class="text-secondary-v2 text-base">Onsen theo chủ đề: bể Onsen trà xanh, bề Onsen Hoa theo mùa, bể Onsen Mùi già.</li><li class="text-secondary-v2 text-base">Event Plaza: Trung tâm giải trí, ẩm thực, lễ hội</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (4, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chi phí cá nhân.</li><li class="text-secondary-v2 text-base">VAT</li><li class="text-secondary-v2 text-base">Tip cho lái xe và HDV</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (4, 'Quy định phụ thu', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em từ 11 tuổi trở lên: tính 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Trẻ em từ 05 – 11 tuổi: tính 75% giá trị tour – Ngủ chung với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em dưới 05 tuổi: miễn phí (Ăn ngủ cùng bố mẹ, ngồi lòng bố hoặc mẹ), 2 người lớn miễn phí 1 trẻ em, trẻ thứ 2 tính 50% giá trị tour.</li><li class="text-secondary-v2 text-base">Lễ tết phụ thu 150.000 vnđ/ người</li><li class="text-secondary-v2 text-base">Phòng riêng: Tùy thuộc vào hạng bungalow hiện có của homestay.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (4, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (5, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe du lịch đời mới vận chuyển theo chương trình, máy lạnh, sạch sẽ</li><li class="text-secondary-v2 text-base">Hướng dẫn viên tiếng Việt suốt tuyến, kinh nghiệm, nhiệt tình;</li><li class="text-secondary-v2 text-base">01 bữa chính suất 150.000VNĐ/khách.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức đền bù tối đa 30.000.000 đồng/người/vụ.</li><li class="text-secondary-v2 text-base">Nước uống 1 chai 500ml/ khách/ ngày</li><li class="text-secondary-v2 text-base">Vé thăm quan Chùa Hương + vé Đò</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (5, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé cáp treo khứ hồi: 220.000 VNĐ/khách</li><li class="text-secondary-v2 text-base">Xe điện từ cổng vào tới khu trung tâm bến đò chùa Hương</li><li class="text-secondary-v2 text-base">Chi phí cá nhân phát sinh ngoài chương trình</li><li class="text-secondary-v2 text-base">Đồ uống trong bữa ăn.</li><li class="text-secondary-v2 text-base">Tiền tip cho lái đò chùa Hương, tối thiểu từ 30.000 VNĐ/khách (do tính chất của tour mong Quý khách thông cảm và hoan hỷ)</li><li class="text-secondary-v2 text-base">Tip cho HDV và tài xế (không bắt buộc)</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (5, 'Phụ thu trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 5 tuổi miễn phí (ăn và ngủ chung với bố mẹ). Bố mẹ tự thanh toán chi phí phát sinh của bé.  Hai người lớn kèm 1 trẻ em, trẻ em thứ 2 tính 50% giá tour.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 – 10 tuổi tính 75% giá tour (ăn suất riêng và ngủ chung với bố mẹ);</li><li class="text-secondary-v2 text-base">Trẻ em từ 11 tuổi trở lên tính như người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (6, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe du lịch đời mới vận chuyển theo chương trình, máy lạnh, sạch sẽ</li><li class="text-secondary-v2 text-base">Hướng dẫn viên tiếng Việt suốt tuyến, kinh nghiệm, nhiệt tình;</li><li class="text-secondary-v2 text-base">01 bữa chính suất 150.000VNĐ/khách.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức đền bù tối đa 30.000.000 đồng/người/vụ</li><li class="text-secondary-v2 text-base">Nước uống 1 chai 500ml/ khách/ ngày</li><li class="text-secondary-v2 text-base">Vé thăm quan các điểm trong chương trình</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (6, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chi phí cá nhân phát sinh ngoài chương trình</li><li class="text-secondary-v2 text-base">Đồ uống trong bữa ăn</li><li class="text-secondary-v2 text-base">Tip cho lái xe và HDV</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (6, 'Phụ thu trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 5 tuổi miễn phí (ăn và ngủ chung với bố mẹ). Bố mẹ tự thanh toán chi phí phát sinh của bé. Hai người lớn kèm 1 trẻ em, trẻ em thứ 2 tính 50% giá tour.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 – 10 tuổi tính 75% giá tour (ăn suất riêng và ngủ chung với bố mẹ);</li><li class="text-secondary-v2 text-base">Trẻ em từ 11 tuổi trở lên tính như người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Giá tour\xa0bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Lưu trú: 07 đêm trên tàu hạng sang</li><li class="text-secondary-v2 text-base">Các lựa chọn ăn uống bao gồm bữa sáng, bữa trưa và bữa tối. Các bữa ăn được chế biến tươi ngon trên tàu bằng các nguyên liệu địa phương tốt nhất có sẵn và tận hưởng trải nghiệm ẩm thực đích thực tại các nhà hàng địa phương trong suốt chuyến tham quan. In</li><li class="text-secondary-v2 text-base">Đồ uống: Nước ngọt miễn phí hàng ngày, bia, rượu vang nhà theo ly… theo danh sách đồ uống; trà và cà phê có sẵn trên tàu. Một đồ uống miễn phí (nước ngọt, bia, rượu vang nhà theo ly) được cung cấp cùng với bữa ăn khi xuống tàu.</li><li class="text-secondary-v2 text-base">Dịch vụ: Dịch vụ tiếp viên trên tàu bao gồm dịch vụ dọn phòng buổi tối; đội ngũ Quản lý tour giàu kinh nghiệm; dịch vụ đưa đón sân bay/khách sạn riêng được bao gồm khi bắt đầu và kết thúc hành trình của bạn tại Hà Nội hoặc Thành phố Hồ Chí Minh; 45 phút trị liệu đầu và vai cho mỗi người cho mỗi chuyến đi.</li><li class="text-secondary-v2 text-base">Tiện nghi trong cabin: Tất cả đều có giường đôi hoặc giường đơn, phòng tắm riêng với vòi sen, nhà vệ sinh và bồn rửa; két an toàn, máy lạnh, khăn trải giường và các tiện nghi khác và được phục vụ hàng ngày.</li><li class="text-secondary-v2 text-base">Các chuyến tham quan: Các chuyến tham quan có hướng dẫn viên nói tiếng Anh địa phương, phí vào cửa theo hành trình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Giá tour\xa0không bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chương trình không bao gồm dịch vụ đưa đón riêng hoặc bất kỳ dịch vụ đưa đón bổ sung nào.</li><li class="text-secondary-v2 text-base">Tất cả đồ uống được sử dụng ngoài giờ ăn khi không ở trên tàu.</li><li class="text-secondary-v2 text-base">Chi phí cá nhân</li><li class="text-secondary-v2 text-base">Chi phí thị thực</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch.</li><li class="text-secondary-v2 text-base">Các chương trình trước và sau chuyến tham quan (nếu cần)</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Chính sách giá cho trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chương trình tour chỉ nhận khách từ 05 tuổi trở lên.</li><li class="text-secondary-v2 text-base">Trẻ em từ 05 đến 11.99 tuổi tính 75% giá vé người lớn.</li><li class="text-secondary-v2 text-base">Trẻ em từ 12 tuổi sẽ được tính như người lớn thứ ba.</li><li class="text-secondary-v2 text-base">Lưu ý: Trẻ em sẽ sử dụng chung giường có sẵn trong cùng cabin với người lớn (Không kê được extra bed).</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Điều khoản và điều kiện chung', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">NhữngĐiều Kiện Đặt Chỗ này không chỉ tạo thành cơ sở của hợp đồng giữa bạn và SJourney Luxury Trains mà còn bao gồm chính sách bảo mật của chúng tôi. Nếu bạn đặt kỳ nghỉ qua trang web của chúng tôi, Điều Khoản Sử Dụng sẽ được áp dụng. Ngoài ra, bất kỳ thông tin bằng văn bản nào khác mà chúng tôi cung cấp trước khi xác nhận đặt chỗ cũng sẽ được xem xét nhằm đảm bảo bạn hiểu rõ và yên tâm khi bắt đầu hành trình của mình.</li><li class="text-secondary-v2 text-base">Để đặt chỗ, vui lòng đặt giữ chỗ qua Website hoặc gọi số 19003440. Khi nhận được yêu cầu và tùy thuộc vào tình trạng còn chỗ, chúng tôi sẽ TẠM THỜI đặt chỗ cho bạn vào ngày đã chọn. Chỗ ngồi sẽ được giữ, NHƯNG nếu có khách khác yêu cầu xác nhận cabin, người giữ chỗ/khách sẽ được yêu cầu xác nhận bằng khoản tiền đặt cọc hoặc hủy giữ chỗ cabin đó. Sau khi nhận được xác nhận bằng văn bản qua email và hoàn thành Mẫu Đặt Chỗ, thông tin xác nhận và hóa đơn tạm tính sẽ được gửi đến bạn.</li><li class="text-secondary-v2 text-base">Khi nhận được khoản thanh toán liên quan, đặt chỗ sẽ được XÁC NHẬN, và hóa đơn xác nhận sẽ được gửi lại. Khi đặt chỗ được xác nhận, các Điều Khoản &amp;amp; Điều Kiện của chúng tôi sẽ được coi là đã được chấp nhận và sẽ được tuân thủ nghiêm ngặt.</li><li class="text-secondary-v2 text-base">Thanh toán có thể được thực hiện qua chuyển khoản ngân hàng hoặc thẻ tín dụng. Chi tiết sẽ được cung cấp tại thời điểm đặt chỗ.</li><li class="text-secondary-v2 text-base">Do chi phí xử lý thẻ tín dụng ở Việt Nam khá cao, chúng tôi ưu tiên khách hàng thanh toán qua chuyển khoản ngân hàng. Vui lòng ghi rõ số tham chiếu đặt chỗ của bạn trong phần nội dung chuyển khoản và gửi email xác nhận thanh toán.</li><li class="text-secondary-v2 text-base">Chúng tôi là một công ty tư nhân tại Việt Nam và có quyền tính phí hoặc hoàn tiền bằng đồng Việt Nam (VND). Theo quy định pháp luật, các khoản hoàn tiền sẽ được xử lý vào đúng tài khoản mà từ đó thanh toán ban đầu được thực hiện.</li><li class="text-secondary-v2 text-base">Tất cả các khoản thanh toán hoặc hoàn tiền qua thẻ tín dụng cho mọi hành trình đều được thực hiện bằng VND. SJourney không chịu trách nhiệm đối với bất kỳ mất mát nào về giá trị tiền tệ do biến động tỷ giá hối đoái.</li><li class="text-secondary-v2 text-base">Sắp xếp chuyến tham quan: Chương trình du lịch của chúng tôi được lên kế hoạch từ nhiều tháng trước và đôi khi cần thay đổi (bao gồm nhưng không giới hạn ở việc hủy bỏ hoặc thay thế diễn giả khách mời). Chúng tôi có quyền thực hiện các thay đổi này bất cứ lúc nào. Phần lớn các thay đổi này sẽ không đáng kể (ví dụ: thay đổi dịch vụ toa tàu gắn vào tàu dịch vụ thông thường) và chúng tôi sẽ thông báo cho bạn hoặc đại lý du lịch của bạn sớm nhất có thể.</li><li class="text-secondary-v2 text-base">Thay đổi: Nếu có thay đổi nhỏ trong chuyến đi, chúng tôi sẽ cố gắng thông báo cho bạn hoặc đại lý du lịch của bạn sớm nhất có thể nếu có thời gian trước khi khởi hành. Tuy nhiên, chúng tôi không chịu trách nhiệm pháp lý trong trường hợp này. Các ví dụ về thay đổi nhỏ bao gồm: Thay đổi chuyến bay đi/về dưới 12 giờ. Thay đổi loại máy bay. Thay đổi chỗ ở sang chỗ khác có tiêu chuẩn tương tự hoặc cao hơn. Thay đổi hãng vận chuyển. Lưu ý rằng các hãng vận chuyển (như hãng hàng không) trong tài liệu quảng cáo có thể thay đổi.</li><li class="text-secondary-v2 text-base">Do thay đổi lịch bay hoặc lý do vận hành khác sau khi tour đã được đặt, có thể cần thay đổi hãng hàng không, sân bay hoặc máy bay đã thông báo. Các thay đổi như vậy sẽ được thông báo cho bạn hoặc đại lý sớm nhất có thể và trong mọi trường hợp sẽ được thông báo tại quầy check-in hoặc cổng lên máy bay. Những thay đổi này được coi là không đáng kể và các điều khoản nêu trên sẽ được áp dụng.</li><li class="text-secondary-v2 text-base">Nếu chúng tôi cần thực hiện thay đổi quan trọng, chúng tôi sẽ thông báo cho bạn sớm nhất có thể trước ngày khởi hành (nếu có đủ thời gian). Bạn sẽ có các lựa chọn sau:</li><li class="text-secondary-v2 text-base">Chấp nhận các sắp xếp đã thay đổi.</li><li class="text-secondary-v2 text-base">Nhận lại toàn bộ số tiền đã thanh toán.</li><li class="text-secondary-v2 text-base">Nếu có và chúng tôi cung cấp, chấp nhận một kỳ nghỉ thay thế (chúng tôi sẽ hoàn lại khoản chênh lệch nếu kỳ nghỉ thay thế có giá trị thấp hơn).</li><li class="text-secondary-v2 text-base">Đối với các tour du lịch đường sắt bao gồm vận chuyển bằng đầu máy xe lửa cụ thể, đi trên toa tàu cụ thể hoặc trên các tuyến cụ thể, trong khi mọi nỗ lực sẽ được thực hiện để đảm bảo rằng các tính năng này được cung cấp như đã quảng cáo, có thể có những trường hợp, vì những lý do nằm ngoài tầm kiểm soát của chúng tôi, các tính năng cụ thể không thể được cung cấp. Trong trường hợp này, chúng tôi sẽ cố gắng cung cấp lực kéo, chỗ ở hoặc tuyến đường thay thế tùy theo hoàn cảnh cho phép. Nếu chúng tôi không thể đưa ra các sắp xếp thay thế phù hợp hoặc nếu bạn từ chối chấp nhận những điều này vì lý do chính đáng, chúng tôi sẽ sắp xếp đưa bạn trở lại điểm khởi hành tour của bạn hoặc đến điểm dịch vụ theo hợp đồng của chúng tôi bắt đầu sớm nhất có thể.</li><li class="text-secondary-v2 text-base">Tuy nhiên, chúng tôi và các đại lý của chúng tôi luôn có quyền thay đổi các khía cạnh của chuyến tham quan vì lý do cơ học, vận hành hoặc các vấn đề khác nằm ngoài tầm kiểm soát hợp lý của chúng tôi (chẳng hạn như bất kỳ hoạt động chiến tranh hoặc khủng bố nào (có nguy cơ hoặc thực tế), bạo loạn, bất ổn dân sự, đóng cửa sân bay, hành động công nghiệp (có nguy cơ hoặc thực tế), điều kiện chính trị, thảm họa thiên nhiên hoặc hạt nhân, hỏa hoạn, điều kiện thời tiết bất lợi, thay đổi hoặc hủy dịch vụ hàng không, đường sông, đường biển hoặc đường sắt hoặc bất kỳ sự kiện nào khác nằm ngoài tầm kiểm soát hợp lý của chúng tôi).</li><li class="text-secondary-v2 text-base">Chúng tôi có quyền trong một số trường hợp (theo quyết định riêng của chúng tôi), do các quy trình quản lý tải trọng hiệu quả trên tàu, cung cấp dịch vụ nâng hạng khoang cho những hành khách được chọn mà không tính thêm phí. Việc nâng hạng khoang như vậy không được đảm bảo, không thể yêu cầu và chỉ có thể được thông báo vào ngày đầu tiên của chuyến tham quan.</li><li class="text-secondary-v2 text-base">Chúng tôi và các đại lý của chúng tôi luôn có quyền hủy bỏ và hoặc sửa đổi bất kỳ chương trình nào của diễn giả khách mời mà không phải chịu bất kỳ trách nhiệm nào với bạn mà không cần thông báo cho bạn và đây luôn là một thay đổi nhỏ. Để tránh nghi ngờ, bạn sẽ không được phép hủy chuyến tham quan của mình và/hoặc bất kỳ khoản hoàn lại nào cho số tiền bạn đã thanh toán, do việc hủy và/hoặc sửa đổi chương trình của diễn giả khách mời. Nếu bạn hủy chuyến tham quan của mình vì những lý do như vậy, phí hủy sẽ được áp dụng.</li><li class="text-secondary-v2 text-base">Nếu chúng tôi không thể cung cấp dịch vụ tương đương các sắp xếp mà bạn đã đặt với chúng tôi sau khi bạn đã khởi hành, nếu có thể, chúng tôi sẽ sắp xếp thay thế cho bạn mà không tính thêm phí và trong trường hợp các sắp xếp thay thế đó có tiêu chuẩn thấp hơn, chúng tôi sẽ cung cấp cho bạn mức giảm giá phù hợp</li><li class="text-secondary-v2 text-base">SJourney hoàn toàn phụ thuộc vào hệ thống vận hành và dịch vụ đường sắt do ngành đường sắt quốc gia Việt Nam cung cấp. Thật không may, khi các dịch vụ này gặp sự cố như hỏng đầu máy, tín hiệu, thiệt hại hoặc mất cắp cáp điện, hành vi phá hoại, mất điện, đường ray gặp vấn đề hoặc bất kỳ sự cố nào khác, chúng tôi bị hạn chế khả năng xử lý. Các sự cố này có thể dẫn đến sự chậm trễ do sự phụ thuộc của chúng tôi vào các tổ chức quốc gia.</li><li class="text-secondary-v2 text-base">SJourney không chịu trách nhiệm và không thể bị quy trách nhiệm cho bất kỳ sự chậm trễ hoặc thay đổi nào trong lịch trình tour do các yếu tố tự nhiên hoặc các vấn đề khác nằm ngoài tầm kiểm soát của chúng tôi.</li><li class="text-secondary-v2 text-base">SJourney có quyền hủy bỏ hoặc điều chỉnh tuyến đường, các chuyến tham quan và ngày khởi hành của tour, miễn là chúng tôi có thể cung cấp cho hành khách các chuyến tham quan hoặc ngày khởi hành thay thế.</li><li class="text-secondary-v2 text-base">SJourney không thể đảm bảo chính xác thời gian của các chuyến tham quan, giờ khởi hành hoặc giờ đến. Nghĩa vụ chính của chúng tôi là vận chuyển hành khách từ điểm xuất phát đến điểm đến theo lịch trình, điều này sẽ được ưu tiên hơn bất kỳ cam kết nào liên quan đến các chuyến tham quan. Tuy nhiên, chúng tôi sẽ cố gắng hết sức để thực hiện các trải nghiệm ngoài tàu theo kế hoạch.</li><li class="text-secondary-v2 text-base">Chúng tôi khuyến cáo mạnh mẽ không nên sắp xếp các chuyến bay cùng ngày với ngày khởi hành hoặc ngày đến của tàu do có thể xảy ra các sự cố chậm trễ liên quan đến chuyến bay hoặc tàu.</li><li class="text-secondary-v2 text-base">Góikỳ nghỉ bằng tàu hỏa không bao gồm vé máy bay trừ khi có quy định khác. Tất cả giá vé máy bay tùy thuộc vào tình trạng còn chỗ và áp dụng các điều kiện của hãng hàng không. Chuyên gia tư vấn của chúng tôi có thể hỗ trợ bạn đặt chuyến bay phù hợp hoặc dịch vụ khác bất cứ khi nào có thể mà không phải là một phần của hợp đồng hành trình hoặc tour du lịch (gói) với chúng tôi. Bạn có thể đặt chuyến bay thông qua bất kỳ đại lý du lịch nào. Chúng tôi không chịu trách nhiệm và nghĩa vụ đối với bất kỳ chuyến bay nào, việc thay đổi chuyến bay hoặc hậu quả phát sinh do nhà cung cấp sửa đổi hoặc hủy chuyến bay. Bạn có thể lấy thông tin chi tiết và điều kiện đầy đủ từ chuyên gia tư vấn đặt chỗ của chúng tôi hoặc đại lý du lịch của bạn liên quan đến việc đặt chuyến bay của bạn. Bạn phải đảm bảo cung cấp thông tin chi tiết về chuyến bay cho chúng tôi đúng hạn (xem phần Thông tin hành khách chi tiết bên dưới). Nếu bạn lỡ chuyến bay hoặc bất kỳ chuyến chuyển nào đã đặt trước, bạn sẽ chịu trách nhiệm đến điểm khởi hành của hành trình/tour du lịch (gói), với chi phí do khách tự chi trả. Trừ khi được nêu rõ trong hành trình, việc chuyển sân bay có thể là chuyển theo nhóm được lên lịch trùng với thời gian đến và khởi hành của nhiều chuyến bay. Có thể sắp xếp dịch vụ đưa đón riêng với chi phí riêng với nhà cung cấp dịch vụ và không phải là một phần của hành trình hoặc hợp đồng tour (gói). Không phải địa điểm nào cũng có dịch vụ đưa đón riêng. Vui lòng tìm hiểu thêm thông tin chi tiết.</li><li class="text-secondary-v2 text-base">Trongtrường hợp không mong muốn xảy ra sự cố trong chuyến đi, vui lòng thông báo ngay cho ban quản lý tàu hỏa, họ sẽ cố gắng giải quyết. Nếu bạn bị mất hoặc hư hỏng hành lý, vui lòng thông báo ngay cho chúng tôi. Nếu khiếu nại của bạn không được giải quyết tại địa phương và bạn muốn khiếu nại thêm, vui lòng gửi thông báo chính thức bằng văn bản về khiếu nại của bạn đến văn phòng chính của SJourney trong vòng 28 ngày kể từ ngày kết thúc chuyến đi (cung cấp mã đặt chỗ và mọi thông tin liên quan khác). Nếu không làm như vậy, chúng tôi sẽ không điều tra khiếu nại của bạn và có thể ảnh hưởng đến quyền của bạn theo các điều kiện này.</li><li class="text-secondary-v2 text-base">Trừ khi có quy định rõ ràng khác trong các điều kiện này, chúng tôi sẽ không chịu trách nhiệm hoặc bồi thường nếu các nghĩa vụ theo hợp đồng của chúng tôi bị ảnh hưởng bởi bất kỳ sự kiện nào mà chúng tôi hoặc nhà cung cấp dịch vụ có liên quan không thể lường trước hoặc tránh được, ngay cả khi đã hết sức cẩn trọng. Các sự kiện này có thể bao gồm nhưng không giới hạn ở chiến tranh, khủng bố, đe dọa chiến tranh, xung đột dân sự, hỏa hoạn, lũ lụt hoặc bất kỳ điều kiện thời tiết khắc nghiệt nào khác và hậu quả của nó hoặc mối đe dọa của hoạt động đó, bạo loạn, khó khăn về lao động, hành động của bất kỳ chính phủ hoặc cơ quan quốc gia hoặc địa phương nào khác bao gồm tranh chấp lao động, đóng cửa khóa, thảm họa thiên nhiên hoặc hạt nhân, thảm họa hóa học hoặc sinh học, thời tiết bất lợi, điều kiện biển, không khí, băng và sông, mất điện, dịch bệnh hoặc đại dịch, không có khả năng xin được bất kỳ giấy phép hoặc sự đồng ý cần thiết nào và sự chậm trễ do các nhà thầu phụ gây ra và tất cả các sự kiện tương tự nằm ngoài tầm kiểm soát của chúng tôi hoặc nhà cung cấp liên quan.</li><li class="text-secondary-v2 text-base">Trong trường hợp Bất khả kháng khiến việc thực hiện hợp đồng đặt chỗ trở nên không thể hoặc ngăn cản việc thực hiện hợp đồng đặt chỗ tại thời điểm đi lại hoặc trong thời gian hợp lý trước ngày đi lại, hợp đồng đặt chỗ có thể bị chấm dứt bởi một trong hai bên. Một sự kiện như vậy cũng yêu cầu phải có thông báo chính thức của các cơ quan quốc gia hoặc quốc tế (ví dụ: ban hành cảnh báo đi lại, tuyên bố rủi ro hoặc [một phần] tình trạng khẩn cấp) tại thời điểm đi lại, trong khoảng thời gian hợp lý trước ngày đi lại hoặc không còn tồn tại. Trong trường hợp như vậy, SJourney sẽ cấp khoản tín dụng trong tương lai bằng với số tiền bạn đã thanh toán cho chúng tôi (trừ bất kỳ chi phí nào không được hoàn lại của bên thứ ba nếu có). Xin lưu ý rằng bạn sẽ không được hoàn lại tiền mặt. Các đại lý du lịch hoặc người bán lại đặt chỗ thay mặt cho khách và chưa thanh toán cho chúng tôi phí hủy nếu có, khi chấm dứt hợp đồng đặt chỗ do Bất khả kháng, sẽ không được hoàn lại tiền dưới dạng tín dụng trong tương lai trừ khi các khoản phí mở đã được cân đối để đổi lại.</li><li class="text-secondary-v2 text-base">SJourneycung cấp dịch vụ bảo quản an toàn cho các đồ vật có giá trị trên tàu và khuyến khích hành khách gửi bất kỳ đồ trang sức hoặc đồ vật có giá trị nào khác mang lên tàu vào hộp an toàn trong phòng được cung cấp. SJourney sẽ không chịu trách nhiệm đối với bất kỳ mất mát hoặc hư hỏng nào đối với tiền, đồ trang sức, đá quý, chứng khoán, công cụ tài chính, vé hoặc các đồ vật có giá trị khác trừ khi được cất giữ trong hộp an toàn trong phòng. Giá trị của các mặt hàng không được vượt quá 500 USD trừ khi SJourney đồng ý bằng văn bản về giá trị cao hơn cho các mặt hàng đó. Các mặt hàng vượt quá giới hạn 500 đô la Mỹ phải được giao cho ban quản lý tàu để giữ an toàn và phải xuất biên lai. Việc giải quyết các yêu cầu bồi thường đối với đồ đạc bị mất sẽ được thực hiện dựa trên giá trị tiền mặt thực tế (chi phí thay thế trừ khấu hao). Việc giải quyết các yêu cầu bồi thường đối với đồ đạc bị hư hỏng sẽ dựa trên chi phí sửa chữa hoặc giá trị tiền mặt thực tế, tùy theo giá trị nào nhỏ hơn. Không được thanh toán bất kỳ khoản tiền nào khi giải quyết bất kỳ yêu cầu bồi thường nào nếu không có bằng chứng về giá trị tiền mặt thực tế hoặc chi phí sửa chữa phát sinh do mất mát hoặc hư hỏng. Bằng chứng đó phải được gửi cho SJourney bằng văn bản và trách nhiệm của SJourney phải được chứng minh trước khi bất kỳ khoản giải quyết nào được thanh toán. SJourney sẽ không chịu trách nhiệm đối với việc mất mát hoặc hư hỏng hành lý hoặc đồ dùng cá nhân. Đồ đạc cá nhân bị mất khi không có người trông coi tại phòng chờ công cộng hoặc các khu vực công cộng khác, cho dù trên tàu hay ở nơi khác, đều không được hoàn trả. Các khoản mất mát do hao mòn thông thường, nguy hiểm trong chuyến đi và các hành động của Chúa khác không được hoàn trả.</li><li class="text-secondary-v2 text-base">Bạnphải có các giấy tờ du lịch hợp lệ bao gồm xác nhận đặt chỗ hợp lệ trước khi bắt đầu hành trình hoặc tour du lịch (gói) hoặc lên tàu, do SJourney hoặc bất kỳ bên thứ ba nào mà bạn đã đặt hành trình và SJourney đã cấp các giấy tờ du lịch hợp lệ cho. Các giấy tờ du lịch chỉ có giá trị cho các ngày, tuyến đường và chỉ được sử dụng với các chi tiết được hiển thị trên các giấy tờ du lịch. Các giấy tờ du lịch không được chuyển nhượng (trừ khi được phép rõ ràng theo bất kỳ thỏa thuận nào thông qua bên thứ ba). Các giấy tờ du lịch có sự thay đổi trái phép không có giá trị để đi du lịch. Các giấy tờ du lịch vẫn là tài sản của hãng vận chuyển và nếu bạn không tuân thủ bất kỳ điều kiện quan trọng nào (bao gồm nhưng không giới hạn ở các điều kiện này) chi phối việc sử dụng, giấy tờ du lịch của bạn có thể bị thu hồi hoặc vô hiệu và bạn phải trả toàn bộ giá vé. Có thể có một khoản phí hành chính để thay thế bất kỳ giấy tờ du lịch nào bị mất hoặc bị hư hỏng. Trước khi bắt đầu hành trình, hãy đảm bảo rằng bạn đã nhận được tất cả các giấy tờ du lịch mới nhất và thực tế từ chúng tôi hoặc bất kỳ bên thứ ba nào, bao gồm xác nhận đặt chỗ và tất cả các giấy tờ du lịch mới nhất có liên quan như hành trình, thông tin lên tàu, thông tin bao gồm, thông tin không bao gồm, v.v. có sẵn trên trang web của SJourney và áp dụng tại thời điểm lên tàu.</li><li class="text-secondary-v2 text-base">Nếubạn dừng lại trong chuyến đi ngoài thời gian được phép, bạn sẽ không được phép tiếp tục chuyến đi để được hoàn lại tiền hoặc bất kỳ khoản bồi thường nào khác từ chúng tôi.</li><li class="text-secondary-v2 text-base">Nếubạn hoặc bất kỳ thành viên nào trong đoàn của bạn có bất kỳ tình trạng y tế hoặc khuyết tật nào có thể ảnh hưởng đến chuyến đi của bạn, vui lòng cung cấp cho chúng tôi thông tin chi tiết đầy đủ trước khi xác nhận đặt chỗ để SJourney có thể tư vấn về tính phù hợp của bất kỳ sự sắp xếp nào đã chọn. Theo cách hành động hợp lý, nếu chúng tôi không thể đáp ứng nhu cầu của những người liên quan, chúng tôi sẽ không xác nhận đặt chỗ của bạn hoặc nếu bạn không cung cấp cho chúng tôi thông tin chi tiết đầy đủ tại thời điểm đặt chỗ, chúng tôi có quyền hủy đặt chỗ và áp dụng các khoản phí hủy áp dụng khi chúng tôi biết được những thông tin chi tiết này. Nếu khả năng đi lại của bạn có thể bị nghi ngờ do bệnh tật gần đây, phẫu thuật, chấn thương, điều trị y tế hoặc tình trạng bệnh lý hiện tại có thể cần điều trị hoặc hỗ trợ trên máy bay (bao gồm nhưng không giới hạn ở oxy bổ sung, xe lăn, hỗ trợ sử dụng phòng tắm, hỗ trợ lên hoặc xuống giường, thuốc được tiêm qua kim tiêm ngoài bệnh tiểu đường được kiểm soát tốt), bạn phải cung cấp cho chúng tôi giấy chứng nhận cập nhật từ bác sĩ của bạn chậm nhất là 5 ngày trước khi khởi hành xác nhận rằng bạn đủ khả năng đi lại và có một hành khách khác đi cùng, người có thể cung cấp cho bạn mọi hỗ trợ phù hợp mà bạn có thể cần. Chúng tôi có thể từ chối vận chuyển (và sẽ không hoàn lại tiền hoặc bồi thường) nếu bạn không cung cấp giấy chứng nhận áp dụng và/hoặc không có người hộ tống phù hợp đi cùng.</li><li class="text-secondary-v2 text-base">SJourneykhông thuê bác sĩ hoặc y tá trên tàu. Nếu hành khách cần chăm sóc y tế trong suốt chuyến đi, các dịch vụ y tế sẽ được cung cấp tại các cơ sở địa phương. Hành khách sẽ chịu trách nhiệm về mọi chi phí và phí tổn của các dịch vụ y tế, điều trị và thuốc men. SJourney sẽ không chịu bất kỳ trách nhiệm nào đối với bất kỳ chi phí nào phát sinh liên quan đến các dịch vụ y tế hoặc đối với chất lượng chăm sóc mà Hành khách nhận được. Hành khách đồng ý điều trị bởi các chuyên gia y tế do SJourney chỉ định, nếu hành khách không thể yêu cầu hoặc ủy quyền điều trị y tế và theo ý kiến \u200b\u200bcủa Quản lý tàu, việc chăm sóc y tế là cần thiết, hoàn toàn do hành khách chi trả và không chịu trách nhiệm đối với SJourney.</li><li class="text-secondary-v2 text-base">SJourneykhông phân biệt đối xử với những người dựa trên tình trạng khuyết tật. SJourney không thể tiếp nhận phụ nữ mang thai đã qua tháng thứ sáu. Hành khách bị khiếm khuyết về khả năng vận động phải có người đi cùng có thể hỗ trợ hành khách trên đất liền và trên tàu, và có trách nhiệm cung cấp cho hành khách mọi sự hỗ trợ và tiện nghi cần thiết. Nếu bất kỳ tình trạng nào như vậy phát sinh sau khi hành trình đã được đặt, hành khách phải thông báo cho SJourney bằng văn bản ngay lập tức. SJourney và tất cả nhân viên trên tàu sẽ không chịu trách nhiệm với hành khách liên quan đến tình trạng đó hoặc cách điều trị tình trạng đó.</li><li class="text-secondary-v2 text-base">Hànhkhách phải được bảo hiểm đầy đủ để chi trả mọi rủi ro về chi phí y tế và hồi hương (bao gồm cả hài cốt) và SJourney không chịu trách nhiệm theo bất kỳ cách nào đối với các nghĩa vụ đó. Chúng tôi đặc biệt khuyến nghị hành khách mua bảo hiểm du lịch để chi trả mọi rủi ro về phí hủy chuyến không lường trước được.</li><li class="text-secondary-v2 text-base">Bạn có trách nhiệm kiểm tra và đáp ứng các yêu cầu về hộ chiếu, thị thực, sức khỏe (bao gồm tiêm chủng, yêu cầu về giấy chứng nhận tiêm chủng và thuốc chống sốt rét) và nhập cư áp dụng cho hành trình du lịch của mình. Bạn phải kiểm tra các yêu cầu cho hoàn cảnh cụ thể của mình với các đại sứ quán và/hoặc lãnh sự quán có liên quan và bác sĩ của bạn nếu có. Các yêu cầu có thể thay đổi và bạn phải kiểm tra vị trí cập nhật kịp thời trước khi khởi hành. Chúng tôi không chịu bất kỳ trách nhiệm nào nếu bạn không thể đi du lịch hoặc chịu bất kỳ tổn thất nào khác do bạn không tuân thủ bất kỳ yêu cầu nào về hộ chiếu, thị thực, nhập cư hoặc thủ tục y tế. Bạn đồng ý hoàn trả cho chúng tôi theo yêu cầu đối với bất kỳ khoản tiền phạt hoặc tổn thất nào khác mà chúng tôi phải chịu do bạn không tuân thủ bất kỳ yêu cầu nào về hộ chiếu, thị thực, nhập cư hoặc thủ tục y tế.</li><li class="text-secondary-v2 text-base">Thông tin hành khách và bảo vệ dữ liệu</li><li class="text-secondary-v2 text-base">Để xử lý đặt chỗ của bạn, bạn sẽ cần cung cấp cho chúng tôi thông tin cá nhân. Chúng tôi sẽ cần cung cấp thông tin cá nhân của bạn cho các nhà cung cấp dịch vụ cũng như cơ quan hải quan và nhập cư. Chúng tôi cũng có thể cung cấp thông tin cá nhân cho các tổ chức an ninh và kiểm tra tín dụng. Một số người mà chúng tôi cung cấp thông tin cá nhân của bạn đang ở các quốc gia có thể không cung cấp mức độ bảo vệ tương tự như quốc gia cư trú của bạn. Bằng cách đặt chỗ, bạn đồng ý cho chúng tôi sử dụng và tiết lộ thông tin cá nhân.</li><li class="text-secondary-v2 text-base">SJourney yêu cầu gửi thông tin cá nhân (ít nhất) của những hành khách đi du lịch chính sau khi nhận được xác nhận đặt chỗ. Các đại lý du lịch phải cung cấp thông tin này thay mặt cho hành khách. Nếu người đặt chỗ không cung cấp thông tin được yêu cầu, SJourney có quyền thu hồi xác nhận mà không có bất kỳ nghĩa vụ nào. Chúng tôi yêu cầu bạn cung cấp đầy đủ thông tin chi tiết về tất cả hành khách đi du lịch 14 ngày trước khi đến điểm đến hoặc ngày bắt đầu của chuyến tham quan hoặc hành trình đã đặt với chúng tôi. Chúng tôi có quyền kiểm tra hộ chiếu, thị thực và giấy tờ du lịch của bạn để đảm bảo rằng bạn đáp ứng mọi yêu cầu đối với bất kỳ quốc gia nào mà chuyến đi của bạn sẽ diễn ra. Nếu bạn hoặc đại lý du lịch thực hiện đặt chỗ thay mặt bạn không thể đáp ứng bất kỳ yêu cầu nào như vậy, không tuân thủ các yêu cầu này hoặc không nộp các giấy tờ du lịch bắt buộc theo thời gian yêu cầu, chúng tôi có quyền áp dụng một khoản phí dịch vụ hợp lý hoặc có thể từ chối cho khách lên máy bay với toàn bộ phí hủy mà không phải chịu thêm bất kỳ trách nhiệm nào.</li><li class="text-secondary-v2 text-base">Khôngđược phép mang động vật và gia súc lên tàu và sẽ không được vận chuyển trên bất kỳ chuyến tàu nào.</li><li class="text-secondary-v2 text-base">CácĐiều kiện Đặt chỗ này và bất kỳ thỏa thuận nào mà chúng áp dụng đều được điều chỉnh theo luật pháp Việt Nam. Chúng tôi đều đồng ý rằng bất kỳ tranh chấp, khiếu nại hoặc vấn đề nào khác phát sinh giữa chúng ta phát sinh từ hoặc liên quan đến hợp đồng hoặc đặt chỗ của bạn sẽ chỉ được giải quyết bởi Tòa án Việt Nam.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Xác nhận đặt chỗ', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Để đặt chỗ, vui lòng đặt giữ chỗ qua Websitehoặc gọi số 19003440. Khi nhận được yêu cầu và tùy thuộc vào tình trạng còn chỗ, chúng tôi sẽ TẠM THỜI đặt chỗ cho bạn vào ngày đã chọn. Chỗ ngồi sẽ được giữ, NHƯNG nếu có khách khác yêu cầu xác nhận cabin, người giữ chỗ/khách sẽ được yêu cầu xác nhận bằng khoản tiền đặt cọc hoặc hủy giữ chỗ cabin đó. Sau khi nhận được xác nhận bằng văn bản qua email và hoàn thành Mẫu Đặt Chỗ, thông tin xác nhận và hóa đơn tạm tính sẽ được gửi đến bạn.</li><li class="text-secondary-v2 text-base">Khi nhận được khoản thanh toán liên quan, đặt chỗ sẽ được XÁC NHẬN, và hóa đơn xác nhận sẽ được gửi lại. Khi đặt chỗ được xác nhận, các Điều Khoản &amp;amp; Điều Kiện của chúng tôi sẽ được coi là đã được chấp nhận và sẽ được tuân thủ nghiêm ngặt.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Thanh toán đặt chỗ', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Thanh toán có thể được thực hiện qua chuyển khoản ngân hàng hoặc thẻ tín dụng. Chi tiết sẽ được cung cấp tại thời điểm đặt chỗ.</li><li class="text-secondary-v2 text-base">Do chi phí xử lý thẻ tín dụng ở Việt Nam khá cao, chúng tôi ưu tiên khách hàng thanh toán qua chuyển khoản ngân hàng. Vui lòng ghi rõ số tham chiếu đặt chỗ của bạn trong phần nội dung chuyển khoản và gửi email xác nhận thanh toán.</li><li class="text-secondary-v2 text-base">Chúng tôi là một công ty tư nhân tại Việt Nam và có quyền tính phí hoặc hoàn tiền bằng đồng Việt Nam (VND). Theo quy định pháp luật, các khoản hoàn tiền sẽ được xử lý vào đúng tài khoản mà từ đó thanh toán ban đầu được thực hiện.</li><li class="text-secondary-v2 text-base">Tất cả các khoản thanh toán hoặc hoàn tiền qua thẻ tín dụng cho mọi hành trình đều được thực hiện bằng VND. SJourney không chịu trách nhiệm đối với bất kỳ mất mát nào về giá trị tiền tệ do biến động tỷ giá hối đoái.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Trước khi khởi hành', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Sắp xếp chuyến tham quan: Chương trình du lịch của chúng tôi được lên kế hoạch từ nhiều tháng trước và đôi khi cần thay đổi (bao gồm nhưng không giới hạn ở việc hủy bỏ hoặc thay thế diễn giả khách mời). Chúng tôi có quyền thực hiện các thay đổi này bất cứ lúc nào. Phần lớn các thay đổi này sẽ không đáng kể (ví dụ: thay đổi dịch vụ toa tàu gắn vào tàu dịch vụ thông thường) và chúng tôi sẽ thông báo cho bạn hoặc đại lý du lịch của bạn sớm nhất có thể.</li><li class="text-secondary-v2 text-base">Thay đổi: Nếu có thay đổi nhỏ trong chuyến đi, chúng tôi sẽ cố gắng thông báo cho bạn hoặc đại lý du lịch của bạn sớm nhất có thể nếu có thời gian trước khi khởi hành. Tuy nhiên, chúng tôi không chịu trách nhiệm pháp lý trong trường hợp này. Các ví dụ về thay đổi nhỏ bao gồm: Thay đổi chuyến bay đi/về dưới 12 giờ. Thay đổi loại máy bay. Thay đổi chỗ ở sang chỗ khác có tiêu chuẩn tương tự hoặc cao hơn. Thay đổi hãng vận chuyển. Lưu ý rằng các hãng vận chuyển (như hãng hàng không) trong tài liệu quảng cáo có thể thay đổi.</li><li class="text-secondary-v2 text-base">Do thay đổi lịch bay hoặc lý do vận hành khác sau khi tour đã được đặt, có thể cần thay đổi hãng hàng không, sân bay hoặc máy bay đã thông báo. Các thay đổi như vậy sẽ được thông báo cho bạn hoặc đại lý sớm nhất có thể và trong mọi trường hợp sẽ được thông báo tại quầy check-in hoặc cổng lên máy bay. Những thay đổi này được coi là không đáng kể và các điều khoản nêu trên sẽ được áp dụng.</li><li class="text-secondary-v2 text-base">Nếu chúng tôi cần thực hiện thay đổi quan trọng, chúng tôi sẽ thông báo cho bạn sớm nhất có thể trước ngày khởi hành (nếu có đủ thời gian). Bạn sẽ có các lựa chọn sau:</li><li class="text-secondary-v2 text-base">Chấp nhận các sắp xếp đã thay đổi.</li><li class="text-secondary-v2 text-base">Nhận lại toàn bộ số tiền đã thanh toán.</li><li class="text-secondary-v2 text-base">Nếu có và chúng tôi cung cấp, chấp nhận một kỳ nghỉ thay thế (chúng tôi sẽ hoàn lại khoản chênh lệch nếu kỳ nghỉ thay thế có giá trị thấp hơn).</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Sau khởi hành', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Đối với các tour du lịch đường sắt bao gồm vận chuyển bằng đầu máy xe lửa cụ thể, đi trên toa tàu cụ thể hoặc trên các tuyến cụ thể, trong khi mọi nỗ lực sẽ được thực hiện để đảm bảo rằng các tính năng này được cung cấp như đã quảng cáo, có thể có những trường hợp, vì những lý do nằm ngoài tầm kiểm soát của chúng tôi, các tính năng cụ thể không thể được cung cấp. Trong trường hợp này, chúng tôi sẽ cố gắng cung cấp lực kéo, chỗ ở hoặc tuyến đường thay thế tùy theo hoàn cảnh cho phép. Nếu chúng tôi không thể đưa ra các sắp xếp thay thế phù hợp hoặc nếu bạn từ chối chấp nhận những điều này vì lý do chính đáng, chúng tôi sẽ sắp xếp đưa bạn trở lại điểm khởi hành tour của bạn hoặc đến điểm dịch vụ theo hợp đồng của chúng tôi bắt đầu sớm nhất có thể.</li><li class="text-secondary-v2 text-base">Tuy nhiên, chúng tôi và các đại lý của chúng tôi luôn có quyền thay đổi các khía cạnh của chuyến tham quan vì lý do cơ học, vận hành hoặc các vấn đề khác nằm ngoài tầm kiểm soát hợp lý của chúng tôi (chẳng hạn như bất kỳ hoạt động chiến tranh hoặc khủng bố nào (có nguy cơ hoặc thực tế), bạo loạn, bất ổn dân sự, đóng cửa sân bay, hành động công nghiệp (có nguy cơ hoặc thực tế), điều kiện chính trị, thảm họa thiên nhiên hoặc hạt nhân, hỏa hoạn, điều kiện thời tiết bất lợi, thay đổi hoặc hủy dịch vụ hàng không, đường sông, đường biển hoặc đường sắt hoặc bất kỳ sự kiện nào khác nằm ngoài tầm kiểm soát hợp lý của chúng tôi).</li><li class="text-secondary-v2 text-base">Chúng tôi có quyền trong một số trường hợp (theo quyết định riêng của chúng tôi), do các quy trình quản lý tải trọng hiệu quả trên tàu, cung cấp dịch vụ nâng hạng khoang cho những hành khách được chọn mà không tính thêm phí. Việc nâng hạng khoang như vậy không được đảm bảo, không thể yêu cầu và chỉ có thể được thông báo vào ngày đầu tiên của chuyến tham quan.</li><li class="text-secondary-v2 text-base">Chúng tôi và các đại lý của chúng tôi luôn có quyền hủy bỏ và hoặc sửa đổi bất kỳ chương trình nào của diễn giả khách mời mà không phải chịu bất kỳ trách nhiệm nào với bạn mà không cần thông báo cho bạn và đây luôn là một thay đổi nhỏ. Để tránh nghi ngờ, bạn sẽ không được phép hủy chuyến tham quan của mình và/hoặc bất kỳ khoản hoàn lại nào cho số tiền bạn đã thanh toán, do việc hủy và/hoặc sửa đổi chương trình của diễn giả khách mời. Nếu bạn hủy chuyến tham quan của mình vì những lý do như vậy, phí hủy sẽ được áp dụng.</li><li class="text-secondary-v2 text-base">Nếu chúng tôi không thể cung cấp dịch vụ tương đương các sắp xếp mà bạn đã đặt với chúng tôi sau khi bạn đã khởi hành, nếu có thể, chúng tôi sẽ sắp xếp thay thế cho bạn mà không tính thêm phí và trong trường hợp các sắp xếp thay thế đó có tiêu chuẩn thấp hơn, chúng tôi sẽ cung cấp cho bạn mức giảm giá phù hợp</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Lịch trình, tuyến đường và các chuyến tham quan', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">SJourney hoàn toàn phụ thuộc vào hệ thống vận hành và dịch vụ đường sắt do ngành đường sắt quốc gia Việt Nam cung cấp. Thật không may, khi các dịch vụ này gặp sự cố như hỏng đầu máy, tín hiệu, thiệt hại hoặc mất cắp cáp điện, hành vi phá hoại, mất điện, đường ray gặp vấn đề hoặcbất kỳ sự cố nào khác, chúng tôi bị hạn chế khả năng xử lý. Các sự cố này có thể dẫn đến sự chậm trễ do sự phụ thuộc của chúng tôi vào các tổ chức quốc gia.</li><li class="text-secondary-v2 text-base">SJourney không chịu trách nhiệm và không thể bị quy trách nhiệm cho bất kỳ sự chậm trễ hoặc thay đổi nào trong lịch trình tour do các yếu tố tự nhiên hoặc các vấn đề khác nằm ngoài tầm kiểm soát của chúng tôi.</li><li class="text-secondary-v2 text-base">SJourney có quyền hủy bỏ hoặc điều chỉnh tuyến đường, các chuyến tham quan và ngày khởi hành của tour, miễn là chúng tôi có thể cung cấp cho hành khách các chuyến tham quan hoặc ngày khởi hành thay thế.</li><li class="text-secondary-v2 text-base">SJourney không thể đảm bảo chính xác thời gian của các chuyến tham quan, giờ khởi hành hoặc giờ đến. Nghĩa vụ chính của chúng tôi là vận chuyển hành khách từ điểm xuất phát đến điểm đến theo lịch trình, điều này sẽ được ưu tiên hơn bất kỳ cam kết nào liên quan đến các chuyến tham quan. Tuy nhiên, chúng tôi sẽ cố gắng hết sức để thực hiện các trải nghiệm ngoài tàu theo kế hoạch.</li><li class="text-secondary-v2 text-base">Chúng tôi khuyến cáo mạnh mẽ không nên sắp xếp các chuyến bay cùng ngày với ngày khởi hành hoặc ngày đến của tàu do có thể xảy ra các sự cố chậm trễ liên quan đến chuyến bay hoặc tàu.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Điều kiện vé máy bay và dịch vụ đưa đón', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Góikỳ nghỉ bằng tàu hỏa không bao gồm vé máy bay trừ khi có quy định khác. Tất cả giá vé máy bay tùy thuộc vào tình trạng còn chỗ và áp dụng các điều kiện của hãng hàng không. Chuyên gia tư vấn của chúng tôi có thể hỗ trợ bạn đặt chuyến bay phù hợp hoặc dịch vụ khác bất cứ khi nào có thể mà không phải là một phần của hợp đồng hành trình hoặc tour du lịch (gói) với chúng tôi. Bạn có thể đặt chuyến bay thông qua bất kỳ đại lý du lịch nào. Chúng tôi không chịu trách nhiệm và nghĩa vụ đối với bất kỳ chuyến bay nào, việc thay đổi chuyến bay hoặc hậu quả phát sinh do nhà cung cấp sửa đổi hoặc hủy chuyến bay. Bạn có thể lấy thông tin chi tiết và điều kiện đầy đủ từ chuyên gia tư vấn đặt chỗ của chúng tôi hoặc đại lý du lịch của bạn liên quan đến việc đặt chuyến bay của bạn. Bạn phải đảm bảo cung cấp thông tin chi tiết về chuyến bay cho chúng tôi đúng hạn (xem phần Thông tin hành khách chi tiết bên dưới). Nếu bạn lỡ chuyến bay hoặc bất kỳ chuyến chuyển nào đã đặt trước, bạn sẽ chịu trách nhiệm đến điểm khởi hành của hành trình/tour du lịch (gói), với chi phí do khách tự chi trả. Trừ khi được nêu rõ trong hành trình, việc chuyển sân bay có thể là chuyển theo nhóm được lên lịch trùng với thời gian đến và khởi hành của nhiều chuyến bay. Có thể sắp xếp dịch vụ đưa đón riêng với chi phí riêng với nhà cung cấp dịch vụ và không phải là một phần của hành trình hoặc hợp đồng tour (gói). Không phải địa điểm nào cũng có dịch vụ đưa đón riêng. Vui lòng tìm hiểu thêm thông tin chi tiết.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Khiếu nại', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trongtrường hợp không mong muốn xảy ra sự cố trong chuyến đi, vui lòng thông báo ngay cho ban quản lý tàu hỏa, họ sẽ cố gắng giải quyết. Nếu bạn bị mất hoặc hư hỏng hành lý, vui lòng thông báo ngay cho chúng tôi. Nếu khiếu nại của bạn không được giải quyết tại địa phương và bạn muốn khiếu nại thêm, vui lòng gửi thông báo chính thức bằng văn bản về khiếu nại của bạn đến văn phòng chính của SJourney trong vòng 28 ngày kể từ ngày kết thúc chuyến đi (cung cấp mã đặt chỗ và mọi thông tin liên quan khác). Nếu không làm như vậy, chúng tôi sẽ không điều tra khiếu nại của bạn và có thể ảnh hưởng đến quyền của bạn theo các điều kiện này.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Tình huống bất khả kháng', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trừ khi có quy định rõ ràng khác trong các điều kiện này, chúng tôi sẽ không chịu trách nhiệm hoặc bồi thường nếu các nghĩa vụ theo hợp đồng của chúng tôi bị ảnh hưởng bởi bất kỳ sự kiện nào mà chúng tôi hoặc nhà cung cấp dịch vụ có liên quan không thể lường trước hoặc tránh được, ngay cả khi đã hết sức cẩn trọng. Các sự kiện này có thể bao gồm nhưng không giới hạn ở chiến tranh, khủng bố, đe dọa chiến tranh, xung đột dân sự, hỏa hoạn, lũ lụt hoặc bất kỳ điều kiện thời tiết khắc nghiệt nào khác và hậu quả của nó hoặc mối đe dọa của hoạt động đó, bạo loạn, khó khăn về lao động, hành động của bất kỳ chính phủ hoặc cơ quan quốc gia hoặc địa phương nào khác bao gồm tranh chấp lao động, đóng cửa khóa, thảm họa thiên nhiên hoặc hạt nhân, thảm họa hóa học hoặc sinh học, thời tiết bất lợi, điều kiện biển, không khí, băng và sông, mất điện, dịch bệnh hoặc đại dịch, không có khả năng xin được bất kỳ giấy phép hoặc sự đồng ý cần thiết nào và sự chậm trễ do các nhà thầu phụ gây ra và tất cả các sự kiện tương tự nằm ngoài tầm kiểm soát của chúng tôi hoặc nhà cung cấp liên quan.</li><li class="text-secondary-v2 text-base">Trong trường hợp Bất khả kháng khiến việc thực hiện hợp đồng đặt chỗ trở nên không thể hoặc ngăn cản việc thực hiện hợp đồng đặt chỗ tại thời điểm đi lại hoặc trong thời gian hợp lý trước ngày đi lại, hợp đồng đặt chỗ có thể bị chấm dứt bởi một trong hai bên. Một sự kiện như vậy cũng yêu cầu phải có thông báo chính thức của các cơ quan quốc gia hoặc quốc tế (ví dụ: ban hành cảnh báo đi lại, tuyên bố rủi ro hoặc [một phần] tình trạng khẩn cấp) tại thời điểm đi lại, trong khoảng thời gian hợp lý trước ngày đi lại hoặc không còn tồn tại. Trong trường hợp như vậy, SJourney sẽ cấp khoản tín dụng trong tương lai bằng với số tiền bạn đã thanh toán cho chúng tôi (trừ bất kỳ chi phí nào không được hoàn lại của bên thứ ba nếu có). Xin lưu ý rằng bạn sẽ không được hoàn lại tiền mặt. Các đại lý du lịch hoặc người bán lại đặt chỗ thay mặt cho khách và chưa thanh toán cho chúng tôi phí hủy nếu có, khi chấm dứt hợp đồng đặt chỗ do Bất khả kháng, sẽ không được hoàn lại tiền dưới dạng tín dụng trong tương lai trừ khi các khoản phí mở đã được cân đối để đổi lại.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Mất hoặc hư hại tài sản', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">SJourneycung cấp dịch vụ bảo quản an toàn cho các đồ vật có giá trị trên tàu và khuyến khích hành khách gửi bất kỳ đồ trang sức hoặc đồ vật có giá trị nào khác mang lên tàu vào hộp an toàn trong phòng được cung cấp. SJourney sẽ không chịu trách nhiệm đối với bất kỳ mất mát hoặc hư hỏng nào đối với tiền, đồ trang sức, đá quý, chứng khoán, công cụ tài chính, vé hoặc các đồ vật có giá trị khác trừ khi được cất giữ trong hộp an toàn trong phòng. Giá trị của các mặt hàngkhông được vượt quá 500 USD trừ khi SJourney đồng ý bằng văn bản về giá trị cao hơn cho các mặt hàng đó. Các mặt hàng vượt quá giới hạn 500 đô la Mỹ phải được giao cho ban quản lý tàu để giữ an toàn và phải xuất biên lai. Việc giải quyết các yêu cầu bồi thường đối với đồ đạc bị mất sẽ được thực hiện dựa trên giá trị tiền mặt thực tế (chi phí thay thế trừ khấu hao). Việc giải quyết các yêu cầu bồi thường đối với đồ đạc bị hư hỏng sẽ dựa trên chi phí sửa chữa hoặc giá trị tiền mặt thực tế, tùy theo giá trị nào nhỏ hơn. Không được thanh toán bất kỳ khoản tiền nào khi giải quyết bất kỳ yêu cầu bồi thường nào nếu không có bằng chứng về giá trị tiền mặt thực tế hoặc chi phí sửa chữa phát sinh do mất mát hoặc hư hỏng. Bằng chứng đó phải được gửi cho SJourney bằng văn bản và trách nhiệm của SJourney phải được chứng minh trước khi bất kỳ khoản giải quyết nào được thanh toán. SJourney sẽ không chịu trách nhiệm đối với việc mất mát hoặc hư hỏng hành lý hoặc đồ dùng cá nhân. Đồ đạc cá nhân bị mất khi không có người trông coi tại phòng chờ công cộng hoặc các khu vực công cộng khác, cho dù trên tàu hay ở nơi khác, đều không được hoàn trả. Các khoản mất mát do hao mòn thông thường, nguy hiểm trong chuyến đi và các hành động của Chúa khác không được hoàn trả.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Tài liệu du lịch / xác nhận đặt chỗ', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Bạnphải có các giấy tờ du lịch hợp lệ bao gồm xác nhận đặt chỗ hợp lệ trước khi bắt đầu hành trình hoặc tour du lịch (gói) hoặc lên tàu, do SJourney hoặc bất kỳ bên thứ ba nào mà bạn đã đặt hành trình và SJourney đã cấp các giấy tờ du lịch hợp lệ cho. Các giấy tờ du lịch chỉ có giá trị cho các ngày, tuyến đường và chỉ được sử dụng với các chi tiết được hiển thị trên các giấy tờ du lịch. Các giấy tờ du lịch không được chuyển nhượng (trừ khi được phép rõ ràng theo bất kỳ thỏa thuận nào thông qua bên thứ ba). Các giấy tờ du lịch có sự thay đổi trái phép không có giá trị để đi du lịch. Các giấy tờ du lịch vẫn là tài sản của hãng vận chuyển và nếu bạn không tuân thủ bất kỳ điều kiện quan trọng nào (bao gồm nhưng không giới hạn ở các điều kiện này) chi phối việc sử dụng, giấy tờ du lịch của bạn có thể bị thu hồi hoặc vô hiệu và bạn phải trả toàn bộ giá vé. Có thể có một khoản phí hành chính để thay thế bất kỳ giấy tờ du lịch nào bị mất hoặc bị hư hỏng. Trước khi bắt đầu hành trình, hãy đảm bảo rằng bạn đã nhận được tất cả các giấy tờ du lịch mới nhất và thực tế từ chúng tôi hoặc bất kỳ bên thứ ba nào, bao gồm xác nhận đặt chỗ và tất cả các giấy tờ du lịch mới nhất có liên quan như hành trình, thông tin lên tàu, thông tin bao gồm, thông tin không bao gồm, v.v. có sẵn trên trang web của SJourney và áp dụng tại thời điểm lên tàu.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Dừng lại & gián đoạn chuyến đi', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Nếubạn dừng lại trong chuyến đi ngoài thời gian được phép, bạn sẽ không được phép tiếp tục chuyến đi để được hoàn lại tiền hoặc bất kỳ khoản bồi thường nào khác từ chúng tôi.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Tình trạng y tế & khuyết tật', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Nếubạn hoặc bất kỳ thành viên nào trong đoàn của bạn có bất kỳ tình trạng y tế hoặc khuyết tật nào có thể ảnh hưởng đến chuyến đi của bạn, vui lòng cung cấp cho chúng tôi thông tin chi tiết đầy đủ trước khi xác nhận đặt chỗ để SJourney có thể tư vấn về tính phù hợp của bất kỳ sự sắp xếp nào đã chọn. Theo cách hành động hợp lý, nếu chúng tôi không thể đáp ứng nhu cầu của những người liên quan, chúng tôi sẽ không xác nhận đặt chỗ của bạn hoặc nếu bạn không cung cấp cho chúng tôi thông tin chi tiết đầy đủ tại thời điểm đặt chỗ, chúng tôi có quyền hủy đặt chỗ và áp dụng các khoản phí hủy áp dụng khi chúng tôi biết được những thông tin chi tiết này. Nếu khả năng đi lại của bạn có thể bị nghi ngờ do bệnh tật gần đây, phẫu thuật, chấn thương, điều trị y tế hoặc tình trạng bệnh lý hiện tại có thể cần điều trị hoặc hỗ trợ trên máy bay (bao gồm nhưng không giới hạn ở oxy bổ sung, xe lăn, hỗ trợ sử dụng phòng tắm, hỗ trợ lên hoặc xuống giường, thuốc được tiêm qua kim tiêm ngoài bệnh tiểu đường được kiểm soát tốt), bạn phải cung cấp cho chúng tôi giấy chứng nhận cập nhật từ bác sĩ của bạn chậm nhất là 5 ngày trước khi khởi hành xác nhận rằng bạn đủ khả năng đi lại và có một hành khách khác đi cùng, người có thể cung cấp cho bạn mọi hỗ trợ phù hợp mà bạn có thể cần. Chúng tôi có thể từ chối vận chuyển (và sẽ không hoàn lại tiền hoặc bồi thường) nếu bạn không cung cấp giấy chứng nhận áp dụng và/hoặc không có người hộ tống phù hợp đi cùng.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Chăm sóc y tế', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">SJourneykhông thuê bác sĩ hoặc y tá trên tàu. Nếu hành khách cần chăm sóc y tế trong suốt chuyến đi, các dịch vụ y tế sẽ được cung cấp tại các cơ sở địa phương. Hành khách sẽ chịu trách nhiệm về mọi chi phí và phí tổn của các dịch vụ y tế, điều trị và thuốc men. SJourney sẽ không chịu bất kỳ trách nhiệm nào đối với bất kỳ chi phí nào phát sinh liên quan đến các dịch vụ y tế hoặc đối với chất lượng chăm sóc mà Hành khách nhận được. Hành khách đồng ý điều trị bởi các chuyên gia y tế do SJourney chỉ định, nếu hành khách không thể yêu cầu hoặc ủy quyền điều trị y tế và theo ý kiến \u200b\u200bcủa Quản lý tàu, việc chăm sóc y tế là cần thiết, hoàn toàn do hành khách chi trả và không chịu trách nhiệm đối với SJourney.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Khuyết tật hoặc thai kỳ', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">SJourneykhông phân biệt đối xử với những người dựa trên tình trạng khuyết tật. SJourney không thể tiếp nhận phụ nữ mang thai đã qua tháng thứ sáu. Hành khách bị khiếm khuyết về khả năng vận động phải có người đi cùng có thể hỗ trợ hành khách trên đất liền và trên tàu, và có trách nhiệm cung cấp cho hành khách mọi sự hỗ trợ và tiện nghi cần thiết. Nếu bất kỳ tình trạng nào như vậy phát sinh sau khi hành trình đã được đặt, hành khách phải thông báo cho SJourney bằng văn bản ngay lập tức. SJourney và tất cả nhân viên trên tàu sẽ không chịu trách nhiệm với hành khách liên quan đến tình trạng đó hoặc cách điều trị tình trạng đó.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Bảo hiểm du lịch', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hànhkhách phải được bảo hiểm đầy đủ để chi trả mọi rủi ro về chi phí y tế và hồi hương (bao gồm cả hài cốt) và SJourney không chịu trách nhiệm theo bất kỳ cách nào đối với các nghĩa vụ đó. Chúng tôi đặc biệt khuyến nghị hành khách mua bảo hiểm du lịch để chi trả mọi rủi ro về phí hủy chuyến không lường trước được.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Hộ chiếu & thị thực', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Bạn có trách nhiệm kiểm tra và đáp ứng các yêu cầu về hộ chiếu, thị thực, sức khỏe (bao gồm tiêm chủng, yêu cầu về giấy chứng nhận tiêm chủng và thuốc chống sốt rét) và nhập cư áp dụng cho hành trình du lịch của mình. Bạn phải kiểm tra các yêu cầu cho hoàn cảnh cụ thể của mình với các đại sứ quán và/hoặc lãnh sự quán có liên quan và bác sĩ của bạn nếu có. Các yêu cầu có thể thay đổi và bạn phải kiểm tra vị trí cập nhật kịp thời trước khi khởi hành. Chúng tôi không chịu bất kỳ trách nhiệm nào nếu bạn không thể đi du lịch hoặc chịu bất kỳ tổn thất nào khác do bạn không tuân thủ bất kỳ yêu cầu nào về hộ chiếu, thị thực, nhập cư hoặc thủ tục y tế. Bạn đồng ý hoàn trả cho chúng tôi theo yêu cầu đối với bất kỳ khoản tiền phạt hoặc tổn thất nào khác mà chúng tôi phải chịu do bạn không tuân thủ bất kỳ yêu cầu nào về hộ chiếu, thị thực, nhập cư hoặc thủ tục y tế.</li><li class="text-secondary-v2 text-base">Thông tin hành khách và bảo vệ dữ liệu</li><li class="text-secondary-v2 text-base">Để xử lý đặt chỗ của bạn, bạn sẽ cần cung cấp cho chúng tôi thông tin cá nhân. Chúng tôi sẽ cần cung cấp thông tin cá nhân của bạn cho các nhà cung cấp dịch vụ cũng như cơ quan hải quan và nhập cư. Chúng tôi cũng có thể cung cấp thông tin cá nhân cho các tổ chức an ninh và kiểm tra tín dụng. Một số người mà chúng tôi cung cấp thông tin cá nhân của bạn đang ở các quốc gia có thể không cung cấp mức độ bảo vệ tương tự như quốc gia cư trú của bạn. Bằng cách đặt chỗ, bạn đồng ý cho chúng tôi sử dụng và tiết lộ thông tin cá nhân.</li><li class="text-secondary-v2 text-base">SJourney yêu cầu gửi thông tin cá nhân (ít nhất) của những hành khách đi du lịch chính sau khi nhận được xác nhận đặt chỗ. Các đại lý du lịch phải cung cấp thông tin này thay mặt cho hành khách. Nếu người đặt chỗ không cung cấp thông tin được yêu cầu, SJourney có quyền thu hồi xác nhận mà không có bất kỳ nghĩa vụ nào. Chúng tôi yêu cầu bạn cung cấp đầy đủ thông tin chi tiết về tất cả hành khách đi du lịch 14 ngày trước khi đến điểm đến hoặc ngày bắt đầu của chuyến tham quan hoặc hành trình đã đặt với chúng tôi. Chúng tôi có quyền kiểm tra hộ chiếu, thị thực và giấy tờ du lịch của bạn để đảm bảo rằng bạn đáp ứng mọi yêu cầu đối với bất kỳ quốc gia nào mà chuyến đi của bạn sẽ diễn ra. Nếu bạn hoặc đại lý du lịch thực hiện đặt chỗ thay mặt bạn không thể đáp ứng bất kỳ yêu cầu nào như vậy, không tuân thủ các yêu cầu này hoặc không nộp các giấy tờ du lịch bắt buộc theo thời gian yêu cầu, chúng tôi có quyền áp dụng một khoản phí dịch vụ hợp lý hoặc có thể từ chối cho khách lên máy bay với toàn bộ phí hủy mà không phải chịu thêm bất kỳ trách nhiệm nào.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Động vật', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Khôngđược phép mang động vật và gia súc lên tàu và sẽ không được vận chuyển trên bất kỳ chuyến tàu nào.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Quyền hạn và luật áp dụng', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">CácĐiều kiện Đặt chỗ này và bất kỳ thỏa thuận nào mà chúng áp dụng đều được điều chỉnh theo luật pháp Việt Nam. Chúng tôi đều đồng ý rằng bất kỳ tranh chấp, khiếu nại hoặcvấn đề nào khác phát sinh giữa chúng ta phát sinh từ hoặc liên quan đến hợp đồng hoặc đặt chỗ của bạn sẽ chỉ được giải quyết bởi Tòa án Việt Nam.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (7, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Việc hủy đặt chỗ đã xác nhận trước ngày khởi hành sẽ phải chịu một khoản phí dựa trên giá tour cho mỗi người. Vui lòng tham khảo Điều Khoản &amp; Điều Kiện để biết thêm chi tiết.</li><li class="text-secondary-v2 text-base">Bảo hiểm hủy tour là bắt buộc vì các khoản phí này sẽ không được miễn trừ. Chúng tôi cung cấp bảo hiểm cho khách hàng là cư dân Việt Nam. Đối với khách quốc tế, chúng tôi khuyến nghị bạn liên hệ với đại lý bảo hiểm địa phương để được hỗ trợ.</li><li class="text-secondary-v2 text-base">Vui lòng mang theo một bản sao hợp đồng bảo hiểm du lịch của bạn, trong đó ghi rõ tên công ty bảo hiểm, số hợp đồng, và số điện thoại khẩn cấp 24/7.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (8, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe du lịch đời mới đưa đón phục vụ suốt tuyến</li><li class="text-secondary-v2 text-base">Hướng dẫn viên nhiệt tình, kinh nghiệm</li><li class="text-secondary-v2 text-base">Bữa ăn trưa theo chương trình set menu 200.000VNĐ/ khách ( không bao gồm đồ uống)</li><li class="text-secondary-v2 text-base">Vé vào cửa 01 khách/ lượt</li><li class="text-secondary-v2 text-base">Nước uống 01 chai 500ml/ khách/ ngày</li><li class="text-secondary-v2 text-base">Chi phí tổ chức teambuilding : thuê địa điểm, âm thanh, loa đài, sân bãi, hoạt náo viên, dụng cụ trò chơi, kịch bản</li><li class="text-secondary-v2 text-base">Thuê nhà sàn nghỉ trưa sạch sẽ</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức cao nhất 20.000.000đ/người/vụ</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (8, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chi phí cá nhân phát sinh ngoài chương trình.</li><li class="text-secondary-v2 text-base">Tiền Tip HDV/ Lái xe nếu có</li><li class="text-secondary-v2 text-base">Các dịch vụ spa, tennis, đạp xe, đạp vịt, v.v.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (9, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vận chuyển: Xe du lịch 45 chỗ đời mới chất lượng, điều hòa phục vụ theo lịch trình.</li><li class="text-secondary-v2 text-base">Vé vào cửa 01 lần vào Khu du lịch Hà Nội Paragon Hill</li><li class="text-secondary-v2 text-base">Vé thăm quan Hanoi Paragon Hill Resort như: Tham quan suối Thác Đa, vườn hồng lớn nhất</li><li class="text-secondary-v2 text-base">MiềnBắc, công viên Nhị Long, toàn cảnh khu Resort, bảo tàng ....</li><li class="text-secondary-v2 text-base">Các bữa ăn trong chương trình: 01 bữa trưa mức 200.000Đ/ suất</li><li class="text-secondary-v2 text-base">Hướng dẫn viên thành thạo, kinh nghiệm, nhiệt tình theo suốt hành trình</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch trọn tour mức 20.000.000Đ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống trên xe tiêu chuẩn 1 chai/ người/ ngày</li><li class="text-secondary-v2 text-base">Nhà sàn nghỉ trưa theo chương trình</li><li class="text-secondary-v2 text-base">Sử dụng xe bus điện không giới hạn</li><li class="text-secondary-v2 text-base">Thuế VAT</li><li class="text-secondary-v2 text-base">Chi phí tổ chức Teambuilding: MC, dụng cụ, kịch bản</li><li class="text-secondary-v2 text-base">Chi phí sân bãi, loa đài.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (9, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chi phí cá nhân phát sinh ngoài chương trình.</li><li class="text-secondary-v2 text-base">Tiền Tip HDV/ Lái xe nếu có</li><li class="text-secondary-v2 text-base">Các dịch vụ khác ngoài chương trình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (10, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô đời mới, máy lạnh suốt tuyến</li><li class="text-secondary-v2 text-base">Hướng dẫn viên nhiệt tình, kinh nghiệm</li><li class="text-secondary-v2 text-base">01 bữa ăn chính (150,000 vnđ/bữa)</li><li class="text-secondary-v2 text-base">Nước uống trên xe 1 chai 50ml/ người/ ngày</li><li class="text-secondary-v2 text-base">Vé vào cửa các điểm tham quan trong lịch trình 1 lượt/ người</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch 20.000.000VNĐ/ người/ vụ</li><li class="text-secondary-v2 text-base">Phí tổ chức teambuilding</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (10, 'Không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Thuế VAT</li><li class="text-secondary-v2 text-base">Phí bồi dưỡng cho hướng dẫn viên và tài xế</li><li class="text-secondary-v2 text-base">Các chi phí cá nhân ngoài chương trình</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (10, 'Phụ thu trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em từ 0-5 tuổi: Miễn phí, ăn ngủ chung với bố mẹ. (Hai người lớn chỉ được kèm 01 trẻ em. Từ trẻ em thứ 2 phụ thu 50% người lớn)</li><li class="text-secondary-v2 text-base">Trẻ em từ: 5-10 tuổi: phụ thu 75% tour</li><li class="text-secondary-v2 text-base">Trẻ trên 11 tuổi: Tính như người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (11, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô đời mới, máy lạnh suốt tuyến</li><li class="text-secondary-v2 text-base">Hướng dẫn viên nhiệt tình, kinh nghiệm</li><li class="text-secondary-v2 text-base">03 bữa ăn chính theo lịch trình: 1 bữa x 350.000VND/khách tại FLC Vĩnh Thịnh, 150.000VND/khách tại Sơn Tây và Vĩnh Phúc.</li><li class="text-secondary-v2 text-base">Nước uống trên xe 1 chai/ người/ ngày</li><li class="text-secondary-v2 text-base">Vé vào cửa các điểm tham quan trong lịch trình 1 lượt/ người</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch 50.000.000VNĐ/ người/ vụ</li><li class="text-secondary-v2 text-base">Phòng nghỉ tại FLC Vĩnh Thịnh: 2 người lớn/phòng, nam nữ nghỉ phòng 3.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (11, 'Không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Thuế VAT</li><li class="text-secondary-v2 text-base">Phí bồi dưỡng cho hướng dẫn viên và tài xế</li><li class="text-secondary-v2 text-base">Các chi phí cá nhân ngoài chương trình</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (11, 'Phụ thu trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em từ 0-5 tuổi: Miễn phí tour, ngồi lòng bố/mẹ, bố/mẹ chuẩn bị xuất ăn. (Hai người lớn chỉ được kèm 01 trẻ em. Từ trẻ em thứ 2 phụ thu 50% người lớn)</li><li class="text-secondary-v2 text-base">Trẻ em từ: 5 – dưới 11 tuổi: phụ thu 75% tour, có chỗ ngồi trên xe, ăn 1 xuất ăn, ngủ cùng bố mẹ</li><li class="text-secondary-v2 text-base">Trẻ trên 11 tuổi: Tính như người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (12, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô du lịch đời mới đưa đón suốt hành trình.</li><li class="text-secondary-v2 text-base">Phòng riêng tại resort tiêu chuẩn ngủ 2, nhóm lẻ ngủ 3 (không kêthêm giường)</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">Ăn trưa theo chương trình.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 30.000.000đ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li><li class="text-secondary-v2 text-base">Vé thăm quan 01 lần theo hành trình.</li><li class="text-secondary-v2 text-base">Chi phí tổ chức gala dinner</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (12, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chi phí cá nhân.</li><li class="text-secondary-v2 text-base">VAT</li><li class="text-secondary-v2 text-base">Tip cho lái xe và HDV</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (12, 'Quy định phụ thu:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em từ 11 tuổi trở lên: tính 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Trẻ em từ 05 – 11 tuổi: tính 75% giá trị tour – Ngủ chung với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em dưới 05 tuổi: miễn phí (Ăn ngủ cùng bố mẹ, ngồi lòng bố hoặc mẹ), 2 người lớn miễn phí 1 trẻ em, trẻ thứ 2 tính 50% giá trị tour.</li><li class="text-secondary-v2 text-base">Lễ tết phụ thu 150.000 vnđ/ người</li><li class="text-secondary-v2 text-base">Phòng riêng: Tùy thuộc vào hạng bungalow hiện có của homestay.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (12, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (13, 'Dịch vụ bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô du lịch đời mới đưa đón suốt hành trình.</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">Nghỉ trưa tại nhà sàn cộng đồng.</li><li class="text-secondary-v2 text-base">Ăn uống theo chương trình (1 Bữa chính 250.000đ/ người/ bữa).</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến 30.000.000đ/ người/ vụ.</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/ người/ ngày.</li><li class="text-secondary-v2 text-base">Vé thăm quan theo hành trình, sử dụng bể sục Onsen nóng/lạnh đa dạng tại resort</li><li class="text-secondary-v2 text-base">Chi phí tổ chức Teambuiding, MC hoạt náo viên, đạo cụ trò chơi…</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (13, 'Không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chi phí cá nhân.</li><li class="text-secondary-v2 text-base">Tiền Tip cho lái xe,HDV</li><li class="text-secondary-v2 text-base">VAT</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (14, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">7 đêm nghỉ trên tàu với phòng tiện nghi, giường đôi hoặc giường đơn, phòng tắm riêng, điều hòa không khí và các tiện nghi cao cấp.</li><li class="text-secondary-v2 text-base">Bữa sáng, trưa và tối được chế biến mới mỗi ngày, kết hợp ẩm thực Việt Nam, châu Á và phương Tây.</li><li class="text-secondary-v2 text-base">05 vòng chơi golf (18 lỗ/vòng) tại các sân golf hàng đầu Việt Nam, bao gồm:</li><li class="text-secondary-v2 text-base">+ Ninh Bình  Royal Golf Club</li><li class="text-secondary-v2 text-base">+ Quảng Bình  FLC Quảng Bình Golf Club</li><li class="text-secondary-v2 text-base">+ Đà Nẵng  Montgomerie Links Golf Club</li><li class="text-secondary-v2 text-base">+ Nha Trang  Vinpearl Golf Club</li><li class="text-secondary-v2 text-base">+ Mũi Né  Sea Links Golf Country Club</li><li class="text-secondary-v2 text-base">Phí sân golf và xe điện (2 người/xe).</li><li class="text-secondary-v2 text-base">Miễn phí đồ uống mềm, bia, rượu vang dùng trong nhà, trà và cà phê tại quầy bar hàng ngày.</li><li class="text-secondary-v2 text-base">Dọn dẹp buổi tối và chăm sóc phòng hàng ngày.</li><li class="text-secondary-v2 text-base">Đội ngũ quản lý tour chuyên nghiệp.</li><li class="text-secondary-v2 text-base">Xe đưa đón riêng tại điểm khởi hành và kết thúc hành trình (trong bán kính 30km từ ga tàu).</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (14, 'Giá tour không bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe đưa đón riêng hoặc các dịch vụ xe đưa đón khác không có trong chương trình.</li><li class="text-secondary-v2 text-base">Các đồ uống không dùng trong bữa ăn hoặc khi không ở trên tàu.</li><li class="text-secondary-v2 text-base">Chi phí cá nhân.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (15, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">VAT 10% </li><li class="text-secondary-v2 text-base">Chưa gồm món ăn quà: Nem rán Tạm Thương, Nộm Bò Khô, Hoa Quả dầm.</li><li class="text-secondary-v2 text-base">Chi phí cá nhân, nước uống trong bữa ăn, các chi phí không nằm trong mục bao gồm</li><li class="text-secondary-v2 text-base">Tip cho lái xe và HDV</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (15, 'Phụ thu trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 6 tuổi, ăn và ngủ chung giường với bố mẹ, tự thanh toán chi phí phát sinh của bé.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 -10 tuổi, tính 75% chi phí người lớn. </li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (17, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe 16c, 29c đưa đón theo lịch trình, lái xe chuyên tuyến.</li><li class="text-secondary-v2 text-base">HDV chuyên nghiệp, phục vụ suốt tuyến, nhiệt tình, vui vẻ.</li><li class="text-secondary-v2 text-base">Photo tour phục vụ đoàn</li><li class="text-secondary-v2 text-base">Ăn 1 bữa trưa tại Ba Vì tiêu chuẩn 150.000/người</li><li class="text-secondary-v2 text-base">Vé tham quan theo lịch trình</li><li class="text-secondary-v2 text-base">Nước uống 1 chai/người</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch trọn tour 30.000.000đ/trường hợp.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (17, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phí VAT</li><li class="text-secondary-v2 text-base">Các chi phí cá nhân khác ngoài chương trình</li></ul>');
