INSERT INTO tour_terms (tour_id, section_title, content) VALUES (28, 'Giá tour không bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé cáp treo Tây Thiên</li><li class="text-secondary-v2 text-base">Chi phí cá nhân phát sinh ngoài chương trình</li><li class="text-secondary-v2 text-base">Đồ uống trong bữa ăn.</li><li class="text-secondary-v2 text-base">Tip cho HDV và tài xế (không bắt buộc)</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (28, 'Giá tour trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 5 tuổi miễn phí (ăn và ngồi chung ghế với bố mẹ). Bố mẹ tự thanh toán chi phí phát sinh của bé. Hai người lớn kèm 1 trẻ em, trẻ em thứ 2 tính 50% giá tour.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 – 10 tuổi tính 75% giá tour (ăn suất riêng và ngồi riêng);</li><li class="text-secondary-v2 text-base">Trẻ em từ 11 tuổi trở lên tính như người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (28, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy sau khi đặt dịch vụ: phí hủy 30% giá tour</li><li class="text-secondary-v2 text-base">Hủy từ 10 - 5 ngày trước ngày khởi hành: phí hủy 50% giá tour</li><li class="text-secondary-v2 text-base">Hủy từ 5 - 3 ngày trước ngày khởi hành: phí hủy 75% giá tour</li><li class="text-secondary-v2 text-base">Hủy trong vòng 3 ngày trước ngày khởi hành: phí hủy 100% giá tour.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (29, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe đời mới đưa đón theo lịch trình</li><li class="text-secondary-v2 text-base">Hướng dẫn viên nhiệt tình, kinh nghiệm suốt tuyến</li><li class="text-secondary-v2 text-base">Phòng nghỉ tại Flamingo Đại Lải: 2 khách/phòng, lẻ khách ngủ 3</li><li class="text-secondary-v2 text-base">Bữa ăn trong chương trình:</li><li class="text-secondary-v2 text-base">Ănsáng: buffet theo tiêu chuẩn phòng</li><li class="text-secondary-v2 text-base">1 bữa trưa 200.000 vnđ/ khách ( nhà hàng bên ngoài)</li><li class="text-secondary-v2 text-base">01 bữa tối gala suất ăn: 450.000 vnđ/ khách</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/ khách/ ngày</li><li class="text-secondary-v2 text-base">Chi phí tổ chức gala dinner: Phòng gala, âm thanh ánh sáng, backdrop, MC, dụng cụ trò chơi</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch trọn tour mức cao nhất 30,000,000đ/ 1 vụ</li><li class="text-secondary-v2 text-base">Cácdịch vụ ưu đãi chung theo quy định của Resort đối với khách lưu trú như sau:</li><li class="text-secondary-v2 text-base">Miễn phí qua cổng Resort</li><li class="text-secondary-v2 text-base">Bữa sáng tại Nhà hàng của Resort</li><li class="text-secondary-v2 text-base">Trà, cà phê, nước suối, bánh Flamingo hoặc hoa quả tươi theo tiêu chuẩn tại phòng</li><li class="text-secondary-v2 text-base">Internet &amp;amp; Tivi truyền hình cáp</li><li class="text-secondary-v2 text-base">Tham quan các tác phẩm nghệ thuật Art in the Forest - Không gian nghệ thuật đương đại tại Resort</li><li class="text-secondary-v2 text-base">Vui chơi không giới hạn tại công viên Wonder Park</li><li class="text-secondary-v2 text-base">Dịch vụ xe điện 24/7 trong suốt thời gian lưu trú</li><li class="text-secondary-v2 text-base">Miễn phí sử dụng xe đạp theo tiêu chuẩn dành cho khách lưu trú tại Resort</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (29, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Đồ uống trong các bữa ăn</li><li class="text-secondary-v2 text-base">Tip cho HDV và lái xe</li><li class="text-secondary-v2 text-base">Các chi phí cá nhân khác</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (29, 'Quy định phụ thu trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 2 tuổi miễn phí tour</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 – 6 tuổi phụ thu 220,000 vnđ/ bé ( chi phí ăn sáng theo quy định của resort)</li><li class="text-secondary-v2 text-base">Trẻ em từ 6 – 10 tuổi 75% giá tour người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (30, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe đời mới đưa đón theo lịch trình</li><li class="text-secondary-v2 text-base">Hướng dẫn viên nhiệt tình, kinh nghiệm suốt tuyến</li><li class="text-secondary-v2 text-base">Bữa ăn trưa theo chương trình 350.000/ khách.</li><li class="text-secondary-v2 text-base">Chi phí tổ chức TeamBuilding: âm thanh, sân bãi, MC, dụng cụ trò chơi.</li><li class="text-secondary-v2 text-base">Vé thăm quan theo lịch trình 01 khách/ lượt</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/ khách/ ngày</li><li class="text-secondary-v2 text-base">Phototour đi cùng chụp hình.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch trọn tour mức cao nhất 20,000,000đ/ 1 vụ</li><li class="text-secondary-v2 text-base">Vé vào cửa thăm quan Flamingo Đại Lải 01 khách/ lượt, gồm các dịch vu:</li><li class="text-secondary-v2 text-base">+Miễn phí sử dụng bể bơi chung của Resort (tùy thuộc điều kiện thời tiết và công suất phòng, Resort sẽ vận hành bể bơi bốn mùa)</li><li class="text-secondary-v2 text-base">+Miễn phí sử dụng dịch vụ xe bus điện di chuyển, thăm quan trong Resort (xe hoạt động theo trục đường chính, không phục vụ riêng theo đoàn. Trường hợp khách đoàn có nhu cầu sử dụng xe điện, áp dụng theo bảng giá quy định của Resort)</li><li class="text-secondary-v2 text-base">+Tự do thăm quan Resort (khu vườn treo, vườn hồng, các tác phẩm nghệ thuật, Container phòng tranh, khu vực rừng thông, ...)</li><li class="text-secondary-v2 text-base">+Miễn phí vui chơi tại công viên Wonder Park tại rừng thông.</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (30, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chi phí cá nhân phát sinh ngoài chương trình.</li><li class="text-secondary-v2 text-base">Tiền Tip HDV/ Lái xe nếu có</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (30, 'Giá tour dành cho trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em từ 0-3 tuổi: Miễn phí, ăn ngủ chung với bố mẹ. (Hai người lớn chỉ được kèm 01 trẻ em. Từ trẻ em thứ 2 phụ thu 50% người lớn)</li><li class="text-secondary-v2 text-base">Trẻ em từ trên 03 - 08 tuổi: phụ thu 50% tour</li><li class="text-secondary-v2 text-base">Trẻ trên 08 - 11 tuổi: phụ thu 75%</li><li class="text-secondary-v2 text-base">Trẻ em trên 11 tuổi: Tính như người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (31, 'Dịch vụ bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô du lịch đời mới đưa đón suốt hành trình.</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến 30.000.000đ/ người/ vụ.</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/ người/ ngày.</li><li class="text-secondary-v2 text-base">Vé thăm quan theo hành trình, bể bơi đa dạng, dịch vụ chung tại resort</li><li class="text-secondary-v2 text-base">Bao gồm vé vào cửa 1 lần và 1 set lẩu trăm món ở NH Poem hoặc 1 set Dimsum tại NH Huo Lie theo tiêu chuẩn nhà hàng</li><li class="text-secondary-v2 text-base">Chi phí tổ chức Teambuiding, MC hoạt náo viên, đạo cụ trò chơi…</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (31, 'Không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chi phí cá nhân.</li><li class="text-secondary-v2 text-base">TiềnTip cho lái xe,HDV</li><li class="text-secondary-v2 text-base">VAT</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (33, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Nếu hủy hoặc thay đổi khách hàng báo trước 10 ngày. (Không giải quyết quá 02 lần thay đổi).\xa0</li><li class="text-secondary-v2 text-base">Báo thay đổi từ 10 ngày – 7 ngày trước ngày khởi hành: Bồi thường 25% Tour\xa0</li><li class="text-secondary-v2 text-base">Báo thay đổi từ 7 ngày – 5 ngày trước ngày khởi hành: Bồi thường 50% Tour\xa0</li><li class="text-secondary-v2 text-base">Báo thay đổi từ 5 ngày – 3 ngày trước ngày khởi hành: Bồi thường 75% Tour\xa0</li><li class="text-secondary-v2 text-base">Báo thay đổi sau thời gian trên: Phạt 100% Tour</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (36, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô vận chuyển đời mới đưa đón đoàn thăm quan theo chương trình.</li><li class="text-secondary-v2 text-base">Ăn 01 bữa trưa tại nhà hàng theo chương trình tour.</li><li class="text-secondary-v2 text-base">Vé xe điện + vé thuyền thăm quan tại Chùa Tam Chúc.</li><li class="text-secondary-v2 text-base">Vé thắng cảnh các điểm thăm quan theo chương trình (vào cửa 01 lượt – nếu có).</li><li class="text-secondary-v2 text-base">Hướng dẫn viên chuyên nghiệp, thân thiện và nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức trách nhiệm bồi thường tối đa: 30.000.000 VNĐ/vụ.</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (36, 'Giá tour không bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Đồ uống trong bữa ăn và các chi tiêu cá nhân không được đề cập trong phần bao gồm.</li><li class="text-secondary-v2 text-base">Tiền bồi dưỡng HDV và lái xe.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (36, 'Giá tour trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 5 tuổi miễn phí (ăn và ngồi chung ghế với bố mẹ). Bố mẹ tự thanh toán chi phí phát sinh của bé. Hai người lớn kèm 1 trẻ em, trẻ em thứ 2 tính 50% giá tour.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 – 10 tuổi tính 75% giá tour (ăn suất riêng và ngồi riêng);</li><li class="text-secondary-v2 text-base">Trẻ em từ 11 tuổi trở lên tính như người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (36, 'Ghi chú', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Lịch trình trên có thể thay đổi cho phù hợp với tình hình thực tế chuyến thăm quan của đoàn, hướng dẫn viên có quyền thay đổi thứ tự các điểm thăm quan nhưng vẫn đảm bảo đủ các điểm thăm quan theo chương trình tour.</li><li class="text-secondary-v2 text-base">Quý khách là người ăn chay vui lòng mang thêm đồ ăn chay theo để đảm bảo khẩu vị của mình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (37, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">QUYĐỊNH HOÀN HỦY</li></ul>');
