INSERT INTO tour_terms (tour_id, section_title, content) VALUES (41, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (42, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe du lịch đời mới vận chuyển theo chương trình, máy lạnh, sạch sẽ </li><li class="text-secondary-v2 text-base">Hướng dẫn viên tiếng Việt suốt tuyến, kinh nghiệm, nhiệt tình;</li><li class="text-secondary-v2 text-base">Nghỉ đêm tại phòng riêng khách sạn 3 sao tiêu chuẩn 02 người/phòng, lẻ người ngủ 3. </li><li class="text-secondary-v2 text-base">03 bữa chính suất 150.000 vnđ/ người,1 bữa sáng tại khách sạn.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức đền bù tối đa 20.000.000 đồng/người/vụ.</li><li class="text-secondary-v2 text-base">Vé thăm quan các điểm trong chương trình.</li><li class="text-secondary-v2 text-base">Đò thăm quan Tam Cốc</li><li class="text-secondary-v2 text-base">Nước uống 1 chai 500ml/ khách/ ngày</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
