INSERT INTO tour_terms (tour_id, section_title, content) VALUES (44, 'Tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé máy bay khứ hồi Bamboo</li><li class="text-secondary-v2 text-base">20kg ký gửi + 7kg hành lý xách tay.</li><li class="text-secondary-v2 text-base">Suất ăn nhẹ trên máy bay</li><li class="text-secondary-v2 text-base">Xe đón tiễn sân bay Nội Bài – khách sạn.</li><li class="text-secondary-v2 text-base">02 đêm tại ks khu vực phố cổ HN</li><li class="text-secondary-v2 text-base">Vé thắng cảnh các tuyến điểm thăm quan theo chương trình.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch trọn gói mức đền bù tối đa 10 triệu đồng.</li><li class="text-secondary-v2 text-base">Phục vụ nước uống hoặc đồ ăn nhẹ và wifi miễn phí trên xe.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (44, 'Không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Thuế VAT 10%</li><li class="text-secondary-v2 text-base">Vé xe điện trung chuyển vào thăm quan chùa Bái Đính</li><li class="text-secondary-v2 text-base">Típ cho nhân viên chèo đò, hướng dẫn, lái xe, nhân viên phục vụ,…</li><li class="text-secondary-v2 text-base">Đồ uống trong bữa ăn, mua sắm chi tiêu cá nhân ngoài chương trình</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (44, 'Chính sách trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Em bé em từ 1 - 5 tuổi miễn phí dịch vụ ( ăn cùng bố mẹ, nếu mất vé bố mẹ tự chi ).Vé máy bay tính theo quy định của hãng.</li><li class="text-secondary-v2 text-base">Trẻ em từ 6 - 9 tuổi tính 75% ( bao gồm vé thắng cảnh, suất ăn riêng, ngủ cùng bố mẹ ).Vé máy bay tính theo quy định của hãng.</li><li class="text-secondary-v2 text-base">Trẻ em từ 10 tuổi trở lên tính 100% giá tour người lớn ( hưởng dịch vụ đầy đủ như người lớn )</li><li class="text-secondary-v2 text-base">Haingười lớn được kèm 01 trẻ em dưới 5 tuổi, nếu có 2 trẻ em dưới 5 tuổi tính 50% phí dịch vụ.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (44, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Đặt chỗ 10 ngày trước ngày khởi hành. Nếu hủy, báo trước 07 ngày.</li><li class="text-secondary-v2 text-base">Báo hủy từ 7- 5 ngày trước ngày khởi hành: Phạt 50% Tour.</li><li class="text-secondary-v2 text-base">Báo hủy 3 ngày trước ngày khởi hành: Phạt 75% Tour</li><li class="text-secondary-v2 text-base">Báo hủy sau thời gian trên phạt 100% Tour.</li><li class="text-secondary-v2 text-base">Vé thắng cảnh tại Tràng An quy định trẻ em có chiều cao từ 1,0 m trở lên phải mua vé như người lớn, em bé cao dưới 1,0 m được miễn giảm 50% tiền vé thăm quan</li><li class="text-secondary-v2 text-base">Vé thắng cảnh tại Tam Cốc quy định trẻ em có chiều cao từ 1,4m trở lên phải mua vé như người lớn, em bé cao dưới 1,4m được miễn giảm 50% tiền vé thăm quan</li><li class="text-secondary-v2 text-base">Đối với khách Việt Nam: Quy định chở 4 khách/chuyến đò, đối với khách nước ngoài: Quy định chở 2 khách/chuyến đò</li><li class="text-secondary-v2 text-base">Quý khách có nhu cầu mua sắm, chụp ảnh,..nên hỏi trước giá cả hoặc tham khảo thông tin từ hướng dẫn viên để được tư vấn tránh bị chặt chém hoặc mua phải hàng kém chất lượng</li><li class="text-secondary-v2 text-base">Nếu Quý khách có chế độ ăn kiêng, ăn chay hoặc Quý khách có chuyến bay cùng ngày vui lòng báo trước để được nhân viên tư vấn và sắp xếp thời gian hợp lý.</li><li class="text-secondary-v2 text-base">Quý khách vui lòng cung cấp đầy đủ thông tin họ và tên, năm sinh, giới tính, Quốc tịch trước ngày khởi hành để được mua bảo hiểm du lịch ( nếu không có đầy đủ thông tin công ty không thể mua bảo hiểm )</li><li class="text-secondary-v2 text-base">Quý khách nên mang theo trang phục nhẹ nhàng, mặc quần dài hoặc váy dài qua đầu gối, đi giầy thể thao, mang theo ô dù, mũ nón,..</li><li class="text-secondary-v2 text-base">Trước khi Quý khách xuống tàu, xe, nên kiểm tra thật kỹ hành lý tư trang cá nhân tại khu vực chỗ ngồi của mình. Công ty không chịu trách nhiệm bồi hoàn nếu Quý khách bị mất hoặc thất lạc</li><li class="text-secondary-v2 text-base">Lịch trình và thứ tự các điểm thăm quan có thể thay đổi cho phù hợp với tình hình thực tế thời tiết nhưng vẫn đảm bảo đầy đủ các điểm thăm quan cho Quý khách</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (45, 'Tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe du lịch 16 - 29 chỗ, đưa đón theo chương trình</li><li class="text-secondary-v2 text-base">Hướng dẫn viên thuyết minh song ngữ Anh - Việt</li><li class="text-secondary-v2 text-base">Khách sạn / Bungalow tiêu chuẩn (2 - 3 khách/phòng)</li><li class="text-secondary-v2 text-base">Vé thắng cảnh, vé đò thăm quan Tràng An và Tam Cốc</li><li class="text-secondary-v2 text-base">Ăn chính 03 bữa, ăn sáng 02 bữa theo chương trình</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch trọng gói mức đền bù tối đa 10 triệu đồng</li><li class="text-secondary-v2 text-base">ƠNước uống Dasani 01 chai 350ml/khách phục vụ trên xe</li></ul>');
