INSERT INTO tour_terms (tour_id, section_title, content) VALUES (56, 'Không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Thuế VAT</li><li class="text-secondary-v2 text-base">Chi phí cá nhân phát sinh ngoài chương trình</li><li class="text-secondary-v2 text-base">Đồ uống trong bữa ăn, nếu không bao gồm đồ uống trong mục Giá bao gồm.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (56, 'Phụ thu trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 5 tuổi miễn phí (ăn và ngủ chung với bố mẹ). Bố mẹ tự thanh toán các chi phí phát sinh của bé. Hai người lớn chỉ được kèm 1 trẻ em miễn phí. Trẻ em thứ 2 tính 50% giá tour.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 - 10 tuổi tính 75% giá tour người lớn</li><li class="text-secondary-v2 text-base">Trẻ em từ 11 tuổi trở lên bằng người lớn.</li></ul>');
