INSERT INTO tour_terms (tour_id, section_title, content) VALUES (62, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé máy bay khứ hồi HCM – Hà Nội có 7 ký hành lý xách tay.</li><li class="text-secondary-v2 text-base">Có 1-2 đêm ngủ tại khách sạn ở Hà Nội</li><li class="text-secondary-v2 text-base">Xe đón tiễn khứ hồi từ sân bay Nội Bài về khách sạn ngay khu phố cổ.</li><li class="text-secondary-v2 text-base">Xe ôtô du lịch đảm bảo chất lượng, lái xe có kinh nghiệm vùng cao.</li><li class="text-secondary-v2 text-base">Nhà nghỉ khép kín hoặc homestay nhà sàn tập thể ở trung tâm Y Tý, có nóng lạnh. </li><li class="text-secondary-v2 text-base">Các bữa ăn tại các nhà hàng: 3 bữa chính (2 bữa 130,000 vnđ/khách, 1 bữa 180,000 vnđ/khách); 40.000VNĐ/bữa phụ.</li><li class="text-secondary-v2 text-base">Vé tham quan du lịch.</li><li class="text-secondary-v2 text-base">Dẫn đoàn am hiểu, nhiệt tình, chu đáo.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch.</li><li class="text-secondary-v2 text-base">Nước uống 500ml/người/ngày.</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (62, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Tiền tip cho lái xe, HDV </li><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (62, 'Quy định phụ thu:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 400.000đ/ khách/ 1 đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình), 400.000đ/khách/1 đêm ở Hà Nội.</li><li class="text-secondary-v2 text-base">Phụ thu chi phí giấy thông hành người nước ngoài 250.000VNĐ/khách</li><li class="text-secondary-v2 text-base">Phụ thu HDV tiếng Anh 10$/ngày.</li></ul>');
