INSERT INTO tour_terms (tour_id, section_title, content) VALUES (81, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé máy bay không hoàn , không hủy </li><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (82, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé máy bay khứ hồi HCM – Hà Nội có 7 ký hành lý xách tay.</li><li class="text-secondary-v2 text-base">Có 1-2 đêm ngủ tại khách sạn ở Hà Nội</li><li class="text-secondary-v2 text-base">Xe đón tiễn khứ hồi từ sân bay Nội Bài về khách sạn ngay khu phố cổ.</li><li class="text-secondary-v2 text-base">Xe ôtô du lịch đảm bảo chất lượng, lái xe có kinh nghiệm vùng cao.</li><li class="text-secondary-v2 text-base">Nhà nghỉ khép kín hoặc homestay nhà sàn tập thể ở trung tâm Y Tý, có nóng lạnh. </li><li class="text-secondary-v2 text-base">Các bữa ăn tại các nhà hàng: 3 bữa chính (2 bữa 130,000 vnđ/khách, 1 bữa 180,000 vnđ/khách); 40.000VNĐ/bữa phụ.</li><li class="text-secondary-v2 text-base">Vé tham quan du lịch.</li><li class="text-secondary-v2 text-base">Dẫn đoàn am hiểu, nhiệt tình, chu đáo.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch.</li><li class="text-secondary-v2 text-base">Nước uống 500ml/người/ngày.</li><li class="text-secondary-v2 text-base">Thuế VAT</li></ul>');
