INSERT INTO tour_terms (tour_id, section_title, content) VALUES (90, 'Chính sách\xa0trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 2 tuổi, phụ thu 10%, ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 tuổi – dưới 5 tuổi phụ thu 60% , hai người lớn chỉ được kèm 1 trẻ em, nếu trẻ em đi kèm nhiều hơn thì từ em thứ 02 trở lên phụ thu 90% giá tour. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 - dưới 11 tuổi phụ thu 90%. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (90, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Riêng vé máy bay thì không hoàn, không hủy theo quy định hãng hàng không.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (91, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ôtô du lịch đảm bảo chất lượng, lái xe có kinh nghiệm vùng cao.</li><li class="text-secondary-v2 text-base">Nhà nghỉ /Home stay/Khách sạn tiêu chuẩn ở Y Tý và Bắc Hà .</li><li class="text-secondary-v2 text-base">Các bữa ăn tại các nhà hàng: 05 bữa 150.000đ/bữa ; 02 bữa sáng:40.000đ/bữa.</li><li class="text-secondary-v2 text-base">Vé tham quan du lịch.</li><li class="text-secondary-v2 text-base">Hướng dẫn viên dẫn đoàn am hiểu, nhiệt tình, chu đáo.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch.</li><li class="text-secondary-v2 text-base">Nước uống 500ml/người/ngày.</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (91, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Tiền tip cho lái xe, HDV.</li><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác.</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li><li class="text-secondary-v2 text-base">Các chi phí quà tặng thiện nguyện.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (91, 'Quy định phụ thu:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 400.000đ/ khách/ đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình).</li><li class="text-secondary-v2 text-base">Phụ thu chi phí giấy thông hành người nước ngoài 250.000VNĐ/khách.</li><li class="text-secondary-v2 text-base">Phụ thu HDV tiếng Anh 10$/ngày.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (91, 'Chính sách trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 5 tuổi miễn phí (ăn uống và ngủ cùng với bố mẹ, bố mẹ tự túc lo cho bé). Hai người lớn chỉ kèm 1 trẻ em, trẻ em thứ 2 trở đi tính giá 50% giá tour người lớn.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5- dưới 11 tuổi giá tour là 75% giá tour người lớn. (Tiêu chuẩn: 01 suất ăn + 01 ghế ngồi và ngủ ghép cùng giường với bố mẹ). Hai người lớn chỉ kèm 1 trẻ em , trẻ em thứ 2 trở đi tính giá tour như người lớn.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (91, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (92, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vận chuyển: xe du lịch đời mới, đưa đón theo chương trình. Lái xe thân thiện và cẩn thận suốt hành trình.</li><li class="text-secondary-v2 text-base">05 bữa chính theo chương trình, tiêu chuẩn 150.000đ/ suất.</li><li class="text-secondary-v2 text-base">02 bữa sáng 35,000đ/suất.</li><li class="text-secondary-v2 text-base">Hướng dẫn viên chuyên nghiệp, chu đáo suốt chương trình</li><li class="text-secondary-v2 text-base">Vé thắng cảnh các điểm trong trong chương trình</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch trọn tour, mức bồi thường tối đa 20.000.000 đồng/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống trên xe: 1 chai 0,5 lit/ người/ ngày.</li><li class="text-secondary-v2 text-base">Nghỉ nhà sàn hoặc phòng riêng Homestay tại Hồ Thầu và tại Bắc Hà</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (92, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Đồ uống trong bữa ăn và chi phí cá nhân</li><li class="text-secondary-v2 text-base">Thuế VAT 8 %</li><li class="text-secondary-v2 text-base">Tiền Tip cho lái xe và Hướng dẫn viên</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (92, 'Giá tour trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Dưới 05 tuổi: miễn phí hoàn toàn (bố mẹ tự chăm sóc, phát sinh bố mẹ tự thanh toán).</li><li class="text-secondary-v2 text-base">Từ đủ 05 trở lên: giá tour bằng 75% giá tour người lớn.</li><li class="text-secondary-v2 text-base">Từ 11 tuổi trở lên: Giá tour bằng 100% giá tour người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (92, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy sau khi đặt dịch vụ: phí hủy 30% giá tour</li><li class="text-secondary-v2 text-base">Hủy 10 – 5 ngày trước ngày khởi hành: phí hủy 50% giá tour</li><li class="text-secondary-v2 text-base">Hủy từ 5 -3 ngày trước ngày khởi hành: phí hủy 75% giá tour</li><li class="text-secondary-v2 text-base">Hủy trong vòng 3 ngày trước ngày khởi hành: phí hủy 100% giá tour</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (93, 'Dịch vụ bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé máy bay khứ hồi HCM – HÀ NỘI, 7 kg hành lí\\ý xách tay</li><li class="text-secondary-v2 text-base">Xe cao cấp đưa đón Hà Nội - Sapa – Hà Nội</li><li class="text-secondary-v2 text-base">Khách sạn 3* trung tâm đầy đủ tiện nghi( 2 - 3 khách/phòng )</li><li class="text-secondary-v2 text-base">Ăn 05 bữa chính, ăn sáng 02 bữa thực đơn theo chương trình</li><li class="text-secondary-v2 text-base">Hướng dẫn viên</li><li class="text-secondary-v2 text-base">Vé thắng cảnh các tuyến điểm thăm quan theo chương trình</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch trọn gói mức đền bù tối đa 10 triệu đồng</li><li class="text-secondary-v2 text-base">Phục vụ nước uống trên xe.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (93, 'Dịch vụ không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Thuế VAT 10%</li><li class="text-secondary-v2 text-base">Vé cáp treo Fansipan; Vé tàu hoả leo núi; Vé Cầu Kính Rồng Mây</li><li class="text-secondary-v2 text-base">Típ cho hướng dẫn viên, lái xe, nhân viên phục vụ</li><li class="text-secondary-v2 text-base">Nghỉ phòng đơn, điện thoại, giặt là, đồ uống, thăm quan phát sinh ngoài chương trình</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (93, 'Chính sách trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Em bé từ 1 – dưới 2 tuổi giá tour là 300.000đ/bé (ăn uống và ngủ chung với bố mẹ).</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 – dưới 5 tuổi giá tour là 2.800.000đ/bé hai người lớn chỉ kèm 1 trẻ em, trẻ em thứ 2 trở lên sẽ tính giá tour 4.180.000đ/bé.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 - dưới 11 tuổi giá tour là 4.870.000đ/bé (chỗ ngồi riêng trên xe, ăn riêng, ngủ cùng bố mẹ )</li><li class="text-secondary-v2 text-base">Trẻ em từ 11 tuổi trở lên tính 100% giá tour như người lớn (dịch vụ đặt như người lớn)</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (93, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Quýkhách lưu ý:</li><li class="text-secondary-v2 text-base">Lịchtrình, các điểm thăm quan có thể thay đổi tuỳ thuộc vào tình trạng thời tiết và phương tiện di chuyển nhưng vẫn đảm bảo đầy đủ các điểm thăm quan cho Quý khách.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (94, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé máy bay BAMBOO/Vietjet khứ hồi HCM – HÀ NỘI có 7 kí hành lí xách tay</li><li class="text-secondary-v2 text-base">Xe Ô tô đời mới đưa đón suốt hành trình</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">Khách sạn tiêu chuẩn 3 sao tiện nghi, trung bình 02 khách/phòng</li><li class="text-secondary-v2 text-base">Ăn uống theo chương trình (Bữa chính 150.000đ/ khách/ bữa)</li><li class="text-secondary-v2 text-base">Vé thắng cảnh vào cửa một lần tại các điểm tham quan.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 20.000.000đ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (94, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Thuế VAT 10%</li><li class="text-secondary-v2 text-base">Giá tham quan Fansipan và cầu kính Tình Yêu.</li><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác.</li><li class="text-secondary-v2 text-base">Chi phí mua và hái dâu tại vườn.</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li><li class="text-secondary-v2 text-base">Phụ thu người nước ngoài 10 USD/ 1 người/ 1 ngày.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (94, 'Quy định phụ thu:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 300.000đ/ khách/ đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình)</li><li class="text-secondary-v2 text-base">Trẻ em dưới 2 tuổi, tính phí 300.000đ/bé, ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 tuổi – dưới 5 tuổi giá tour 3.700.000đ/bé hai người lớn chỉ được kèm 1 trẻ em, nếu trẻ em đi kèm nhiều hơn thì từ em thứ 02 trở lên giá tour là 6.090.000đ/bé</li><li class="text-secondary-v2 text-base">Trẻ em từ 5- dưới 11 tuổi giá tour là 6.580.000đ/bé. (Tiêu chuẩn: 01 suất ăn + 01 ghế ngồi và ngủ ghép chung phòng với gia đình + vé máy bay).</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
