INSERT INTO tour_terms (tour_id, section_title, content) VALUES (95, 'Quy định phụ thu', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụthu phòng đơn 300.000đ/ khách/ đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình)</li><li class="text-secondary-v2 text-base">Trẻ em từ 11 tuổi trở lên: tính 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Trẻ em từ 05 – 11 tuổi: tính 75% giá trị tour – Ngủ chung với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em dưới 05 tuổi: miễn phí (Ăn ngủ cùng bố mẹ, ngồi lòng bố hoặc mẹ), 2 người lớn miễn phí 1 trẻ em, trẻ thứ 2 tính 50% giá trị tour.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (95, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (97, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé máy bay khứ hồi HCM – HN có 7 ký hành lý xách tay.</li><li class="text-secondary-v2 text-base">1 đến 2 đêm ngủ tại khách sạn ở Hà Nội</li><li class="text-secondary-v2 text-base">Xe đón tiễn khứ hồi từ sân bay Nội Bài về khách sạn ngay khu phố cổ.</li><li class="text-secondary-v2 text-base">Xe ôtô du lịch đảm bảo chất lượng, lái xe có kinh nghiệm vùng cao.</li><li class="text-secondary-v2 text-base">Nhà nghỉ khép kín hoặc homestay nhà sàn tập thể ở trung tâm Y Tý, có nóng lạnh. </li><li class="text-secondary-v2 text-base">Các bữa ăn tại các nhà hàng: 3 bữa chính (2 bữa 130,000 vnđ/khách, 1 bữa 180,000 vnđ/khách); 40.000VNĐ/bữa phụ.</li><li class="text-secondary-v2 text-base">Vé tham quan du lịch.</li><li class="text-secondary-v2 text-base">Dẫn đoàn am hiểu, nhiệt tình, chu đáo.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch.</li><li class="text-secondary-v2 text-base">Nước uống 500ml/người/ngày.</li><li class="text-secondary-v2 text-base">Thuế VAT</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (97, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Tiền tip cho lái xe, HDV</li><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (97, 'Quy định phụ thu:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 400.000 VNĐ/ khách/ 1 đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình), 400.000 VNĐ/khách/1 đêm ở Hà Nội.</li><li class="text-secondary-v2 text-base">Phụ thu chi phí giấy thông hành người nước ngoài 250.000VNĐ/khách</li><li class="text-secondary-v2 text-base">Phụ thu HDV tiếng Anh 10$/ngày</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (97, 'Quy định phụ thu trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 2 tuổi, phụ thu 10%, ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 tuổi – dưới 5 tuổi phụ thu 60% , hai người lớn chỉ được kèm 1 trẻ em, nếu trẻ em đi kèm nhiều hơn thì từ em thứ 02 trở lên phụ thu 90% giá tour. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 - dưới 11 tuổi phụ thu 90%. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (97, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.\xa0</li><li class="text-secondary-v2 text-base">Quý khách có mặt trước giờ bay 180 phút, hành lý gọn nhẹ khi đi mang theo giấy tờ tùy thân bản chính.</li><li class="text-secondary-v2 text-base">*Lưu ý giấy tờ mang theo:</li><li class="text-secondary-v2 text-base">Hành khách từ 14 tuổi trở lên:</li><li class="text-secondary-v2 text-base">+Khi làm thủ tục đi tàu bay trên các chuyến bay nội địa phải xuất trình một trong những giấy tờ được quy định dưới đây:</li><li class="text-secondary-v2 text-base">+Đối với hành khách mang quốc tịch nước ngoài: hộ chiếu.</li><li class="text-secondary-v2 text-base">+Đối với hành khách mang quốc tịch Việt Nam phải xuất trình một trong các loại giấy tờ sau:</li><li class="text-secondary-v2 text-base">Hộ chiếu</li><li class="text-secondary-v2 text-base">Giấy chứng minh nhân dân</li><li class="text-secondary-v2 text-base">Giấy chứng nhận của các lực lượng vũ trang</li><li class="text-secondary-v2 text-base">Thẻ đại biểu Quốc hội</li><li class="text-secondary-v2 text-base">Thẻ đảng viên</li><li class="text-secondary-v2 text-base">Thẻ nhà báo</li><li class="text-secondary-v2 text-base">Giấy phép lái xe ô tô, mô tô</li><li class="text-secondary-v2 text-base">Thẻ kiểm soát an ninh hàng không</li><li class="text-secondary-v2 text-base">Thẻ nhận dạng của các hãng hàng không Việt Nam</li><li class="text-secondary-v2 text-base">Giấy chứng nhận nhân thân có xác nhận của công an phường, xã nơi cư trú.</li><li class="text-secondary-v2 text-base">Hành khách dưới 14 tuổi:</li><li class="text-secondary-v2 text-base">+Hành khách dưới 14 tuổi khi làm thủ tục đi tàu bay trên các chuyến bay nội địa phải xuất trình các loại giấy tờ sau:</li><li class="text-secondary-v2 text-base">+Đối với hành khách mang quốc tịch nước ngoài: hộ chiếu.</li><li class="text-secondary-v2 text-base">+Đối với hành khách mang quốc tịch Việt Nam phải xuất trình một trong các loại giấy tờ sau:</li><li class="text-secondary-v2 text-base">Hộ chiếu</li><li class="text-secondary-v2 text-base">Giấy khai sinh</li><li class="text-secondary-v2 text-base">Giấy chứng sinh (đối với trường hợp dưới 1 tháng tuổi chưa có giấy khai sinh)</li><li class="text-secondary-v2 text-base">Giấy xác nhận của tổ chức xã hội đối với trẻ em do tổ chức xã hội đang nuôi dưỡng (có giá trị sử dụng trong thời gian 06 tháng kể từ ngày xác nhận</li><li class="text-secondary-v2 text-base">+Trường hợp hành khách dưới 14 tuổi đi tàu bay mà không có người đi kèm ngoài các giấy tờ nêu trên phải có giấy cam kết của người đại diện theo pháp luật: Giấy cam kết của người đại diện theo pháp luật của hành khách dưới 14 tuổi đi tàu bay là giấy cam kết vận chuyển giữa hãng hàng không và người đại diện theo pháp luật của khách đó, không bắt buộc phải có giấy xác nhận của chính quyền địa phương và các cơ quan chức năng.</li><li class="text-secondary-v2 text-base">Các giấy tờ nêu trên phải đảm bảo các điều kiện:</li><li class="text-secondary-v2 text-base">+Còn giá trị sử dụng.</li><li class="text-secondary-v2 text-base">+Có ảnh đóng dấu giáp lai, trừ giấy khai sinh, giấy chứng sinh của trẻ em, thẻ kiểm soát an ninh, thẻ nhận dạng của các hãng hàng không.</li><li class="text-secondary-v2 text-base">+Trẻ em (dưới 12 tuổi) khi đi du lịch mang theo giấy khai sinh (bản chính hoặc sao y có thị thực) để làm thủ tục hàng không. Trường hợp không có cha hoặc mẹ đi cùng phải có giấy ủy quyền của cha mẹ và có xác nhận của chính quyền địa phương.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (98, 'Giá tour\xa0bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô du lịch đảm bảo chất lượng, lái xe có kinh nghiệm vùng cao.</li><li class="text-secondary-v2 text-base">Nhà nghỉ /Homestay/Khách sạn tiêu chuẩn ở Y Tý và Bắc Hà.  </li><li class="text-secondary-v2 text-base">Các bữa ăn tại các nhà hàng: 05 bữa 150.000đ/bữa ; 02 bữa sáng:40.000đ/bữa</li><li class="text-secondary-v2 text-base">Vé tham quan du lịch.</li><li class="text-secondary-v2 text-base">Dẫn đoàn am hiểu, nhiệt tình, chu đáo.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch.</li><li class="text-secondary-v2 text-base">Nước uống 500ml/người/ngày.</li><li class="text-secondary-v2 text-base">Thuế VAT</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (98, 'Giá tour không bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Tiền tip cho lái xe, HDV </li><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (98, 'Chính sách trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 5 tuổi miễn phí (ăn uống và ngủ cùng với bố mẹ, bố mẹ tự túc lo cho bé). Hai người lớn chỉ kèm 1 trẻ em, trẻ em thứ 2 trở đi tính giá 50% giá tour người lớn.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5- dưới 11 tuổi giá tour là 75% giá tour người lớn. (Tiêu chuẩn: 01 suất ăn + 01 ghế ngồi và ngủ ghép cùng giường với bố mẹ). Hai người lớn chỉ kèm 1 trẻ em , trẻ em thứ 2 trở đi tính giá tour như người lớn.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (98, 'Quy định phụ thu', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 400.000 VNĐ/ khách/ đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình).</li><li class="text-secondary-v2 text-base">Phụ thu chi phí giấy thông hành người nước ngoài 250.000VNĐ/khách</li><li class="text-secondary-v2 text-base">Phụ thu HDV tiếng Anh 10$/ngày.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (98, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (99, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô giường nằm Hà Nội - Lào Cai - Hà Nội.</li><li class="text-secondary-v2 text-base">Vận chuyển tại Trung Quốc: Xe ô tô đời mới đi theo lịch trình bên Trung Quốc.</li><li class="text-secondary-v2 text-base">Lưu trú: Khách sạn tại Trung Quốc tiêu chuẩn 4 sao địa phương: Ngủ 02 khách/phòng 2 giường. Nếu đăng ký nhóm 3-5-7 khách lẻ nam/nữ sẽ ngủ ghép phòng hoặc phụ thu phòng đơn.</li><li class="text-secondary-v2 text-base">Các bữa ăn: 02 bữa sáng (Ăn sáng: Bún hoặc mỳ) + 04 bữa chính tiêu chuẩn 40NDT/suất + 01 bữa Bún Qua Cầu</li><li class="text-secondary-v2 text-base">Vé tham quan các điểm xác định trong chương trình: Tượng phật Di Lạc và xe điện 1 lượt, Đông Phong Vận, Chu Gia Hoa Viên</li><li class="text-secondary-v2 text-base">HDV du lịch tiếng Việt/tiếng Trung.</li><li class="text-secondary-v2 text-base">Nước uống trên xe.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch quốc tế.</li><li class="text-secondary-v2 text-base">Thủ tục XNC Việt Nam - Trung Quốc</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (99, 'Giá tour\xa0không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chi phí cá nhân và đồ uống ngoài chương trình</li><li class="text-secondary-v2 text-base">Chi phí tái nhập cảnh đối với khách nước ngoài</li><li class="text-secondary-v2 text-base">Lưu trú: Chi phí ngủ phòng đơn: 900.000VND/khách.</li><li class="text-secondary-v2 text-base">Kiểm dịch y tế.</li><li class="text-secondary-v2 text-base">Tiền TIP cho tài xế và HDV địa phương: 5 USD/ khách/ ngày x 3 ngày = 15 USD</li><li class="text-secondary-v2 text-base">01 bữa tối ngày 2 không bao gồm trong chương trình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (99, 'Chính sách giá trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 2 tuổi tính 30% giá người lớn, ngủ chung giường với bố mẹ</li><li class="text-secondary-v2 text-base">Trẻ em 2 – 11 tuổi tính 90% giá người lớn, ăn suất riêng, ngủ chung giường với bố mẹ. Ngủ riêng giường thanh toán 100% giá người lớn.</li><li class="text-secondary-v2 text-base">Trẻ em 12 tuổi trở lên tính như người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (99, 'Chính sách chung đối với khách lẻ ghép đoàn:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Quý khách đặt cọc 2 triệu ngay khi đăng ký tour. Thanh toán số tiền còn lại trước ngày khởi hành 10 ngày.</li><li class="text-secondary-v2 text-base">Do tính chất đoàn khách lẻ, nên lịch khởi hành có thể thay đổi sang ngày khởi hành mới sau đó nếu:</li><li class="text-secondary-v2 text-base">a.Trước ngày khởi hành 7 ngày mà đoàn không đủ số lượng khách từ 20 người lớn trở lên.</li><li class="text-secondary-v2 text-base">b.Thời gian dự kiến ngày khởi hành gặp các nguyên nhân bất khả kháng như thiên tai, bão lụt, chiến tranh….</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (99, 'Thủ tục thông hành:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Thủtục thông hành bao gồm:</li><li class="text-secondary-v2 text-base">CCCD scan</li><li class="text-secondary-v2 text-base">01 ảnh 4 x6 chụp trên nền phông mầu trắng để xin cấp thông hành.</li><li class="text-secondary-v2 text-base">Quyđịnh về ảnh:</li><li class="text-secondary-v2 text-base">Ảnh chụp thẳng, để lộ rõ khuôn mặt, lộ trán, vành tai.</li><li class="text-secondary-v2 text-base">Không mặc áo màu trắng, không đeo kính</li><li class="text-secondary-v2 text-base">Thông tin các nước đã đi trong vòng 3 năm và thời gian lưu trú tại nước đó</li><li class="text-secondary-v2 text-base">Thủ tục thông hành hoàn thiện trước 5 ngày làm việc.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (99, 'Lưu ý:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Lịch trình có thể thay đổi để phù hợp với chương trình thực tế khi đi tham quan nhưng vẫn đẩy đủ các điểm.</li><li class="text-secondary-v2 text-base">Giá áp dụng cho khách hàng từ 10 tuổi đến 69 tuổi, từ 70 tuổi trở lên sẽ đóng thêm chênh lệch cho mức phí bảo hiểm cao cấp, từ 75 tuổi trở lên yêu cầu phải có giấy chứng nhận đầy đủ sức khỏe để đi du lịch nước ngoài của cơ quan y tế có thẩm quyền cấp và phải có người thân khỏe mạnh dưới 60 tuổi đi cùng</li><li class="text-secondary-v2 text-base">Các dịch vụ được đặt và thanh toán trước nên nếu các thành viên tách đoàn hoặc không sử dụng dịch vụ sẽ không được hoàn lại.</li><li class="text-secondary-v2 text-base">Mọi sự thay đổi phải được thống nhất với trưởng đoàn, chúng tôi không đáp ứng các thay đổi của cá nhân.</li><li class="text-secondary-v2 text-base">Nếu các thay đổi làm ảnh hưởng tới lịch trình, dịch vụ (bỏ điểm tham quan, bỏ dịch vụ…) quý khách phải chấp nhận các phát sinh đó.Các thay đổi phải dựa trên tính khả thi và tình hình thực tế.</li><li class="text-secondary-v2 text-base">Trong hành trình, Quý Khách không được tự động nhận thêm người nhà hoặc người quen lên xe mà không có sự thỏa thuận dịch vụ với chúng tôi trước đó – chúng tôi có quyền từ chối cung cấp dịch vụ cho các trường hợp trên.</li><li class="text-secondary-v2 text-base">Quy định chung của khách sạn, nhận phòng 12:00 – 14:00, trả phòng trước 12:00.</li><li class="text-secondary-v2 text-base">Hành trình tham quan thường là không có thời gian nghỉ trưa và thường phải đi bộ nhiều.</li><li class="text-secondary-v2 text-base">Chúng tôi không chịu trách nhiệm bất cứ sự chậm trễ nào từ phía Quý khách.</li><li class="text-secondary-v2 text-base">Nếu quý khách có nhu cầu tách đoàn vui lòng thông báo cho nhân viên bán tour tại thời điểm đăng ký tour</li><li class="text-secondary-v2 text-base">Trường hợp Quý khách không được xuất cảnh và nhập cảnh vì lý do cá nhân, công ty sẽ không chịu trách nhiệm và sẽ không hoàn trả tiền tour.</li><li class="text-secondary-v2 text-base">Khách dưới 14 tuổi cần có người giám hộ và có ủy quyền của người thân cho người giám hộ có dấu xác nhận của phường.</li><li class="text-secondary-v2 text-base">Khách đã phẫu thuật thẩm mỹ, nếu quá khác so với ảnh trên hộ chiếu, vui lòng làm lại hộ chiếu – công ty du lịch sẽ không chịu trách nhiệm nếu khách không được xuất cảnh khỏi Việt Nam – hoặc không được nhập cảnh vào nước sở tại.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (100, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vận chuyển: xe du lịch đời mới, đưa đón theo chương trình. Lái xe thân thiện và cẩn thận suốt hành trình.</li><li class="text-secondary-v2 text-base">05 bữa chính theo chương trình, tiêu chuẩn 150.000đ/ suất.</li><li class="text-secondary-v2 text-base">02 bữa sáng 35,000đ/suất.</li><li class="text-secondary-v2 text-base">Vé máy bay khứ hồi HCM – HN có 7 kí xách tay</li><li class="text-secondary-v2 text-base">Hướng dẫn viên chuyên nghiệp, chu đáo suốt chương trình</li><li class="text-secondary-v2 text-base">Vé thắng cảnh các điểm trong trong chương trình</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch trọn tour, mức bồi thường tối đa 20.000.000 đồng/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống trên xe: 1 chai 0,5 lit/ người/ ngày.</li><li class="text-secondary-v2 text-base">Nghỉ nhà sàn hoặc phòng riêng Homestay tại Hồ Thầu và tại Bắc Hà</li><li class="text-secondary-v2 text-base">Xe đón tiễn khứ hồi từ sân bay Nội Bài về khách sạn</li><li class="text-secondary-v2 text-base">1- 2 đêm khách sạn tại HN ( tùy thuộc vào ngày khởi hành của chương trình)</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (100, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Đồ uống trong bữa ăn và chi phí cá nhân</li><li class="text-secondary-v2 text-base">Thuế VAT</li><li class="text-secondary-v2 text-base">Tiền Tip cho lái xe và Hướng dẫn viên</li><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 250.000vnđ/ khách/đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình), 650.000đ/khách/2 đêm ở Hà Nội.</li><li class="text-secondary-v2 text-base">Phụ thu 250.000 vnđ/khách khi tham gia tour vào dịp lễ, tết</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (100, 'Chính sách trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 2 tuổi, tính phí 300.000đ/bé, ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 tuổi – dưới 5 tuổi giá tour bằng 60% giá tour người lớn/bé, hai người lớn chỉ được kèm 1 trẻ em, nếu trẻ em đi kèm nhiều hơn thì từ em thứ 02 trở lên giá tour như giá trẻ em 5 - dưới 11 tuổi</li><li class="text-secondary-v2 text-base">Trẻ em từ 5- dưới 11 tuổi giá tour bằng 90% giá tour người lớn/bé. (Tiêu chuẩn: 01 suất ăn + 01 ghế ngồi và ngủ ghép chung phòng với gia đình + tiền vé máy bay)</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (100, 'Lưu ý:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Quýkhách có mặt trước giờ bay 180 phút, hành lý gọn nhẹ khi đi mang theo giấy tờ tùy thân bản chính.</li><li class="text-secondary-v2 text-base">Lưuý giấy tờ mang theo:</li><li class="text-secondary-v2 text-base">Hànhkhách từ 14 tuổi trở lên:</li><li class="text-secondary-v2 text-base">Khi làm thủ tục đi tàu bay trên các chuyến bay nội địa phải xuất trình một trong những giấy tờ được quy định dưới đây:</li><li class="text-secondary-v2 text-base">Đối với hành khách mang quốc tịch nước ngoài: hộ chiếu.</li><li class="text-secondary-v2 text-base">Đối với hành khách mang quốc tịch Việt Nam phải xuất trình một trong các loại giấy tờ sau:</li><li class="text-secondary-v2 text-base">Hộchiếu</li><li class="text-secondary-v2 text-base">Giấychứng minh nhân dân</li><li class="text-secondary-v2 text-base">Giấychứng nhận của các lực lượng vũ trang</li><li class="text-secondary-v2 text-base">Thẻđại biểu Quốc hội</li><li class="text-secondary-v2 text-base">Thẻđảng viên</li><li class="text-secondary-v2 text-base">Thẻnhà báo</li><li class="text-secondary-v2 text-base">Giấyphép lái xe ô tô, mô tô</li><li class="text-secondary-v2 text-base">Thẻkiểm soát an ninh hàng không</li><li class="text-secondary-v2 text-base">Thẻnhận dạng của các hãng hàng không Việt Nam</li><li class="text-secondary-v2 text-base">Giấychứng nhận nhân thân có xác nhận của công an phường, xã nơi cư trú.</li><li class="text-secondary-v2 text-base">Hànhkhách dưới 14 tuổi:</li><li class="text-secondary-v2 text-base">Hànhkhách dưới 14 tuổi khi làm thủ tục đi tàu bay trên các chuyến bay nội địa phải xuất trình các loại giấy tờ sau:</li><li class="text-secondary-v2 text-base">Đốivới hành khách mang quốc tịch nước ngoài: hộ chiếu.</li><li class="text-secondary-v2 text-base">Đốivới hành khách mang quốc tịch Việt Nam phải xuất trình một trong các loại giấy tờ sau:</li><li class="text-secondary-v2 text-base">Hộchiếu</li><li class="text-secondary-v2 text-base">Giấykhai sinh</li><li class="text-secondary-v2 text-base">Giấychứng sinh (đối với trường hợp dưới 1 tháng tuổi chưa có giấy khai sinh)</li><li class="text-secondary-v2 text-base">Giấyxác nhận của tổ chức xã hội đối với trẻ em do tổ chức xã hội đang nuôi dưỡng (có giá trị sử dụng trong thời gian 06 tháng kể từ ngày xác nhận</li><li class="text-secondary-v2 text-base">Trườnghợp hành khách dưới 14 tuổi đi tàu bay mà không có người đi kèm ngoài các giấy tờ nêu trên phải có giấy cam kết của người đại diện theo pháp luật: Giấy cam kết của người đại diện theo pháp luật của hành khách dưới 14 tuổi đi tàu bay là giấy cam kết vận chuyển giữa hãng hàng không và người đại diện theo pháp luật của khách đó, không bắt buộc phải có giấy xác nhận của chính quyền địa phương và các cơ quan chức năng.</li><li class="text-secondary-v2 text-base">Cácgiấy tờ nêu trên phải đảm bảo các điều kiện:</li><li class="text-secondary-v2 text-base">Còn giá trị sử dụng.</li><li class="text-secondary-v2 text-base">Có ảnh đóng dấu giáp lai, trừ giấy khai sinh, giấy chứng sinh của trẻ em, thẻ kiểm soát an ninh, thẻ nhận dạng của các hãng hàng không.</li><li class="text-secondary-v2 text-base">Trẻ em (dưới 12 tuổi) khi đi du lịch mang theo giấy khai sinh (bản chính hoặc sao y có thị thực) để làm thủ tục hàng không. Trường hợp không có cha hoặc mẹ đi cùng phải có giấy ủy quyền của cha mẹ và có xác nhận của chính quyền địa phương.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (100, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour\xa0</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour\xa0</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour\xa0</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour\xa0</li><li class="text-secondary-v2 text-base">Riêng vé máy bay không\xa0 hoàn, không hủy.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (101, 'Giá bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe Ô tô du lịch đời mới đưa đón suốt hành trình.</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">Nghỉ đêm tại homestay Trạm Tấu, phòng nghỉ tập thể đêm 1. Đêm 2 ngủ tại lán nghỉ trên núi, có chăn dày ấm.</li><li class="text-secondary-v2 text-base">Ăn uống theo chương trình (Bữa chính 150.000đ/ khách/ bữa; Ăn sáng 30.000đ/ khách/ bữa, 1 bữa ăn trưa nhẹ ngày 2 trên đường leo.</li><li class="text-secondary-v2 text-base">Porter dẫn đường</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 50.000.000đ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li><li class="text-secondary-v2 text-base">Xe ôm di chuyển từ Trạm Tấu tới Mỏ Chì và ngược lại</li><li class="text-secondary-v2 text-base">Vé thăm quan theo lịch trình tour.</li><li class="text-secondary-v2 text-base">Vé suối khoáng nóng theo lịch trình.</li><li class="text-secondary-v2 text-base">Tặng áo cờ đỏ sao vàng</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (101, 'Không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (101, 'Một số lưu ý khi đi trekking', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Giầy tờ tùy thân</li><li class="text-secondary-v2 text-base">Trang phục: Quấn áo thoải mái, giày leo núi ôm chân, mũ, găng tay dài tránh nắng, áo mưa mỏng…</li><li class="text-secondary-v2 text-base">Y tế: Mang theo những vật dụng y tế thông dụng như thuốc giảm đau, thuốc chống côn trùng, urgo, kem chống nắng…</li><li class="text-secondary-v2 text-base">Vật dụng cá nhân: Dao nhỏ để gọt hoa quả hoặc cắt thái, bật lửa,…</li><li class="text-secondary-v2 text-base">Bắt buộc phải có người dẫn đường</li><li class="text-secondary-v2 text-base">Không nên uống quá say đêm hôm trước, hôm sau leo mệt.</li><li class="text-secondary-v2 text-base">Mỗi người mang theo khoảng 2 lít nước/người vì leo lên, leo xuống khá mệt và tốn nước.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (102, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">QUYĐỊNH HOÀN HỦY</li><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (103, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Lịchtrình, các điểm thăm quan có thể thay đổi tuỳ thuộc vào tình trạng thời tiết và phương tiện di chuyển nhưng vẫn đảm bảo đầy đủ các điểm thăm quan cho Quý khách.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (104, 'Giá bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô đưa đón theo chương trình sạch sẽ, tiện nghi.</li><li class="text-secondary-v2 text-base">Lái xe am hiểu cung đường chuyên các tuyến Đông Tây Bắc Việt Nam.</li><li class="text-secondary-v2 text-base">Homestay dân dộc nghỉ cộng đồng tại Mù Cang Chải và khách sạn 3*** tại Sapa ( 02 người/phòng, lẻ người ngủ 3)</li><li class="text-secondary-v2 text-base">Bữa trong chương trình : 5 bữa chính suất 120.000đ/ khách + 2 bữa sáng.</li><li class="text-secondary-v2 text-base">Hướng dẫn viên suốt tuyến, nhiệt tình, kinh nghiệm.</li><li class="text-secondary-v2 text-base">Vé tham quan 1 người/lượt</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức cao nhất 20.000.000/ người/ vụ.</li><li class="text-secondary-v2 text-base">Nước uống tiêu chuẩn 01 chai 500ml/người/ngày.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (104, 'Giá không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Thuế VAT (10%)</li><li class="text-secondary-v2 text-base">Các chi phí cá nhân khác (điện thoại, giặt là, đồ uống trong bữa ăn,…)</li><li class="text-secondary-v2 text-base">Vé cáp treo Fansipan.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (104, 'Quy định phụ thu:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 6 tuổi: Miễn phí, ăn và ngủ chung với bố mẹ. Bố mẹ tự thanh toán các chi phí phát sinh của bé (Hai người lớn chỉ kèm 1 trẻ em, trẻ em thứ 2 tính 50% giá tour)</li><li class="text-secondary-v2 text-base">Từ 6 tuổi - 10 tuổi: Tính 75% giá tour.</li><li class="text-secondary-v2 text-base">Từ 11 tuổi trở lên: Tính bằng giá người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (104, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (105, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe Ô tô đời mới đưa đón suốt hành trình (16-29c tùy theo số lượng khách PYS sẽ sắp xếp xe cho phù hợp)</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">03 đêm lưu trú tại Khách sạn tiêu chuẩn 3 sao tiện nghi, trung bình 02 khách/phòng + 01 đêm ngủ tại nhà sàn cộng đồng.</li><li class="text-secondary-v2 text-base">Ăn uống theo chương trình (09 Bữa ăn chính 130.000 VNĐ/ khách/ bữa + 04 bữa sáng theo chương trình)</li><li class="text-secondary-v2 text-base">Vé thắng cảnh vào cửa một lần tại các điểm tham quan.</li><li class="text-secondary-v2 text-base">1 đồ uống/khách tại cafe Mây ơi</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 30.000.000 VNĐ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li><li class="text-secondary-v2 text-base">Thuế VAT</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (105, 'Giá tour không bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Giá tham quan Fansipan và cầu kính Tình Yêu, KDL Cổng Trời</li><li class="text-secondary-v2 text-base">Chi phí xe ôm vào tận ruộng bậc thang</li><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác.</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li><li class="text-secondary-v2 text-base">Tiền tip lái xe, HDV</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (105, 'Chính sách trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 5 tuổi miễn phí (ăn uống và ngủ cùng với bố mẹ, bố mẹ tự túc lo cho bé). Hai người lớn chỉ kèm 1 trẻ em, trẻ em thứ 2 trở đi tính giá 50% giá tour người lớn.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5- dưới 11 tuổi giá tour là 75% giá tour người lớn. (Tiêu chuẩn: 01 suất ăn + 01 ghế ngồi và ngủ ghép cùng giường với bố mẹ). Hai người lớn chỉ kèm 1 trẻ em , trẻ em thứ 2 trở đi tính giá tour như người lớn.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
