INSERT INTO tour_terms (tour_id, section_title, content) VALUES (115, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li><li class="text-secondary-v2 text-base">Xemthêm các đánh giá của khách hàng thực tế đã đông hành cùng PYS Travel:\xa0https://pystravel.vn/khachhang</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (116, 'Dịch vụ bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé máy bay khứ hồi HCM – HÀ NỘI, 7 kg hành lí\\ý xách tay</li><li class="text-secondary-v2 text-base">Xe cao cấp đưa đón Hà Nội - Sapa – Hà Nội</li><li class="text-secondary-v2 text-base">Khách sạn 3* trung tâm đầy đủ tiện nghi( 2 - 3 khách/phòng )</li><li class="text-secondary-v2 text-base">Ăn 05 bữa chính, ăn sáng 02 bữa thực đơn theo chương trình</li><li class="text-secondary-v2 text-base">Hướng dẫn viên</li><li class="text-secondary-v2 text-base">Vé thắng cảnh các tuyến điểm thăm quan theo chương trình</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch trọn gói mức đền bù tối đa 10 triệu đồng</li><li class="text-secondary-v2 text-base">Phục vụ nước uống trên xe.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (116, 'Dịch vụ không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Thuế VAT 10%</li><li class="text-secondary-v2 text-base">Vé cáp treo Fansipan; Vé tàu hoả leo núi; Vé Cầu Kính Rồng Mây</li><li class="text-secondary-v2 text-base">Típ cho hướng dẫn viên, lái xe, nhân viên phục vụ</li><li class="text-secondary-v2 text-base">Nghỉ phòng đơn, điện thoại, giặt là, đồ uống, thăm quan phát sinh ngoài chương trình</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (116, 'Chính sách trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Em bé từ 1 – dưới 2 tuổi giá tour là 300.000đ/bé (ăn uống và ngủ chung với bố mẹ).</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 – dưới 5 tuổi giá tour là 2.800.000đ/bé hai người lớn chỉ kèm 1 trẻ em, trẻ em thứ 2 trở lên sẽ tính giá tour 4.180.000đ/bé.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 - dưới 11 tuổi giá tour là 4.870.000đ/bé (chỗ ngồi riêng trên xe, ăn riêng, ngủ cùng bố mẹ )</li><li class="text-secondary-v2 text-base">Trẻ em từ 11 tuổi trở lên tính 100% giá tour như người lớn (dịch vụ đặt như người lớn)</li></ul>');
