INSERT INTO tour_terms (tour_id, section_title, content) VALUES (117, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé máy bay BAMBOO/Vietjet khứ hồi HCM – HÀ NỘI có 7 kí hành lí xách tay</li><li class="text-secondary-v2 text-base">Xe Ô tô đời mới đưa đón suốt hành trình</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">Khách sạn tiêu chuẩn 3 sao tiện nghi, trung bình 02 khách/phòng</li><li class="text-secondary-v2 text-base">Ăn uống theo chương trình (Bữa chính 150.000đ/ khách/ bữa)</li><li class="text-secondary-v2 text-base">Vé thắng cảnh vào cửa một lần tại các điểm tham quan.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 20.000.000đ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (117, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Thuế VAT 10%</li><li class="text-secondary-v2 text-base">Giá tham quan Fansipan và cầu kính Tình Yêu.</li><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác.</li><li class="text-secondary-v2 text-base">Chi phí mua và hái dâu tại vườn.</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li><li class="text-secondary-v2 text-base">Phụ thu người nước ngoài 10 USD/ 1 người/ 1 ngày.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (117, 'Quy định phụ thu:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 300.000đ/ khách/ đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình)</li><li class="text-secondary-v2 text-base">Trẻ em dưới 2 tuổi, tính phí 300.000đ/bé, ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 tuổi – dưới 5 tuổi giá tour 3.700.000đ/bé hai người lớn chỉ được kèm 1 trẻ em, nếu trẻ em đi kèm nhiều hơn thì từ em thứ 02 trở lên giá tour là 6.090.000đ/bé</li><li class="text-secondary-v2 text-base">Trẻ em từ 5- dưới 11 tuổi giá tour là 6.580.000đ/bé. (Tiêu chuẩn: 01 suất ăn + 01 ghế ngồi và ngủ ghép chung phòng với gia đình + vé máy bay).</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
