INSERT INTO tour_terms (tour_id, section_title, content) VALUES (120, 'Giá bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô đưa đón theo chương trình sạch sẽ, tiện nghi.</li><li class="text-secondary-v2 text-base">Lái xe am hiểu cung đường chuyên các tuyến Đông Tây Bắc Việt Nam.</li><li class="text-secondary-v2 text-base">Homestay dân dộc nghỉ cộng đồng tại Mù Cang Chải và khách sạn 3*** tại Sapa ( 02 người/phòng, lẻ người ngủ 3)</li><li class="text-secondary-v2 text-base">Bữa trong chương trình : 5 bữa chính suất 120.000đ/ khách + 2 bữa sáng.</li><li class="text-secondary-v2 text-base">Hướng dẫn viên suốt tuyến, nhiệt tình, kinh nghiệm.</li><li class="text-secondary-v2 text-base">Vé tham quan 1 người/lượt</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức cao nhất 20.000.000/ người/ vụ.</li><li class="text-secondary-v2 text-base">Nước uống tiêu chuẩn 01 chai 500ml/người/ngày.</li></ul>');
