INSERT INTO tour_terms (tour_id, section_title, content) VALUES (122, 'Giá tour\xa0bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé máy bay khứ hồi HCM – Hà Nội có 7 ký hành lý xách tay</li><li class="text-secondary-v2 text-base">Xe ôtô du lịch đảm bảo chất lượng, lái xe có kinh nghiệm vùng cao.</li><li class="text-secondary-v2 text-base">Nhà nghỉ /Homestay/Khách sạn tiêu chuẩn ở Y Tý và Bắc Hà </li><li class="text-secondary-v2 text-base">Các bữa ăn tại các nhà hàng: 05 bữa 150.000 VNĐ ; 02 bữa sáng: 35.000 VNĐ/bữa</li><li class="text-secondary-v2 text-base">Vé thăm quan du lịch.</li><li class="text-secondary-v2 text-base">Hướng dẫn viên dẫn đoàn am hiểu, nhiệt tình, chu đáo.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch.</li><li class="text-secondary-v2 text-base">Nước uống 500ml/người/ngày.</li><li class="text-secondary-v2 text-base">Thuế VAT</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (122, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Tiền tip cho lái xe, HDV </li><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (122, 'Chính sách trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 2 tuổi, phụ thu 10%, ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 tuổi – dưới 5 tuổi phụ thu 60% , hai người lớn chỉ được kèm 1 trẻ em, nếu trẻ em đi kèm nhiều hơn thì từ em thứ 02 trở lên phụ thu 90% giá tour. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 - dưới 11 tuổi phụ thu 90%. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (122, 'Quy định phụ thu:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 400.000 VNĐ/ khách/ đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình).</li><li class="text-secondary-v2 text-base">Phụ thu chi phí giấy thông hành người nước ngoài 250.000VNĐ/khách</li><li class="text-secondary-v2 text-base">Phụ thu HDV tiếng Anh 10$/ngày.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (122, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngàylễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (123, 'Giá tour\xa0bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ôtô du lịch đảm bảo chất lượng, lái xe có kinh nghiệm vùng cao.</li><li class="text-secondary-v2 text-base">Nhà nghỉ /Homestay/Khách sạn tiêu chuẩn ở Y Tý và Bắc Hà </li><li class="text-secondary-v2 text-base">Các bữa ăn tại các nhà hàng: 05 bữa 150.000 VNĐ ; 02 bữa sáng: 40.000 VNĐ/bữa</li><li class="text-secondary-v2 text-base">Vé thăm quan du lịch.</li><li class="text-secondary-v2 text-base">Hướng dẫn viên dẫn đoàn am hiểu, nhiệt tình, chu đáo.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch.</li><li class="text-secondary-v2 text-base">Nước uống 500ml/người/ngày.</li><li class="text-secondary-v2 text-base">ThuếVAT</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (123, 'Giá tour không bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Tiền tip cho lái xe, HDV </li><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (123, 'Chính sách trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 5 tuổi miễn phí (ăn uống và ngủ cùng với bố mẹ, bố mẹ tự túc lo cho bé). Hai người lớn chỉ kèm 1 trẻ em, trẻ em thứ 2 trở đi tính giá 50% giá tour người lớn.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5- dưới 11 tuổi giá tour là 75% giá tour người lớn. (Tiêu chuẩn: 01 suất ăn + 01 ghế ngồi và ngủ ghép cùng giường với bố mẹ). Hai người lớn chỉ kèm 1 trẻ em , trẻ em thứ 2 trở đi tính giá tour như người lớn.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
