INSERT INTO tour_terms (tour_id, section_title, content) VALUES (127, 'Chính sách trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 2 tuổi, phụ thu 10%, ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 tuổi – dưới 5 tuổi phụ thu 60% , hai người lớn chỉ được kèm 1 trẻ em, nếu trẻ em đi kèm nhiều hơn thì từ em thứ 02 trở lên phụ thu 90% giá tour. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 - dưới 11 tuổi phụ thu 90%. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn</li></ul>');
