INSERT INTO tour_terms (tour_id, section_title, content) VALUES (136, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (137, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé máy bay khứ hồi HCM – HN có 7 ký hành lý xách tay.</li><li class="text-secondary-v2 text-base">1 đến 2 đêm ngủ tại khách sạn ở Hà Nội</li><li class="text-secondary-v2 text-base">Xe đón tiễn khứ hồi từ sân bay Nội Bài về khách sạn ngay khu phố cổ.</li><li class="text-secondary-v2 text-base">Xe ôtô du lịch đảm bảo chất lượng, lái xe có kinh nghiệm vùng cao.</li><li class="text-secondary-v2 text-base">Nhà nghỉ khép kín hoặc homestay nhà sàn tập thể ở trung tâm Y Tý, có nóng lạnh. </li><li class="text-secondary-v2 text-base">Các bữa ăn tại các nhà hàng: 3 bữa chính (2 bữa 130,000 vnđ/khách, 1 bữa 180,000 vnđ/khách); 40.000VNĐ/bữa phụ.</li><li class="text-secondary-v2 text-base">Vé tham quan du lịch.</li><li class="text-secondary-v2 text-base">Dẫn đoàn am hiểu, nhiệt tình, chu đáo.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch.</li><li class="text-secondary-v2 text-base">Nước uống 500ml/người/ngày.</li><li class="text-secondary-v2 text-base">Thuế VAT</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (137, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Tiền tip cho lái xe, HDV</li><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (137, 'Quy định phụ thu:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 400.000 VNĐ/ khách/ 1 đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình), 400.000 VNĐ/khách/1 đêm ở Hà Nội.</li><li class="text-secondary-v2 text-base">Phụ thu chi phí giấy thông hành người nước ngoài 250.000VNĐ/khách</li><li class="text-secondary-v2 text-base">Phụ thu HDV tiếng Anh 10$/ngày</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (137, 'Quy định phụ thu trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 2 tuổi, phụ thu 10%, ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 tuổi – dưới 5 tuổi phụ thu 60% , hai người lớn chỉ được kèm 1 trẻ em, nếu trẻ em đi kèm nhiều hơn thì từ em thứ 02 trở lên phụ thu 90% giá tour. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 - dưới 11 tuổi phụ thu 90%. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (137, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.\xa0</li><li class="text-secondary-v2 text-base">Quý khách có mặt trước giờ bay 180 phút, hành lý gọn nhẹ khi đi mang theo giấy tờ tùy thân bản chính.</li><li class="text-secondary-v2 text-base">*Lưu ý giấy tờ mang theo:</li><li class="text-secondary-v2 text-base">Hành khách từ 14 tuổi trở lên:</li><li class="text-secondary-v2 text-base">+Khi làm thủ tục đi tàu bay trên các chuyến bay nội địa phải xuất trình một trong những giấy tờ được quy định dưới đây:</li><li class="text-secondary-v2 text-base">+Đối với hành khách mang quốc tịch nước ngoài: hộ chiếu.</li><li class="text-secondary-v2 text-base">+Đối với hành khách mang quốc tịch Việt Nam phải xuất trình một trong các loại giấy tờ sau:</li><li class="text-secondary-v2 text-base">Hộ chiếu</li><li class="text-secondary-v2 text-base">Giấy chứng minh nhân dân</li><li class="text-secondary-v2 text-base">Giấy chứng nhận của các lực lượng vũ trang</li><li class="text-secondary-v2 text-base">Thẻ đại biểu Quốc hội</li><li class="text-secondary-v2 text-base">Thẻ đảng viên</li><li class="text-secondary-v2 text-base">Thẻ nhà báo</li><li class="text-secondary-v2 text-base">Giấy phép lái xe ô tô, mô tô</li><li class="text-secondary-v2 text-base">Thẻ kiểm soát an ninh hàng không</li><li class="text-secondary-v2 text-base">Thẻ nhận dạng của các hãng hàng không Việt Nam</li><li class="text-secondary-v2 text-base">Giấy chứng nhận nhân thân có xác nhận của công an phường, xã nơi cư trú.</li><li class="text-secondary-v2 text-base">Hành khách dưới 14 tuổi:</li><li class="text-secondary-v2 text-base">+Hành khách dưới 14 tuổi khi làm thủ tục đi tàu bay trên các chuyến bay nội địa phải xuất trình các loại giấy tờ sau:</li><li class="text-secondary-v2 text-base">+Đối với hành khách mang quốc tịch nước ngoài: hộ chiếu.</li><li class="text-secondary-v2 text-base">+Đối với hành khách mang quốc tịch Việt Nam phải xuất trình một trong các loại giấy tờ sau:</li><li class="text-secondary-v2 text-base">Hộ chiếu</li><li class="text-secondary-v2 text-base">Giấy khai sinh</li><li class="text-secondary-v2 text-base">Giấy chứng sinh (đối với trường hợp dưới 1 tháng tuổi chưa có giấy khai sinh)</li><li class="text-secondary-v2 text-base">Giấy xác nhận của tổ chức xã hội đối với trẻ em do tổ chức xã hội đang nuôi dưỡng (có giá trị sử dụng trong thời gian 06 tháng kể từ ngày xác nhận</li><li class="text-secondary-v2 text-base">+Trường hợp hành khách dưới 14 tuổi đi tàu bay mà không có người đi kèm ngoài các giấy tờ nêu trên phải có giấy cam kết của người đại diện theo pháp luật: Giấy cam kết của người đại diện theo pháp luật của hành khách dưới 14 tuổi đi tàu bay là giấy cam kết vận chuyển giữa hãng hàng không và người đại diện theo pháp luật của khách đó, không bắt buộc phải có giấy xác nhận của chính quyền địa phương và các cơ quan chức năng.</li><li class="text-secondary-v2 text-base">Các giấy tờ nêu trên phải đảm bảo các điều kiện:</li><li class="text-secondary-v2 text-base">+Còn giá trị sử dụng.</li><li class="text-secondary-v2 text-base">+Có ảnh đóng dấu giáp lai, trừ giấy khai sinh, giấy chứng sinh của trẻ em, thẻ kiểm soát an ninh, thẻ nhận dạng của các hãng hàng không.</li><li class="text-secondary-v2 text-base">+Trẻ em (dưới 12 tuổi) khi đi du lịch mang theo giấy khai sinh (bản chính hoặc sao y có thị thực) để làm thủ tục hàng không. Trường hợp không có cha hoặc mẹ đi cùng phải có giấy ủy quyền của cha mẹ và có xác nhận của chính quyền địa phương.</li></ul>');
