INSERT INTO tour_terms (tour_id, section_title, content) VALUES (140, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe du lịch 16/29/35/45 máy lạnh đưa đón theo lịch trình. Lái xe am hiểu cung đường</li><li class="text-secondary-v2 text-base">Nghỉ đêm tại khách sạn tiêu chuẩn 3 sao tiêu chuẩn 02 người/phòng, lẻ người ngủ 3.</li><li class="text-secondary-v2 text-base">Các bữa ăn theo chương trình: 01 bữa sáng, 03 bữa 150.000 VNĐ/người</li><li class="text-secondary-v2 text-base">Vé thắng cảnh tại các điểm thăm quan: Thác Dải Yếm, Happy Land, Mocha Hill</li><li class="text-secondary-v2 text-base">Hướng dẫn viên vui vẻ nhiệt tình.</li><li class="text-secondary-v2 text-base">Nước uống trên xe: 01 chai/ khách/ ngày</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức 30.000.000 VNĐ/ người/ vụ</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (140, 'Giá tour không bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Các chi phí cá nhân ngoài chương trình: vé thăm quan, điện thoại, đồ uống, giặt là... </li><li class="text-secondary-v2 text-base">Tiền tip cho HDV và lái xe</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (140, 'Quy định phụ thu', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 400.000 VNĐ/ khách/ đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình).</li><li class="text-secondary-v2 text-base">Phụ thu chi phí giấy thông hành người nước ngoài 250.000VNĐ/khách</li><li class="text-secondary-v2 text-base">Phụ thu HDV tiếng Anh 10$/ngày.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (140, 'Chính sách trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 5 tuổi miễn phí (ăn uống và ngủ cùng với bố mẹ, bố mẹ tự túc lo cho bé). Hai người lớn chỉ kèm 1 trẻ em, trẻ em thứ 2 trở đi tính giá 50% giá tour người lớn.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5- dưới 11 tuổi giá tour là 75% giá tour người lớn. (Tiêu chuẩn: 01 suất ăn + 01 ghế ngồi và ngủ ghép cùng giường với bố mẹ). Hai người lớn chỉ kèm 1 trẻ em , trẻ em thứ 2 trở đi tính giá tour như người lớn.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
