INSERT INTO tour_terms (tour_id, section_title, content) VALUES (141, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô du lịch đời mới đưa đón suốt hành trình.</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">Homestay tại Tà Xùa, sạch sẽ tiện nghi, phòng tập thể. Hoặc phòng riêng tiêu chuẩn 2-3 khách/ phòng</li><li class="text-secondary-v2 text-base">Ăn uống theo chương trình (1 Bữa chính 120.000 VNĐ/ khách/ bữa + 1 bữa tối 180.000 VNĐ/ khách + 1 bữa trưa ngày 2 suất ăn 150.000 VNĐ/ khách + Ăn sáng tại homestay). </li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 30.000.000 VNĐ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li><li class="text-secondary-v2 text-base">Vé thăm quan theo hành trình.</li><li class="text-secondary-v2 text-base">Thuế VAT</li></ul>');
