INSERT INTO tour_terms (tour_id, section_title, content) VALUES (151, 'Chính sách trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 2 tuổi, phụ thu 10%, ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 tuổi – dưới 5 tuổi phụ thu 60% , hai người lớn chỉ được kèm 1 trẻ em, nếu trẻ em đi kèm nhiều hơn thì từ em thứ 02 trở lên phụ thu 90% giá tour. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 - dưới 11 tuổi phụ thu 90%. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (151, 'Quy định phụ thu', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 400.000 VNĐ/ khách/ 1 đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình), 400.000 VNĐ/khách/1 đêm ở Hà Nội.</li><li class="text-secondary-v2 text-base">Phụ thu chi phí giấy thông hành người nước ngoài 250.000VNĐ/khách</li><li class="text-secondary-v2 text-base">Phụ thu HDV tiếng Anh 10$/ngày</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (151, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Riêng vé máy bay không hoàn, không hủy theo quy định của hãng hàng không.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (152, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô du lịch đời mới đưa đón suốt hành trình.</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">Homestay tại Tà Xùa, sạch sẽ tiện nghi, phòng tập thể. Hoặc phòng riêng tiêu chuẩn 2-3 khách/ phòng</li><li class="text-secondary-v2 text-base">Ăn uống theo chương trình (1 Bữa chính 120.000 VNĐ/ khách/ bữa + 1 bữa tối 180.000 VNĐ/ khách + 1 bữa trưa ngày 2 suất ăn 150.000 VNĐ/ khách + Ăn sáng tại homestay). </li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 30.000.000 VNĐ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li><li class="text-secondary-v2 text-base">Vé thăm quan theo hành trình.</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
