INSERT INTO tour_terms (tour_id, section_title, content) VALUES (160, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe Ô tô đời mới đưa đón suốt hành trình (16-29c tùy theo số lượng khách PYS sẽ sắp xếp xe cho phù hợp)</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">03 đêm lưu trú tại Khách sạn tiêu chuẩn 3 sao tiện nghi, trung bình 02 khách/phòng + 01 đêm ngủ tại nhà sàn cộng đồng.</li><li class="text-secondary-v2 text-base">Ăn uống theo chương trình (09 Bữa ăn chính 130.000 VNĐ/ khách/ bữa + 04 bữa sáng theo chương trình)</li><li class="text-secondary-v2 text-base">Vé thắng cảnh vào cửa một lần tại các điểm tham quan.</li><li class="text-secondary-v2 text-base">1 đồ uống/khách tại cafe Mây ơi</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 30.000.000 VNĐ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li><li class="text-secondary-v2 text-base">Thuế VAT</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (160, 'Giá tour không bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Giá tham quan Fansipan và cầu kính Tình Yêu, KDL Cổng Trời</li><li class="text-secondary-v2 text-base">Chi phí xe ôm vào tận ruộng bậc thang</li><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác.</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li><li class="text-secondary-v2 text-base">Tiền tip lái xe, HDV</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (160, 'Chính sách trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 5 tuổi miễn phí (ăn uống và ngủ cùng với bố mẹ, bố mẹ tự túc lo cho bé). Hai người lớn chỉ kèm 1 trẻ em, trẻ em thứ 2 trở đi tính giá 50% giá tour người lớn.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5- dưới 11 tuổi giá tour là 75% giá tour người lớn. (Tiêu chuẩn: 01 suất ăn + 01 ghế ngồi và ngủ ghép cùng giường với bố mẹ). Hai người lớn chỉ kèm 1 trẻ em , trẻ em thứ 2 trở đi tính giá tour như người lớn.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (160, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Riêng vé máy bay không hoàn, không hủy theo quy định của hãng hàng không.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (161, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe du lịch máy lạnh đưa đón theo lịch trình. Lái xe am hiểu cung đường</li><li class="text-secondary-v2 text-base">Phòng nghỉ đêm tại khách sạn 3*** (2 người/phòng), lẻ nam/nữ ngủ phòng ba. </li><li class="text-secondary-v2 text-base">Các bữa ăn theo chương trình (01 bữa sáng,03 bữa chính suất 150.000 VNĐ/người/suất) </li><li class="text-secondary-v2 text-base">Vé thắng cảnh tại các điểm thăm quan: Thác Dải Yếm ( chưa gồm: phí cầu kính ), Happy Land, Đồi chè,…</li><li class="text-secondary-v2 text-base">Vé máy bay khứ hồi vietjet/bamboo HCM – HÀ NỘI có 7 kg hành lý xách tay </li><li class="text-secondary-v2 text-base">1- 2 đêm khách sạn tại Hà Nội (tùy theo ngày khởi hành chương trình)</li><li class="text-secondary-v2 text-base">Xe đón tiễn sân bay khứ hồi từ sân bay Nội Bài về khách sạn</li><li class="text-secondary-v2 text-base">Hướng dẫn viên vui vẻ nhiệt tình. Lái xe am hiểu cung đường</li><li class="text-secondary-v2 text-base">Nước uống 1 chai/ khách/ ngày </li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức 30.000.000 VNĐ/ người/ vụ</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
