INSERT INTO tour_terms (tour_id, section_title, content) VALUES (171, 'Quy định phụ thu', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em từ 11 tuổi trở lên: tính 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Trẻ em từ 05 – 11 tuổi: tính 75% giá trị tour – Ngủ chung với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em dưới 05 tuổi: miễn phí (Ăn ngủ cùng bố mẹ, ngồi lòng bố hoặc mẹ), 2 người lớn miễn phí 1 trẻ em, trẻ thứ 2 tính 50% giá trị tour.</li><li class="text-secondary-v2 text-base">Lễ tết phụ thu 150.000 vnđ/ người.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (171, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Riêng vé máy bay thì không hoàn, không hủy theo quy định hãng hàng không.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (172, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe du lịch đời mới, máy lạnh đưa đón theo lịch trình.</li><li class="text-secondary-v2 text-base">Vé máy bay khứ hồi HCM – HÀ NỘI có 7 ký hành lý xách tay</li><li class="text-secondary-v2 text-base">Có 01-02 đêm nghỉ tại khách sạn ngay khu phố cổ Hà Nội.</li><li class="text-secondary-v2 text-base">Xe đón tiễn sân bay Nội bài về khách sạn ngay khu phố cổ Hà Nội.</li><li class="text-secondary-v2 text-base">02 đêm tại phòng khách sạn 3 sao tiêu chuẩn 02 khách/phòng, Lẻ Nam/Nữ ngủ 3 người/phòng</li><li class="text-secondary-v2 text-base">Các bữa ăn theo chương trình (4 bữa chính: 120,000 VNĐ/suất, 01 bữa chính: 150,000 VNĐ/suất), 02 bữa phụ: 40,000 VNĐ/suất)</li><li class="text-secondary-v2 text-base">Vé thắng cảnh tại các điểm thăm quan.</li><li class="text-secondary-v2 text-base">Hướng dẫn viên vui vẻ nhiệt tình.</li><li class="text-secondary-v2 text-base">Nước uống 1 chai/ khách/ ngày</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức cao nhất 30.000.000 VNĐ/người/vụ.</li><li class="text-secondary-v2 text-base">Thuế VAT</li></ul>');
