INSERT INTO tour_terms (tour_id, section_title, content) VALUES (175, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (176, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô du lịch đời mới đưa đón suốt hành trình.</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">Homestay tại Tà Xùa, sạch sẽ tiện nghi, phòng tập thể. Hoặc phòng riêng tiêu chuẩn 2-3 khách/ phòng</li><li class="text-secondary-v2 text-base">Ăn uống theo chương trình (2 Bữa chính 120.000đ/ khách/ bữa + 1 bữa tối 180.000/ khách, 1 bữa sáng 35.000/khách)</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 30.000.000đ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li><li class="text-secondary-v2 text-base">Vé thăm quan theo hành trình.</li><li class="text-secondary-v2 text-base">Thuế VAT</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (176, 'Giá tour không bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chi phí cá nhân.</li><li class="text-secondary-v2 text-base">Đồ uống trong các bữa ăn…</li><li class="text-secondary-v2 text-base">Tip cho lái xe và HDV</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (176, 'Quy định phụ thu', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em từ 11 tuổi trở lên: tính 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Trẻ em từ 05 – 11 tuổi: tính 75% giá trị tour – Ngủ chung với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em dưới 05 tuổi: miễn phí (Ăn ngủ cùng bố mẹ, ngồi lòng bố hoặc mẹ), 2 người lớn miễn phí 1 trẻ em, trẻ thứ 2 tính 50% giá trị tour.</li><li class="text-secondary-v2 text-base">Lễ tết phụ thu 150.000 vnđ/ người.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (176, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (177, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe du lịch máy lạnh đưa đón theo lịch trình. Lái xe am hiểu cung đường</li><li class="text-secondary-v2 text-base">Phòng nghỉ đêm tại khách sạn 3*** (2 người/phòng), lẻ nam/nữ ngủ phòng ba.</li><li class="text-secondary-v2 text-base">Các bữa ăn theo chương trình (01 bữa sáng,03 bữa chính suất 150.000 vnđ/người/suất)</li><li class="text-secondary-v2 text-base">Chi phí tổ chức giã bánh dày, làm sáp ong truyền thống.</li><li class="text-secondary-v2 text-base">Vé thắng cảnh tại các điểm thăm quan.</li><li class="text-secondary-v2 text-base">Vé máy bay khứ hồi vietjet/bamboo HCM – HÀ NỘI có 7 ký xách tay</li><li class="text-secondary-v2 text-base">1- 2 đêm khách sạn tại Hà Nội (tùy theo ngày khởi hành chương trình)</li><li class="text-secondary-v2 text-base">Xe đón tiễn sân bay khứ hồi từ sân bay Nội Bài về khách sạn</li><li class="text-secondary-v2 text-base">Hướng dẫn viên vui vẻ nhiệt tình. Lái xe am hiểu cung đường</li><li class="text-secondary-v2 text-base">Nước uống 1 chai/ khách/ ngày</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức 20.000.000VNĐ/ người/ vụ</li><li class="text-secondary-v2 text-base">Thuế VAT</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (177, 'Giá tour không bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Tiền tip lái xe, HDV</li><li class="text-secondary-v2 text-base">Đồ uống trong các bữa ăn.</li><li class="text-secondary-v2 text-base">Chi phí cá nhân.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (177, 'Phụ thu trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 2 tuổi, phụ thu 10%, ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 tuổi – dưới 5 tuổi phụ thu 60% , hai người lớn chỉ được kèm 1 trẻ em, nếu trẻ em đi kèm nhiều hơn thì từ em thứ 02 trở lên phụ thu 90% giá tour. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 - dưới 11 tuổi phụ thu 90%. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (177, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Riêng vé máy bay không hoàn, không hủy theo quy định của hãng hàng không.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (178, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé máy bay BAMBOO/Vietjet khứ hồi HCM – HÀ NỘI có 7 kí hành lí xách tay</li><li class="text-secondary-v2 text-base">Xe Ô tô đời mới đưa đón suốt hành trình</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">Khách sạn tiêu chuẩn 3 sao tiện nghi, trung bình 02 khách/phòng</li><li class="text-secondary-v2 text-base">Ăn uống theo chương trình (Bữa chính 150.000đ/ khách/ bữa)</li><li class="text-secondary-v2 text-base">Vé thắng cảnh vào cửa một lần tại các điểm tham quan.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 20.000.000đ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (178, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Thuế VAT 10%</li><li class="text-secondary-v2 text-base">Giá tham quan Fansipan và cầu kính Tình Yêu.</li><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác.</li><li class="text-secondary-v2 text-base">Chi phí mua và hái dâu tại vườn.</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li><li class="text-secondary-v2 text-base">Phụ thu người nước ngoài 10 USD/ 1 người/ 1 ngày.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (178, 'Quy định phụ thu:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 300.000đ/ khách/ đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình)</li><li class="text-secondary-v2 text-base">Trẻ em dưới 2 tuổi, tính phí 300.000đ/bé, ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 tuổi – dưới 5 tuổi giá tour 3.700.000đ/bé hai người lớn chỉ được kèm 1 trẻ em, nếu trẻ em đi kèm nhiều hơn thì từ em thứ 02 trở lên giá tour là 6.090.000đ/bé</li><li class="text-secondary-v2 text-base">Trẻ em từ 5- dưới 11 tuổi giá tour là 6.580.000đ/bé. (Tiêu chuẩn: 01 suất ăn + 01 ghế ngồi và ngủ ghép chung phòng với gia đình + vé máy bay).</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (178, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Riêng vé máy bay không hoàn, không hủy theo quy định của hãng hàng không.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (179, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xeô tô đưa đón theo lịch trình</li><li class="text-secondary-v2 text-base">Hướng dẫn viên bản địa nhiệt tình, vui vẻ.</li><li class="text-secondary-v2 text-base">Vé vào cửa các điểm thăm quan</li><li class="text-secondary-v2 text-base">01 bữa ăn nhẹ (Gà nướng,lợn nướng, xôi, dưa chuột, trái cây)</li><li class="text-secondary-v2 text-base">Chi phí setup dã ngoại: bàn ghế, thảm ngồi, cốc chén…</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/khách/ngày</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (179, 'Giá không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hóa đơn VAT</li><li class="text-secondary-v2 text-base">Nước uống trong bữa ăn</li><li class="text-secondary-v2 text-base">Các chi phí khác ngoài chương trình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (180, 'Giá bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe Ô tô du lịch đời mới đưa đón suốt hành trình.</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">Nghỉ đêm tại khách sạn 3*, 2 khách/ phòng, lẻ nam nữ ngủ phòng 3 khách.</li><li class="text-secondary-v2 text-base">Ăn uống theo chương trình (Bữa chính 150.000đ/ khách/ bữa; Ăn sáng 30.000đ/ khách/ bữa, 1 bữa ăn nhẹ.</li><li class="text-secondary-v2 text-base">Porter dẫn dường.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 50.000.000đ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li><li class="text-secondary-v2 text-base">Chi phí làm thủ tục với biên phòng.</li><li class="text-secondary-v2 text-base">Vé thăm quan theo lịch trình tour.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (180, 'Không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (180, 'Một số lưu ý khi đi trekking', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Giấ y tờ tùy thân để làm thủ tục với đồn biên phòng Pha Luông.</li><li class="text-secondary-v2 text-base">Trang phục: Quấn áo thoải mái, giày leo núi ôm chân, mũ, găng tay dài tránh nắng, áo mưa mỏng…</li><li class="text-secondary-v2 text-base">Y tế: Mang theo những vật dụng y tế thông dụng như thuốc giảm đau, thuốc chống côn trùng, urgo, kem chống nắng…</li><li class="text-secondary-v2 text-base">Vật dụng cá nhân: Dao nhỏ để gọt hoa quả hoặc cắt thái, bật lửa,…</li><li class="text-secondary-v2 text-base">Bắt buộc phải có người dẫn đường, phải xin phép đồn biên phòng.</li><li class="text-secondary-v2 text-base">Không nên uống quá say đêm hôm trước, hôm sau leo mệt.</li><li class="text-secondary-v2 text-base">Mỗi người mang theo khoảng 2 lít nước/người vì leo lên, leo xuống (từ đồn biên phòng) khá mệt và tốn nước.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (181, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe du lịch máy lạnh đưa đón theo lịch trình. Lái xe am hiểu cung đường</li><li class="text-secondary-v2 text-base">Nghỉ đêm tại khách sạn 3*** sạch sẽ tiện nghi, tiêu chuẩn 02 người/phòng</li><li class="text-secondary-v2 text-base">Các bữa ăn : 01 bữa sáng + 03 bữa chính suất 120.000 vnđ/người</li><li class="text-secondary-v2 text-base">Vé thắng cảnh tại các điểm thăm quan.</li><li class="text-secondary-v2 text-base">Hướng dẫn viên vui vẻ nhiệt tình.</li><li class="text-secondary-v2 text-base">Nước uống 1 chai/ khách/ ngày</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức 20.000.000VNĐ/ người/ vụ</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (181, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Thuế VAT.</li><li class="text-secondary-v2 text-base">Các chi phí cá nhân ngoài chương trình: điện thoại, đồ uống, giặt là...</li><li class="text-secondary-v2 text-base">Tip cho lái xe và HDV</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (181, 'Điều kiện phụ thu:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn: 650.000 VNĐ/khách.</li><li class="text-secondary-v2 text-base">Trẻ em dưới 6 tuổi, ăn uống và ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 6 -10 tuổi, tính 75% chi phí người lớn.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
