INSERT INTO tour_terms (tour_id, section_title, content) VALUES (183, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô du lịch đời mới đưa đón suốt hành trình.</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">Homestay phòng riêng/phòng dorm tại Tà Xùa, khách sạn 3 sao tại Mộc Châu tiêu chuẩn 2 khách/phòng</li><li class="text-secondary-v2 text-base">Ăn uống theo chương trình (1 Bữa chính 120.000đ/ khách/ bữa + 3 bữa chính tiêu chuẩn 150.000đ/khách/bữa + 1 bữa tối 180.000/ khách + 2 bữa ăn sáng)</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 30.000.000đ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li><li class="text-secondary-v2 text-base">Vé thăm quan theo hành trình.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (183, 'Giá tour không bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chi phí cá nhân.</li><li class="text-secondary-v2 text-base">Vé cầu kính Bạch Long</li><li class="text-secondary-v2 text-base">VAT</li><li class="text-secondary-v2 text-base">Tip cho lái xe và HDV</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (183, 'Quy định phụ thu', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em từ 11 tuổi trở lên: tính 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Trẻ em từ 05 – 11 tuổi: tính 75% giá trị tour – Ngủ chung với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em dưới 05 tuổi: miễn phí (Ăn ngủ cùng bố mẹ, ngồi lòng bố hoặc mẹ), 2 người lớn miễn phí 1 trẻ em, trẻ thứ 2 tính 50% giá trị tour.</li><li class="text-secondary-v2 text-base">Lễ tết phụ thu 150.000 vnđ/ người</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (183, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (184, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe du lịch 16/29/35/45 máy lạnh đưa đón theo lịch trình. Lái xe am hiểu cung đường</li><li class="text-secondary-v2 text-base">Nghỉ đêm tại khách sạn tiêu chuẩn 3 sao tiêu chuẩn 02 người/phòng, lẻ người ngủ 3.</li><li class="text-secondary-v2 text-base">Các bữa ăn theo chương trình: 01 bữa sáng, 03 bữa 150.000 VNĐ/người</li><li class="text-secondary-v2 text-base">Vé thắng cảnh tại các điểm thăm quan: Thác Dải Yếm, Happy Land, Mocha Hill</li><li class="text-secondary-v2 text-base">Hướng dẫn viên vui vẻ nhiệt tình.</li><li class="text-secondary-v2 text-base">Nước uống trên xe: 01 chai/ khách/ ngày</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức 30.000.000 VNĐ/ người/ vụ</li><li class="text-secondary-v2 text-base">Thuế VAT</li></ul>');
