INSERT INTO tour_terms (tour_id, section_title, content) VALUES (201, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Riêng vé máy bay không hoàn, không hủy theo quy định của hãng hàng không.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (202, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe du lịch đời mới, máy lạnh đưa đón theo lịch trình. </li><li class="text-secondary-v2 text-base">02 đêm tại phòng khách sạn 3 sao tiêu chuẩn 02 khách/phòng, Lẻ Nam/Nữ ngủ 3 người/phòng</li><li class="text-secondary-v2 text-base">Các bữa ăn theo lịch trình 04 bữa chính: 120.000VNĐ/suất ; 01 bữa chính: 150.000 VNĐ/suất,02 bữa sáng - 35.000VNĐ/suất.</li><li class="text-secondary-v2 text-base">Vé thắng cảnh tại các điểm thăm quan. </li><li class="text-secondary-v2 text-base">Hướng dẫn viên vui vẻ nhiệt tình. </li><li class="text-secondary-v2 text-base">Nước uống 1 chai/ khách/ ngày </li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức cao nhất 30.000.000 VNĐ/người/vụ.</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (202, 'Giá không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Các chi phí cá nhân ngoài chương trình: điện thoại, đồ uống, giặt là... </li><li class="text-secondary-v2 text-base">Tip cho lái xe và HDV</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (202, 'Quy định phụ thu:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 400.000VNĐ/ khách/ 1 đêm(Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình).</li><li class="text-secondary-v2 text-base">Phụ thu HDV tiếng Anh 10$/ngày.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (202, 'Phụ thu trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 5 tuổi miễn phí (ăn uống và ngủ cùng với bố mẹ, bố mẹ tự túc lo cho bé). Hai người lớn chỉ kèm 1 trẻ em, trẻ em thứ 2 trở đi tính giá 50% giá tour người lớn.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5- dưới 11 tuổi giá tour là 75% giá tour người lớn. (Tiêu chuẩn: 01 suất ăn + 01 ghế ngồi và ngủ ghép cùng giường với bố mẹ). Hai người lớn chỉ kèm 1 trẻ em , trẻ em thứ 2 trở đi tính giá tour như người lớn.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
