INSERT INTO tour_terms (tour_id, section_title, content) VALUES (218, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe du lịch đời mới, máy lạnh đưa đón theo lịch trình.</li><li class="text-secondary-v2 text-base">Vé máy bay khứ hồi HCM – HÀ NỘI có 7 ký hành lý xách tay</li><li class="text-secondary-v2 text-base">Có 01-02 đêm nghỉ tại khách sạn ngay khu phố cổ Hà Nội.</li><li class="text-secondary-v2 text-base">Xe đón tiễn sân bay Nội bài về khách sạn ngay khu phố cổ Hà Nội.</li><li class="text-secondary-v2 text-base">02 đêm tại phòng khách sạn 3 sao tiêu chuẩn 02 khách/phòng, Lẻ Nam/Nữ ngủ 3 người/phòng</li><li class="text-secondary-v2 text-base">Các bữa ăn theo chương trình (4 bữa chính: 120,000 VNĐ/suất, 01 bữa chính: 150,000 VNĐ/suất), 02 bữa phụ: 40,000 VNĐ/suất)</li><li class="text-secondary-v2 text-base">Vé thắng cảnh tại các điểm thăm quan.</li><li class="text-secondary-v2 text-base">Hướng dẫn viên vui vẻ nhiệt tình.</li><li class="text-secondary-v2 text-base">Nước uống 1 chai/ khách/ ngày</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức cao nhất 30.000.000 VNĐ/người/vụ.</li><li class="text-secondary-v2 text-base">Thuế VAT</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (218, 'Giá tour không bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Các chi phí cá nhân ngoài chương trình: điện thoại, đồ uống, giặt là...</li><li class="text-secondary-v2 text-base">Tip cho lái xe và HDV</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li></ul>');
