INSERT INTO tour_terms (tour_id, section_title, content) VALUES (228, 'Quy định phụ thu', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em từ 11 tuổi trở lên: tính 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Trẻ em từ 05 – 11 tuổi: tính 75% giá trị tour – Ngủ chung với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em dưới 05 tuổi: miễn phí (Ăn ngủ cùng bố mẹ, ngồi lòng bố hoặc mẹ), 2 người lớn miễn phí 1 trẻ em, trẻ thứ 2 tính 50% giá trị tour.</li><li class="text-secondary-v2 text-base">Lễ tết phụ thu 150.000 vnđ/ người</li></ul>');
