INSERT INTO tour_terms (tour_id, section_title, content) VALUES (261, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (262, 'Giá bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô đưa đón theo chương trình sạch sẽ, tiện nghi.</li><li class="text-secondary-v2 text-base">Lái xe am hiểu cung đường chuyên các tuyến Đông Tây Bắc Việt Nam.</li><li class="text-secondary-v2 text-base">Homestay dân dộc nghỉ cộng đồng tại Mù Cang Chải và khách sạn 3*** tại Sapa ( 02 người/phòng, lẻ người ngủ 3)</li><li class="text-secondary-v2 text-base">Bữa trong chương trình : 5 bữa chính suất 120.000đ/ khách + 2 bữa sáng.</li><li class="text-secondary-v2 text-base">Hướng dẫn viên suốt tuyến, nhiệt tình, kinh nghiệm.</li><li class="text-secondary-v2 text-base">Vé tham quan 1 người/lượt</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức cao nhất 20.000.000/ người/ vụ.</li><li class="text-secondary-v2 text-base">Nước uống tiêu chuẩn 01 chai 500ml/người/ngày.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (262, 'Giá không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Thuế VAT (10%)</li><li class="text-secondary-v2 text-base">Các chi phí cá nhân khác (điện thoại, giặt là, đồ uống trong bữa ăn,…)</li><li class="text-secondary-v2 text-base">Vé cáp treo Fansipan.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (262, 'Quy định phụ thu:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 6 tuổi: Miễn phí, ăn và ngủ chung với bố mẹ. Bố mẹ tự thanh toán các chi phí phát sinh của bé (Hai người lớn chỉ kèm 1 trẻ em, trẻ em thứ 2 tính 50% giá tour)</li><li class="text-secondary-v2 text-base">Từ 6 tuổi - 10 tuổi: Tính 75% giá tour.</li><li class="text-secondary-v2 text-base">Từ 11 tuổi trở lên: Tính bằng giá người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (262, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (308, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé máy bay khứ hồi HCM – HN có 7 ký hành lý xách tay</li><li class="text-secondary-v2 text-base">Xe ô tô du lịch đưa đón theo lịch trình, lái xe có kinh nghiệm tuyến du lịch Tây Bắc (xe 16c-29c tùy theo số lượng khách PYS sẽ sắp xếp xe cho phù hợp)</li><li class="text-secondary-v2 text-base">Nghỉ tại khách sạn tiêu chuẩn 2 -3 người/phòng đầy đủ tiện nghi</li><li class="text-secondary-v2 text-base">Bữa ăn theo lịch trình: 150.000 VNĐ/bữa chính; 40.000 VNĐ/bữa phụ.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch. Nước uống 500ml/người/ngày.</li><li class="text-secondary-v2 text-base">HDV nhiệt tình, kinh nghiệm.</li><li class="text-secondary-v2 text-base">Xe đón tiễn sân bay Nội Bài </li><li class="text-secondary-v2 text-base">01 đêm ngủ tại khách sạn HN</li><li class="text-secondary-v2 text-base">Vé vào cửa tham quan các điểm theo chương trình. </li><li class="text-secondary-v2 text-base">Nước uống trên xe.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (308, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Đồ uống gọi thêm trong các bữa ăn.</li><li class="text-secondary-v2 text-base">Chi phí cá nhân.</li><li class="text-secondary-v2 text-base">Xe ôm chở vào sâu trong bản Chế Cu Nha, La Pán Tẩn.</li><li class="text-secondary-v2 text-base">Vé suối khoáng nóng Trạm Tấu.</li><li class="text-secondary-v2 text-base">Tiền tip lái xe, HDV</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (308, 'Quy định phụ thu', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 400.000 VNĐ/ khách/ 1 đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình), 400.000 VNĐ/khách/1 đêm ở Hà Nội.</li><li class="text-secondary-v2 text-base">Phụ thu chi phí giấy thông hành người nước ngoài 250.000VNĐ/khách</li><li class="text-secondary-v2 text-base">Phụ thu HDV tiếng Anh 10$/ngày</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (308, 'Chính sách\xa0trẻ em:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 2 tuổi, phụ thu 10%, ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 tuổi – dưới 5 tuổi phụ thu 60% , hai người lớn chỉ được kèm 1 trẻ em, nếu trẻ em đi kèm nhiều hơn thì từ em thứ 02 trở lên phụ thu 90% giá tour. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 - dưới 11 tuổi phụ thu 90%. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (308, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour\xa0</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour\xa0</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour\xa0</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour\xa0</li><li class="text-secondary-v2 text-base">Riêng vé máy bay không hoàn, không hủy.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (309, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe Ô tô đời mới đưa đón suốt hành trình (16-29c tùy theo số lượng khách PYS sẽ sắp xếp xe cho phù hợp)</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">03 đêm lưu trú tại Khách sạn tiêu chuẩn 3 sao tiện nghi, trung bình 02 khách/phòng + 01 đêm ngủ tại nhà sàn cộng đồng.</li><li class="text-secondary-v2 text-base">Ăn uống theo chương trình (09 Bữa ăn chính 130.000 VNĐ/ khách/ bữa + 04 bữa sáng theo chương trình)</li><li class="text-secondary-v2 text-base">Vé thắng cảnh vào cửa một lần tại các điểm tham quan.</li><li class="text-secondary-v2 text-base">1 đồ uống/khách tại cafe Mây ơi</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 30.000.000 VNĐ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li><li class="text-secondary-v2 text-base">Thuế VAT</li></ul>');
