INSERT INTO tour_terms (tour_id, section_title, content) VALUES (311, 'Giá tour bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé máy bay Vietjet khứ hồi HCM – HÀ NỘI có 7kg hành lý xách tay </li><li class="text-secondary-v2 text-base">Xe Ô tô đời mới đưa đón suốt hành trình (16-29c tùy theo số lượng khách PYS sẽ sắp xếp xe cho phù hợp)</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình từ Hà Nội, nhiệt tình, vui vẻ.</li><li class="text-secondary-v2 text-base">04 đêm lưu trú tại Khách sạn tiêu chuẩn 3 sao tiện nghi, trung bình 02 khách/phòng + 01 đêm ngủ tại nhà sàn cộng đồng Mù Cang Chải.</li><li class="text-secondary-v2 text-base">01 đồ uống tại khu Check in Mây ơi</li><li class="text-secondary-v2 text-base">Ăn uống theo chương trình (09 Bữa ăn chính 130.000đ/ khách/ bữa + 04 bữa sáng theo chương trình)</li><li class="text-secondary-v2 text-base">Vé thắng cảnh vào cửa một lần tại các điểm tham quan.</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 30.000.000đ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (311, 'Giá tour không bao gồm', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé tham quan Fansipan và cầu kính Tình Yêu.</li><li class="text-secondary-v2 text-base">Vé vào KDL Cổng Trời</li><li class="text-secondary-v2 text-base">Chi phí xe ôm vào tận ruộng bậc thang </li><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác.</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li><li class="text-secondary-v2 text-base">Xe tiễn sân bay ngày 5.</li><li class="text-secondary-v2 text-base">Tiền tip lái xe, HDV</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (311, 'Chính sách trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 2 tuổi, phụ thu 10%, ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 2 tuổi – dưới 5 tuổi phụ thu 60% , hai người lớn chỉ được kèm 1 trẻ em, nếu trẻ em đi kèm nhiều hơn thì từ em thứ 02 trở lên phụ thu 90% giá tour. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em từ 5 - dưới 11 tuổi phụ thu 90%. Trẻ em ngủ chung giường với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ từ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (311, 'Quy định phụ thu', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 400.000 VNĐ/ khách/ 1 đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình), 400.000 VNĐ/khách/1 đêm ở Hà Nội.</li><li class="text-secondary-v2 text-base">Phụ thu chi phí giấy thông hành người nước ngoài 250.000VNĐ/khách</li><li class="text-secondary-v2 text-base">Phụ thu HDV tiếng Anh 10$/ngày</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (311, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Riêng vé máy bay không hoàn, không hủy theo quy định của hãng hàng không.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
