INSERT INTO tour_terms (tour_id, section_title, content) VALUES (267, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Vé tham quan Fansipan và cầu kính Bạch Long.</li><li class="text-secondary-v2 text-base">Vé vào KDL Cổng Trời</li><li class="text-secondary-v2 text-base">Đồ uống, điện thoại, chi phí giặt là hoặc chi phí cá nhân khác.</li><li class="text-secondary-v2 text-base">Chi phí mua và hái dâu tại vườn.</li><li class="text-secondary-v2 text-base">Ăn uống ngoài chương trình.</li><li class="text-secondary-v2 text-base">TIP cho HDV và lái xe</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (267, 'Quy định phụ thu:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Phụ thu phòng đơn 400.000đ/ khách/ 1 đêm (Áp dụng trong trường hợp khách muốn ở 01 mình 01 phòng trong suốt hành trình), 400.000đ/khách/1 đêm ở Hà Nội.</li><li class="text-secondary-v2 text-base">Phụ thu chi phí giấy thông hành người nước ngoài 250.000VNĐ/khách.</li><li class="text-secondary-v2 text-base">Phụ thu HDV tiếng Anh 10$/ngày.</li></ul>');
