INSERT INTO tour_terms (tour_id, section_title, content) VALUES (294, 'Phụ thu trẻ em', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em dưới 5 tuổi miễn phí (ăn uống và ngủ cùng với bố mẹ, bố mẹ tự túc lo cho bé). Hai người lớn chỉ kèm 1 trẻ em, trẻ em thứ 2 trở đi tính giá 50% giá tour người lớn.</li><li class="text-secondary-v2 text-base">Trẻem từ 5 dưới 11 tuổi giá tour là 75% giá tour người lớn. (Tiêu chuẩn: 01 suất ăn + 01 ghế ngồi và ngủ ghép cùng giường với bố mẹ). Hai người lớn chỉ kèm 1 trẻ em , trẻ em thứ 2 trở đi tính giá tour như người lớn.</li><li class="text-secondary-v2 text-base">Trẻtừ 11 tuổi trở lên, tính bằng chi phí người lớn.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (294, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour </li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour </li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour </li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour </li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (295, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe ô tô du lịch đời mới đưa đón suốt hành trình.</li><li class="text-secondary-v2 text-base">HDV kinh nghiệm, nhiệt tình suốt hành trình.</li><li class="text-secondary-v2 text-base">Phòng khách sạn 2 người/phòng, nhóm lẻ ngủ 3</li><li class="text-secondary-v2 text-base">Ăn uống theo chương trình (2 bữa trưa 150.000 VNĐ/bữa, 1 bữa tối tại nhà hàng Nón, bữa sáng tại resort)</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch suốt tuyến, mức cao nhất 30.000.000đ/ người/ vụ</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/người/ngày.</li><li class="text-secondary-v2 text-base">Vé thăm quan 01 lần theo hành trình.</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (295, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chi phí cá nhân</li><li class="text-secondary-v2 text-base">Tip cho lái xe và HDV</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (295, 'Quy định phụ thu:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Trẻ em từ 11 tuổi trở lên: tính 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Trẻ em từ 05 – 11 tuổi: tính 75% giá trị tour – Ngủ chung với bố mẹ.</li><li class="text-secondary-v2 text-base">Trẻ em dưới 05 tuổi: miễn phí (Ăn ngủ cùng bố mẹ, ngồi lòng bố hoặc mẹ), 2 người lớn miễn phí 1 trẻ em, trẻ thứ 2 tính 50% giá trị tour.</li><li class="text-secondary-v2 text-base">Lễ tết phụ thu 150.000 vnđ/ người</li><li class="text-secondary-v2 text-base">Phòng riêng: Tùy thuộc vào hạng bungalow hiện có của homestay.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (295, 'Chính sách hoàn hủy', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Hủy tour ngay sau khi đăng ký đến 10 ngày trước ngày khởi hành, phạt 30% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 5 – 10 ngày trước ngày khởi hành, phạt 50% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 3 – 5 ngày trước ngày khởi hành, phạt 75% trên giá tour.</li><li class="text-secondary-v2 text-base">Hủy tour trong vòng từ 0 – 3 ngày trước ngày khởi hành, phạt 100% giá trị tour.</li><li class="text-secondary-v2 text-base">Ngày lễ tết không hoàn, không hủy, không đổi, không áp dụng chính sách hủy trên.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (296, 'Giá tour bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Xe du lịch đời mới phục vụ suốt tuyến Hà Nội – Resort</li><li class="text-secondary-v2 text-base">Hướng dẫn viên nhiệt tình, kinh nghiệm</li><li class="text-secondary-v2 text-base">1 bữa ăn chính suất 200.000đ/người ( không bao gồm đồ uống)</li><li class="text-secondary-v2 text-base">Chiphí tổ chức teambuilding (thuê âm thanh,loa mic, địa điểm sân bãi, hoạt náo viên, dụng cụ trò chơi,...)</li><li class="text-secondary-v2 text-base">Vé thăm quan 01 khách/ lượt</li><li class="text-secondary-v2 text-base">Thuê nhà sàn nghỉ trưa sạch sẽ tiện nghi</li><li class="text-secondary-v2 text-base">Thuê xe đạp, bơi không giới hạn tại bể bơi ngoài trời và bể bơi có mái che.</li><li class="text-secondary-v2 text-base">Các hoạt động như chơi cà kheo, bóng rổ, bóng bàn, bóng chuyền, cầu lông. Lưu ý trưởng đoàn nhận đồ và đặt cọc 200.000 vnđ, và sau khi trả đủ đồ sẽ nhận lại số tiền này.</li><li class="text-secondary-v2 text-base">Nước uống 01 chai/ khách/ ngày</li><li class="text-secondary-v2 text-base">Bảo hiểm du lịch mức cao nhất 20.000.000đ/người/vụ</li><li class="text-secondary-v2 text-base">Thuế VAT.</li></ul>');
INSERT INTO tour_terms (tour_id, section_title, content) VALUES (296, 'Giá tour không bao gồm:', '<ul class="list-disc pl-4 space-y-2"><li class="text-secondary-v2 text-base">Chi phí cá nhân phát sinh ngoài chương trình.</li><li class="text-secondary-v2 text-base">Dịch vụ Hát Karaoke hình máy chiếu (2,800,000đ/ 03 giờ)</li><li class="text-secondary-v2 text-base">Tiền Tip HDV/ Lái xe nếu có.</li></ul>');
