USE booking_tour;

CREATE TABLE users (
  id INT PRIMARY KEY AUTO_INCREMENT,
  name VARCHAR(100),
  email VARCHAR(100) UNIQUE,
  password VARCHAR(255),
  phone VARCHAR(20),
  role ENUM('user', 'admin') DEFAULT 'user',
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE IF NOT EXISTS tours (
  id INT PRIMARY KEY AUTO_INCREMENT,
  title VARCHAR(255),
  slug VARCHAR(255),
  num_day INT,
  num_night INT,
  price INT,
  old_price INT,
  rating FLOAT,
  rating_count INT,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  location_id INT NOT NULL,          -- FK đến locations
  FOREIGN KEY (location_id) REFERENCES locations(id) ON DELETE CASCADE
);

--  Bảng Hình ảnh liên quan đến tour
CREATE TABLE IF NOT EXISTS tours_images (
   id INT PRIMARY KEY AUTO_INCREMENT,
   tour_id INT,
   image_url VARCHAR(255),
   created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
   FOREIGN KEY (tour_id) REFERENCES tours(id) ON DELETE CASCADE
);



-- Bảng tour_overviews
CREATE TABLE IF NOT EXISTS tour_overviews (
  id INT PRIMARY KEY AUTO_INCREMENT,
  tour_id INT,
  content TEXT,
  FOREIGN KEY (tour_id) REFERENCES tours(id) ON DELETE CASCADE
);

-- Bảng tour_schedules
CREATE TABLE IF NOT EXISTS tour_schedules (
  id INT PRIMARY KEY AUTO_INCREMENT,
  tour_id INT,
  day_text VARCHAR(100),       
  content TEXT,                             
  FOREIGN KEY (tour_id) REFERENCES tours(id) ON DELETE CASCADE
);


-- Bảng tour_departures
CREATE TABLE tour_departures (
  id INT PRIMARY KEY AUTO_INCREMENT,
  tour_id INT,
  departure_date DATE,
  return_date DATE,
  available_seats INT DEFAULT NULL,  
  price INT,
  FOREIGN KEY (tour_id) REFERENCES tours(id) ON DELETE CASCADE
);

-- Bảng thông tin cần lưu ý
CREATE TABLE tour_terms (
    id INT AUTO_INCREMENT PRIMARY KEY,
    tour_id INT NOT NULL,
    section_title VARCHAR(255),
    content TEXT,
    FOREIGN KEY (tour_id) REFERENCES tours(id) ON DELETE CASCADE
);


-- Bảng tour_prices
CREATE TABLE IF NOT EXISTS tour_prices (
  id INT PRIMARY KEY AUTO_INCREMENT,
  tour_id INT,
  target_type ENUM('adult', 'child', 'infant'),
  min_age INT,
  max_age INT,
  price INT,
  FOREIGN KEY (tour_id) REFERENCES tours(id) ON DELETE CASCADE
);

-- Bảng đánh giá tour
CREATE TABLE IF NOT EXISTS tour_reviews (
  id INT PRIMARY KEY AUTO_INCREMENT,
  tour_id INT,
  name VARCHAR(100),
  rating INT CHECK (rating BETWEEN 1 AND 5),
  comment TEXT,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  FOREIGN KEY (tour_id) REFERENCES tours(id) ON DELETE CASCADE
);

-- Bảng bookings
CREATE TABLE IF NOT EXISTS bookings (
  id INT PRIMARY KEY AUTO_INCREMENT,
  tour_id INT NOT NULL,
  gender ENUM('male', 'female', 'other'),
  full_name VARCHAR(100) NOT NULL,
  phone_number VARCHAR(20) NOT NULL,
  email VARCHAR(100) NOT NULL,
  address VARCHAR(255),
  note TEXT,
  departure_date DATE NOT NULL,
  total_price INT NOT NULL,
  status ENUM('pending', 'confirmed', 'cancelled') DEFAULT 'pending',
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  FOREIGN KEY (tour_id) REFERENCES tours(id) ON DELETE CASCADE
);

SET FOREIGN_KEY_CHECKS = 0;
TRUNCATE TABLE bookings;
SET FOREIGN_KEY_CHECKS = 1;


-- 1. Đảm bảo tour_id không được null
ALTER TABLE bookings
MODIFY tour_id INT NOT NULL;

-- 2. Thêm cột gender
ALTER TABLE bookings
ADD gender ENUM('male', 'female', 'other') DEFAULT 'other';


-- 3. Thêm ràng buộc NOT NULL cho full_name, phone_number, email, departure_date, total_price
ALTER TABLE bookings
MODIFY full_name VARCHAR(100) NOT NULL,
MODIFY phone_number VARCHAR(20) NOT NULL,
MODIFY email VARCHAR(100) NOT NULL,
MODIFY departure_date DATE NOT NULL,
MODIFY total_price INT NOT NULL;

-- 4. Thêm cột address
ALTER TABLE bookings
ADD address VARCHAR(255);

-- 5. Thêm cột note
ALTER TABLE bookings
ADD note TEXT;

-- Bảng booking_details
CREATE TABLE IF NOT EXISTS booking_details (
  id INT PRIMARY KEY AUTO_INCREMENT,
  booking_id INT,
  target_type ENUM('adult', 'child', 'infant'),
  quantity INT,
  FOREIGN KEY (booking_id) REFERENCES bookings(id) ON DELETE CASCADE
);

-- Bảng payments
CREATE TABLE IF NOT EXISTS payments (
  id INT PRIMARY KEY AUTO_INCREMENT,
  booking_id INT,
  amount INT,
  payment_method VARCHAR(50),
  status ENUM('unpaid', 'paid', 'failed') DEFAULT 'unpaid',
  paid_at DATETIME,
  order_id VARCHAR(50),
  FOREIGN KEY (booking_id) REFERENCES bookings(id) ON DELETE CASCADE
);




-- Navbar
-- Bảng Miền/Châu lục
CREATE TABLE regions (
  id INT PRIMARY KEY AUTO_INCREMENT,
  name VARCHAR(100) NOT NULL -- VD: 'Miền Bắc'
);


-- Bảng Vùng con trong miền/nhóm quốc Gia
CREATE TABLE subregions (
  id INT PRIMARY KEY AUTO_INCREMENT,
  name VARCHAR(100) NOT NULL,         -- VD: 'Tây Bắc'
  region_id INT NOT NULL,             -- FK đến regions
  FOREIGN KEY (region_id) REFERENCES regions(id) ON DELETE CASCADE
);

-- Bảng tỉnh thành/quốc gia
CREATE TABLE locations (
  id INT PRIMARY KEY AUTO_INCREMENT,
  name VARCHAR(100) NOT NULL,        -- VD: 'Sapa', 'Cát Bà'
  subregion_id INT NOT NULL,         -- FK đến subregions
  FOREIGN KEY (subregion_id) REFERENCES subregions(id) ON DELETE CASCADE
);


